INSERT INTO `pause_pau` (`pau_id`, `pau_phrase`) VALUES
(1, '<br/><br/><br/><br/><br/><br/><br/>     Regarder un atome le change, regarder un homme le transforme, <br/><br/>                                                                         regarder l\'\'avenir le bouleverse. <br/><br/><br/><br/>     Gaston Berger'),
(2, '<br/><br/><br/><br/><br/><br/><br/>      Notre fin c\'\'est d\'\'arriver à une véritable organisation où chaque homme serait <br/><br/>un homme et  où les collectivités seraient également  humaines.<br/>            <br/>                                                                                                                          Jean Paul Sartre'),
(3, '<br/><br/><br/><br/>    Savoir où l\'\'on va , c\'\'est très bien; mais il faut encore montrer qu\'\'on y va.<br/><br/><br/><br/>                                                                                                                    Emile Zola <br/><br/>'),
(4, '<br/><br/><br/><br/><br/><br/><br/><br/>                                                                        Nous sommes nés pour agir.<br/><br/><br/>            Montaigne'),
(5, '<br/><br/><br/><br/><br/><br/><br/>                                    Chaque homme porte en soi la forme entière de l\'\'humanité<br/><br/><br/>                   Montaigne'),
(6, '<br/><br/><br/><br/>        Qui veut gouverner les hommes, il ne faut pas les chasser devant soi; <br/><br/>il faut les faire suivre.<br/><br/><br/><br/>                                                                                                                     Montesquieu<br/><br/>'),
(7, '<br/><br/><br/><br/><br/><br/><br/>                                                            L\'\'important ce n\'\'est pas la vie, c\'\'est la langue.<br/><br/><br/>           Brodsky Joseph'),
(8, '<br/><br/>Le meilleur chef est celui dont on sait à peine qu\'\'il existe.<br/><br/>Il est moins bon si la foule lui obéit et l\'\'acclame. <br/><br/>Le pire est celui qui méprise.<br/><br/>Négligez d\'\'honorer le peuple et le peuple négligera de vous honorer. <br/><br/>Mais du bon chef, qui parle peu, une fois la tâche achevée et le dessein accompli, tous<br/>diront: <br/><br/>\\\"Nous avons fait cela nous mêmes\\\".<br/><br/>                                                                                                                          Lao Tseu<br/>                                                                                                                          Livre de la Vertu du Tao'),
(9, '<br/><br/><br/><br/><br/>L\'\'homme le plus heureux est celui qui fait le bonheur d\'\'un plus grand nombre d\'\'autres.<br/><br/><br/><br/><br/><br/><br/><br/>                                                                                                                                                          Diderot'),
(10, '<br/><br/><br/><br/><br/>              Quiconque est convaincu contre son gré; son opinion n\'\'a pas changé.<br/><br/>               <br/><br/><br/>                                                                                           Marshall Salhins<br/>                                                                                         Age de pierre, âge d\'\'abondance'),
(11, '<br/><br/><br/><br/><br/><br/><br/>                               Faire un meilleur avenir avec les éléments élargis du passé.<br/><br/><br/><br/>                                                                                                                                                            Goethe <br/>'),
(12, '<br/><br/><br/><br/><br/><br/>                                        Un marteau manque parfois son but -- un bouquet  jamais.<br/><br/><br/>            A hammer sometimes misses its mark -- a bouquet never.'),
(13, '<br/><br/><br/><br/><br/>Pour vivre en paix avec tout le monde, il ne suffit pas de ne point se mêler des affaires <br/><br/>d\'\'autrui,  il faut encore souffrir qu\'\'autrui se mêle des vôtres.<br/><br/><br/><br/><br/><br/>Fréron '),
(14, '<br/><br/><br/><br/>         Vous croyez que c\'\'est ce que vous gardez qui vous fait riche. <br/><br/>         On vous l\'\'a dit. <br/><br/>         Moi,  je vous dis que c\'\'est ce que vous donnez qui vous fait riche.<br/><br/><br/><br/>                                                                                                                             Jean Giono <br/>                                                                                                            Que ma joie demeure'),
(15, '<br/><br/><br/><br/><br/><br/>                                         A tout âge s\'\'ouvre la porte étincelante du courage.<br/><br/><br/><br/>    Paul Eluard'),
(16, '<br/><br/><br/><br/>        Moins on parle, et bien souvent mieux on pense.<br/><br/><br/><br/><br/><br/>                                                                                                                Sainte Beuve'),
(17, '<br/><br/><br/><br/>      Le drame de la direction technique d\'\'une entreprise, c\'\'est que son objet a <br/><br/>des exigences propres qui sont souvent en conflit avec les exigences humaines <br/><br/>des individus qu\'\'elle met en oeuvre.<br/><br/>                                                        <br/>                                                                                                                  Emmanuel Mounier'),
(18, '<br/><br/><br/><br/><br/><br/>                      L\'\'expérience est la lanterne qui n\'\'éclaire que le chemin déjà parcouru.<br/>            <br/> <br/><br/>                     Proverbe chinois'),
(19, '<br/><br/><br/><br/>        Un bienfait perd souvent sa grâce à le trop publier.<br/><br/><br/><br/><br/><br/>                                                                                                            Pierre Corneille<br/>                                                                                                            Théodore<br/>                            '),
(20, '<br/><br/><br/><br/><br/><br/>                                                     Il n\'\'y a de richesse que d\'\'hommes.<br/>                   <br/><br/><br/><br/><br/>    Jean Bodin'),
(21, '<br/><br/><br/>                     L\'\'infaillible façon de tuer un homme, <br/><br/>                     c\'\'est de le payer pour être chômeur, <br/><br/>                     et puis c\'\'est gai dans ma ville, <br/><br/>                     ça fait des morts qui marchent.<br/><br/><br/>                                                                                                                               Félix Leclerc'),
