(30, '<br/><br/><br/><br/><br/><br/>                                                       Il faut savoir obéir pour savoir commander.<br/><br/><br/><br/><br/><br/>   ?'),
(31, '<br/><br/>     Il ne faudrait pas aimer les hommes pour leur être d\'\'un réel secours. <br/><br/>    Seulement désirer rendre meilleure telle expression de leur regard lorsqu\'\'il se pose <br/>    sur plus appauvri qu\'\'eux, prolonger d\'\'une seconde telle minute agréable de leur vie.  <br/><br/>    A partir de cette démarche et chaque racine traitée, leur respiration se ferait plus sereine. <br/><br/>     Surtout ne pas entièrement leur supprimer ces sentiers pénibles, à l\'\'effort desquels <br/>     succède  l\'\'évidence de la vérité à travers fleurs et fruits.<br/><br/><br/><br/>                                                                                                                                        René Char<br/>                                                                                                                            Feuillets d\'\'hypnos<br/>'),
(32, '<br/><br/><br/><br/><br/><br/><br/>                 Tiens vis à vis des autres ce que tu t\'\'es promis à toi seul. Là est ton contrat.<br/><br/><br/>                  René Char<br/>                  Feuillet d\'\'Hypnos'),
