(75, '<br/><br/><br/><br/>        L\'\'excès d\'\'autorité produit la haine. <br/><br/>        L\'\'excès de l\'\'indulgence affaiblit l\'\'autorité. <br/><br/>        Sachez garder le milieu et vous ne serez exposé ni au mépris ni aux outrages. <br/><br/>        Il faut imiter le chirurgien qui, suivant le besoin, applique le fer ou le baume sur la<br/>        blessure.<br/><br/><br/>                                                                                                                 Saadi<br/>                                                                                                                 Le jardin des roses'),
(76, '<br/><br/><br/><br/>        La France est divisée en quarante millions de Francais... <br/><br/>        La France est le seul pays au monde où, si vous ajoutez dix citoyens à dix autres,<br/><br/>        vous ne faites pas une addition mais vingt divisions.<br/>    <br/>                                                                                                 Pierre Daninos<br/>                                                                                                 Les carnets du major Thomson'),
(77, '<br/><br/><br/><br/><br/>          Gèle, gèle, ciel rigoureux,<br/><br/>                                                     Ta morsure est moins cruelle<br/><br/>                                                                                                                Que celle d\'\'un bienfait oublié.<br/><br/>                            <br/>          Shakespeare<br/>          Comme il vous plaira'),
(78, '<br/><br/><br/><br/><br/>                                                                   Je n\'\'ai rien à déclarer sinon mon génie.<br/><br/><br/>                           <br/>               Oscar Wilde<br/>                à la douane des Etats Unis, 1882<br/>'),
(79, '<br/><br/><br/><br/><br/>            Le mal de la grandeur, c\'\'est quand du pouvoir elle sépare la conscience.<br/><br/><br/><br/>                                                                                                                                Shakespeare<br/>                                                                                                                               Jules César '),
(80, '<br/><br/><br/><br/><br/><br/><br/>                       L\'\'homme n\'\'est rien d\'\'autre autre que la série de ses actes.<br/><br/><br/>                                                                                                                                Hegel<br/>                                                                                                                                Encyclopédie   '),
(81, '<br/><br/><br/><br/>           L\'\'homme! C\'\'est magnifique! Cela sonne ... fier, fier! <br/>           L\'\'homme! Il faut respecter l\'\'homme! <br/>           Ne pas en avoir pitié... <br/>           Ne pas l\'\'abaisser par la pitié... <br/>           Il faut le respecter.<br/><br/><br/><br/><br/>                                                                                                                              Maxime Gorki<br/>                                                                                                                               Les bas fonds'),
