(104, '<br/><br/><br/><br/><br/><br/>                                ... pour gagner du bien, le savoir faire vaut mieux que le savoir.<br/><br/><br/><br/><br/><br/><br/><br/>                                 Beaumarchais                           <br/><br/>                                 Le mariage de Figaro'),
(105, '<br/><br/><br/><br/><br/>                                         Aimer ce qui résiste et croyez qui vous blâme.<br/><br/><br/><br/><br/><br/>                                                                                                                                      C. Delavigne<br/>                                                                                                                                      Louis XI'),
(106, '<br/><br/><br/><br/><br/><br/>            Mieux vaut un mauvais caractère, que pas de caractère du tout.<br/><br/><br/><br/><br/>             Georges Bernanos<br/>             Le chemin de la Croix des Armes'),
(107, '<br/><br/><br/><br/><br/><br/><br/>    La fortune pour arriver à moi passera par les conditions <br/><br/>                                                                                  que lui impose mon caractère.<br/><br/><br/>                                                                                                                           Chamfort<br/><br/>                                                                                                                           Maximes et pensées'),
(108, '<br/><br/><br/><br/><br/>      Quand une chose bonne a un inconvénient, il est ordinairement plus prudent d\'\'ôter <br/><br/>l\'\'inconvénient que la chose.<br/><br/><br/><br/><br/>                                                                                                                        Montesquieu <br/>                                                                                                                        Mes pensées'),
(109, '<br/><br/><br/><br/><br/><br/><br/>                   L\'\'expérience instruit plus sûrement que le conseil.<br/><br/>    <br/>                                                                                                                   André Gide <br/>                                                                                                                   Les faux monnayeurs'),
(110, '<br/><br/><br/><br/><br/><br/>                             On donne des conseils mais on n\'\'inspire pas la conduite.<br/><br/><br/>                                                                                                                                    La Rochefoucault<br/>                                                                                                                                    Maximes'),
(111, '  <br/><br/><br/><br/>  <br/>          N\'\'a de conviction que celui qui n\'\'a rien approfondi.<br/><br/><br/><br/><br/><br/>        <br/>                                                                                                                  E. M. Cioran<br/>                                                                                                                  De l\'\'inconvénient d\'\'être né'),
(112, '<br/><br/><br/><br/><br/><br/><br/>    - Dis, dessine moi un mouton.<br/><br/>                                                                                                               Antoine de Saint Exupéry<br/>                                                                                                                                     Le petit prince'),
(113, '<br/><br/><br/><br/><br/>      Le vrai savant n\'\'est pas celui celui qui donne les bonnes réponses, <br/><br/>                                                       c\'\'est celui qui pose les bonnes questions.<br/><br/><br/><br/><br/>                                                                                                 Claude Levi Stauss<br/><br/>'),
(114, '<br/><br/><br/><br/><br/>                Ne désespérer jamais. <br/><br/>                                                                          Faites infuser davantage.<br/><br/><br/><br/><br/><br/>                                                                                                                                 Henri Michaux<br/>                                                                                                                                 Tranches de savoir'),
(115, '<br/><br/><br/><br/><br/>            L\'\'homme est une création du désir, non pas une création du besoin.<br/><br/><br/>                                                                                                                   Gaston Bachelard<br/>                                                                                                                   La psychanalyse du feu <br/>'),
(116, '<br/><br/><br/><br/><br/><br/><br/>             Notre désir retirerait à la mer sa robe chaude avant de nager sur son coeur.<br/><br/><br/><br/><br/>                                                                                                                              René Char<br/>                                                                                                                              Fureur et Mystère'),
(117, '<br/><br/><br/><br/><br/>           S\'\'il n\'\'y avait parfois l\'\'étanchéité de l\'\'ennui,<br/><br/><br/>                                                                                                         le coeur s\'\'arrêterait de battre.<br/><br/><br/>           René Char<br/>           Fureur et mystère'),
(118, '<br/><br/>       L\'\'expérience de chacun est le trésor de tous.<br/><br/>                 <br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>                                                                                                                     Gérard de Nerval<br/>                                                                                                                      Promenades et souvenirs'),
(119, '<br/><br/><br/>               Entre deux individus, <br/><br/>                                       l\'\'harmonie n\'\'est jamais donnée, <br/><br/>                                                                                       elle doit indéfiniment se reconquérir.<br/><br/><br/><br/><br/>               Simone de Beauvoir<br/>               La force de l\'\'âge'),
(120, '<br/><br/>                         <br/>                                                                               Les affronts à l\'\'honneur ne se réparent point.<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>         Pierre Corneille<br/>         Le Cid'),
(121, '<br/><br/><br/>           Depuis que la science nous appris la modestie,<br/><br/>           nous savons que les bonnes idées, <br/><br/>           les seules qui méritent notre crédit, <br/><br/>           sont celles qui ne prétendent qu\'\'à une vérité provisoire.<br/><br/><br/><br/><br/><br/>                                                                                                                 Emmanuelle Arsan<br/>                                                                                                                 Emmanuelle IV'),
(122, '<br/><br/><br/>         Tout ce qui augmente la liberté augmente la responsabilité.<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>                                                                                                                         Victor Hugo<br/>                                                                                                                          Actes et Paroles'),
(123, '<br/><br/><br/><br/><br/>           Les plus détestables mensonges sont ceux <br/>  <br/>                                                    qui se rapprochent le plus de la vérité.<br/><br/><br/><br/><br/><br/><br/>                                                                                                          André Gide<br/>                                                                                                          Si le grain ne meurt'),
(124, '<br/><br/><br/><br/><br/>     Personne presque ne s\'\'avise de lui-même du mérite d\'\'un autre.<br/><br/><br/><br/>                                                                                                                               La Bruyère <br/>                                                                                                                               Les Caractères<br/><br/>                            '),
(125, '<br/><br/><br/><br/><br/><br/><br/>         Les mots sont comme des allumettes: il en a qui prennent, d\'\'autres pas.<br/><br/>    <br/><br/><br/><br/><br/>         Pierre Daninos<br/>          Nouveaux carnets du Major Thomson <br/>'),
(126, '<br><br><br><br>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;L\'autorité contraint à l\'obéissance, mais la raison la persuade.<br><br><br><br><br><br><br><br><br><br>                                                                                                                      Cardinal de Richelieu<br>                                                                                                                      Testament politique'),
(127, '<br/><br/><br/><br/><br/>        L\'\'obeissance à un homme dont l\'\'autorité n\'\'est pas illuminée de légitimité, <br/><br/>c\'\'est un cauchemar.<br/><br/>  <br/><br/><br/><br/><br/>         Simone Weil<br/>         La pesanteur et la grâce'),
(128, '<br/><br/><br/><br/><br/>     Certains riches font juste assez de bien pour se faire haïr de leurs obligés.<br/><br/><br/><br/><br/><br/><br/><br/>                                                                                                                            Jean Rostand<br/>                                                                                                                             De la vanité'),
(129, '<br/><br/><br/><br/><br/><br/>   Le pouvoir qu\'\'on appelle légitime est celui qui réussit à éviter le mépris et la haine.<br/><br/><br/><br/><br/>                                                                                                                         Merleau Ponty<br/>                                                                                                                         Eloge de la philosophie<br/>                            '),
(130, '<br/><br/><br/><br/><br/><br/>   C\'\'est une expérience éternelle que tout homme qui a du pouvoir est porté à en abuser.<br/><br/><br/><br/><br/><br/><br/>             Montesquieu<br/>             De l\'\'esprit des lois'),
(131, '<br/><br/><br/><br/><br/>     Il n\'\'existe d\'\'autre voie vers la solidarité humaine que la recherche et respect de la <br/><br/>     dignité individuelle.<br/><br/><br/><br/>  <br/>                                                                                                              P. Lecomte du Noüy<br/>                                                                                                              L\'\'homme et sa destinée<br/>                            '),
(132, '<br/><br/><br/>     Tous les moyens de l\'\'esprit sont enfermés dans le langage; <br/><br/>      et qui n\'\'a pas réfléchi sur le langage n\'\'a pas réfléchi du tout.<br/><br/><br/><br/><br/><br/><br/><br/>                                                                                                                  Alain <br/>                                                                                                                  Propos sur l\'\'éducation'),
(133, '<br/><br/><br/><br/><br/>      Il faut propager au plus vite cette notion que l\'\'homme n\'\'est pas une force de travail <br/><br/>mais une structure qui traite l\'\'information et qui trouve être également une source <br/><br/>d\'\'information.<br/><br/><br/>                                                                                                                        Henri Laborit<br/>                                                                                                                        La nouvelle grille'),
(134, '<br/><br/><br/><br/><br/>    Ne pas communiquer pour communiquer mais communiquer pour agir ensemble.<br/>    <br/><br/><br/><br/><br/><br/><br/><br/>                                                                                                                               Pierre Goguelin'),
(135, '<br/><br/><br/><br/><br/><br/>                Ne pas tout faire, ne pas laisser faire, faire faire.<br/><br/><br/><br/><br/><br/><br/>           <br/>                                                                                                                                                   Foch'),
(136, '<br/><br/><br/><br/><br/><br/><br/>          To get the right things done.<br/><br/>                                                      (Faire que les choses qui doivent être faites le soient).<br/><br/><br/><br/>           Peter Drucker'),
(137, '<br/><br/><br/><br/>            Si tu veux être aimé, aime<br/><br/><br/><br/><br/><br/><br/><br/><br/>           <br/>                                                                                                                                         Sénèque'),
(138, '<br/><br/><br/><br/><br/><br/>                             L\'\'on est plus souvent dupe par la défiance que par la confiance.<br/><br/>    <br/>                             Cardinal de Retz'),
(139, '<br/><br/><br/><br/><br/><br/>             Donner du sens au travail.<br/><br/>             Faire que l\'\'efficacité puisse encore donner du sens.<br/><br/><br/><br/><br/>               ??? '),
(140, '<br/><br/><br/><br/><br/><br/>                 Penser globalement, agir localement.<br/><br/><br/><br/><br/>                                                                                              René Dubos'),
(141, '<br/><br/><br/><br/>                                                   Penser globalement, agir localement.<br/><br/><br/><br/><br/>                                                                           René Dubos'),
(142, '<br/><br/><br/><br/><br/>       Quand le masque de l\'\'homme s\'\'applique au visage de la terre, <br/><br/>elle a les yeux crevés.<br/><br/><br/><br/><br/>                                                                                                                             René Char'),
(143, '<br/><br/><br/><br/><br/><br/>                                                                               Ne t\'\'attarde pas à l\'\'ornière des résultats.<br/><br/><br/><br/><br/><br/><br/>            René Char<br/>           Fureur et mystère');
INSERT INTO `pause_pau` (`pau_id`, `pau_phrase`) VALUES
(144, '<br/><br/><br/><br/><br/>                      L\'\'acquiescement éclaire le visage. Le refus lui donne la beauté.<br/><br/><br/>                                                                                                                              René Char<br/>                                                                                                                              Feuillets d\'\'Hypnos'),
(145, '<br/><br/><br/><br/>                                  <br/>          <br/>                                                          Les yeux seuls sont capables de pousser un cri.<br/><br/><br/><br/><br/><br/><br/>         René Char<br/>         Feuillets d\'\'Hypnos'),
(146, '<br/><br/><br/><br/>                 Accumule, puis distribue. <br/><br/>                 Sois la partie du miroir de l\'\'univers la plus dense, <br/>                                                                                      la plus utile et la moins apparente.<br/><br/><br/><br/><br/>                                                                                                                     René Char<br/>                                                                                                                      Feuillets d\'\'Hypnos'),
(147, '<br/><br/><br/><br/><br/><br/>           Faire que les choses qui doivent être faites, le soient.<br/><br/><br/><br/>                                                                                          Peter Drucker'),
(148, '<br/><br/><br/><br/>               On se bat bien que pour les causes qu\'\'on modèle soi-même <br/><br/>et avec lesquelles on se brûle en s\'\'identifiant.<br/> <br/><br/><br/><br/>                                                                                                                        René Char<br/>                                                                                                                        Fureur et mystère<br/>                                                                                                                        Feuillets d\'\'Hypnos'),
(149, '<br/><br/><br/><br/><br/><br/><br/>                   Entre deux individus, l\'\'harmonie n\'\'est jamais donnée, <br/>elle doit indéfiniment se reconquérir.  <br/><br/><br/><br/>                 Simone de Beauvoir'),
(150, '<br/><br/><br/><br/><br/><br/>                 Quand une chose bonne a un inconvénient, <br/>Il est plus prudent d\'\'ôtér l\'\'inconvénient que la chose.<br/><br/>      Montesquieu<br/>       Mes pensées'),
(151, '<br/><br/><br/><br/><br/><br/>              La science part de l\'\'émerveillement et de l\'\'évidence.<br/><br/><br/><br/>  Albert Einstein<br/><br/>'),
(152, '<br/><br/><br/><br/><br/><br/><br/>     Le hasard n\'\'aide que ceux qui y sont préparés.<br/><br/>                                                                                                       Louis Pasteur'),
(153, '<br/><br/><br/><br/><br/>          Le génie se reconnaît à cette faculté d\'\'associer, de combiner ou <br/><br/>d\'\'identifier des concepts apparemment lointains.<br/><br/><br/><br/><br/>                                                                                         Albert Einstein<br/><br/><br/><br/>                        <br/><br/><br/>'),
(154, '<br/><br/><br/><br/><br/><br/>     C\'\'est le but de toute activité de l\'\'intelligence de transformer<br/><br/> un miracle en quelque chose qu\'\'elle a saisi.<br/><br/><br/>                                                                                        Albert Einstein'),
(155, '<br/><br/><br/><br/><br/><br/><br/>     Le créateur est toujours au travail.<br/><br/>                                                                                           Robert Andrew  Millikan'),
(156, '<br/><br/><br/><br/><br/><br/><br/>                                        Ce qu\'\'on acceptait hier est remis à la meule aujourd\'\'hui.<br/><br/><br/>Victor Hugo<br/>'),
(157, '<br/><br/><br/><br/><br/><br/>      Toute science crée une nouvelle ignorance.<br/><br/><br/>                                                                                            Henri Michaux'),
(158, '<br/><br/><br/><br/><br/><br/>      Au commencement (ou presque) était la curiosité.<br/><br/><br/><br/><br/>                                                                                                           Isaac Asimov<br/><br/><br/><br/>'),
(159, '<br/><br/><br/><br/><br/><br/>    Notre planète est le berceau de la raison. <br/><br/>    Il n\'\'est pas possible de vivre éternellement dans un berceau.<br/><br/><br/>    Constantin Edouardovitch Tsiolkovski,<br/>    Un des pères de l\'\'astronautique soviétique         '),
(160, '<br/><br/><br/><br/><br/><br/>     Le cerveau humain a évidemment un penchant vers l\'\'infini et <br/><br/>caresse volontiers ce fantôme d\'\'idée.<br/><br/><br/>                                                                          Edgard Allan Poe<br/>'),
(161, '<br/><br/><br/><br/><br/><br/>       C\'\'est lorsque les concepts font défaut  qu\'\'un mot prend toute sa signification.<br/><br/><br/><br/>      Johann Wolfgang von Goethe'),
(162, '<br/><br/><br/><br/><br/><br/>     L\'\'observateur écoute la nature, <br/><br/>     l\'\'expérimentateur l\'\'interroge et la force à se dévoiler.<br/><br/><br/><br/>                                                                                            Cuvier'),
(163, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>                       <br/> Pour être seul, face à soi.<br/><br/>'),
(164, '<br/><br/><br/><br/><br/><br/>                Le mérite de l\'\'investigation consiste à poursuivre dans l\'\'expérience <br/><br/>ce qu\'\'il cherche mais à voir en même temps ce qu\'\'il ne cherchait pas.<br/><br/><br/>                                                                                                 Claude Bernard'),
(165, '<br/><br/><br/><br/><br/><br/>                      Les fleurs répandent leur parfum sur les mains qui les offrent.<br/><br/><br/><br/>                     Proverbe chinois'),
(166, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>                                Mieux  vaut glisser du pied que de la langue.<br/><br/><br/><br/>   Proverbe ghanéen'),
(167, '<br/><br/><br/><br/><br/><br/><br/>             <br/><br/><br/>                                        L\'\'ignorance ne tue pas, mais elle fait transpirer.<br/><br/>Proverbe haitien'),
(168, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>                                      <br/><br/><br/>                              Pour savoir où mène la route, demande à celui qui en vient.<br/><br/><br/>                                                                                                                  Proverbe indien<br/><br/><br/><br/><br/><br/><br/>'),
(169, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>                      L\'\'espoir est le salaire du pauvre.<br/><br/><br/>Proverbe danois<br/><br/><br/><br/><br/>'),
(170, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>                          La grenouille dans le puits se moque de l\'\'océan.<br/><br/><br/>Proverbe japonais'),
(171, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>    L\'\'éducation est une parure dans la prospérité et un refuge dans l\'\'adversité.<br/><br/>Proverbe grec'),
(172, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/> Sors de ton village mais ne le laisse pas sortir de toi.<br/><br/><br/>                                                                                                Proverbe afghan<br/><br/>                                                                                        '),
(173, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>    Qui ne commence jamais ne termine jamais.<br/><br/><br/>                                                                                          Proverbe hongrois'),
(174, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>    La vie est courte, mais un sourire ne prend qu\'\'une seconde. <br/><br/>                                                                                       Proverbe cubain'),
(175, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>      Qui n\'\'a pas de clôture autour de son champ n\'\'a pas d\'\'ennemi.<br/><br/><br/>     Proverbe burundais'),
(176, ' <br/><br/><br/><br/><br/><br/>      <br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>   Pause'),
(177, '<br/><br/><br/><br/><br/><br/><br/>     <br/><br/><br/><br/><br/><br/>   Sois heureux de ton vivant car tu seras mort longtemps.<br/><br/>                                                                         Proverbe écossais'),
(178, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>       La charité regarde le besoin, non la cause.<br/><br/><br/>                                                                                              Proverbe allemand'),
(179, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>     Les pieds mènent là où un coeur les attend.<br/><br/><br/>                                                                                       Proverbe éthopien'),
(180, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>    <br/>       Le désir embellit la laideur.<br/><br/><br/>                                                                     Proverbe espagnol'),
(181, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>     L\'\'expérience est le peigne que la nature nous offre quand on devient chauve.<br/><br/><br/> Proverbe belge'),
(182, '<br/><br/><br/><br/><br/><br/><br/>                                                                Travailler à bien penser<br/><br/><br/><br/>          Pascal'),
(183, ' <br/><br/><br/><br/><br/><br/><br/><br/><br/>            La théorie, c\'\'est lorsqu\'\'on sait tout ce que rien ne fonctionne.<br/><br/><br/>            La pratique, c\'\'est lorsque tout fonctionne et que personne ne sait personne.<br/><br/><br/><br/>                                                                                                                                              Einstein  '),
(184, '<br/><br/><br/><br/><br/><br/><br/>                               La science sans la sagesse est comme mise à la loterie. <br/>                               On risque de perdre tout ce qu\'\'on a investi.<br/><br/><br/><br/>    Einstein'),
(185, '<br/><br/><br/><br/><br/><br/>            La vie c\'\'est comme la bicyclette: il faut sans cesse avancer, <br/><br/>    sans arrêter pour ne pas perdre l\'\'équilibre.<br/><br/><br/>             Einstein  '),
(186, '<br/><br/><br/><br/><br/><br/>           L\'\'imagination est plus importante que le savoir. <br/><br/>   On peut tout savoir et ne rien faire tandis que l\'\'imagination on peut tout faire.<br/><br/><br/>                                                                                                                                  Einstein'),
(187, '<br/><br/><br/><br/><br/><br/><br/>                                           Il est plus difficile de détruire un préjudice qu\'\'un atome.<br/><br/><br/><br/>                                                                                       Einstein'),
(188, '<br/><br/><br/>  <br/><br/><br/>              La carte n\'\'est pas le territoire.<br/><br/><br/>                                                   Notre vision du monde n\'\'est pas le monde '),
(189, '<br/><br/><br/><br/><br/>     Le plus important pour un homme de science n\'\'est pas <br/><br/>                         ses diplômes <br/>                                              ni le nombre de ses années d\'\'études, <br/>                                                                   ni même son expérience <br/><br/>       mais son intuition.<br/><br/> <br/>                                                                                                   Eintein'),
(190, '<br/><br/><br/><br/><br/>                            Le monde ne sera pas détruit par ceux qui font le mal, <br/><br/>    mais par ceux qui les regardent agir et qui refusent d\'\'intervenir.<br/><br/><br/>                           Einstein'),
(191, '<br/><br/><br/><br/><br/><br/><br/><br/>                Les plans ne sont rien; c\'\'est la planification qui compte.<br/><br/><br/>                                                                                                                 D.D. Eisenhower<br/><br/><br/><br/>'),
(192, '<br/><br/><br/><br/><br/><br/><br/>              On ne voit bien qu\'\'avec le coeur, l\'\'essentiel est invisible.<br/><br/><br/>                                                                         Antoine de Saint Exupéry<br/>                                                                          Le Petit Prince<br/><br/>'),
(193, '<br/><br/><br/><br/><br/><br/><br/><br/>      Rappelles-toi que ce n\'\'est pas dans les montagnes que l\'\'eau trébuche, <br/>                            mais sur de petits cailloux.<br/><br/><br/><br/>     Proberbe balkan'),
(194, '<br/><br/><br/><br/><br/><br/>  <br/><br/><br/>                         Le succès est une conséquence non un but.<br/><br/>                                                                                                             Gustave Flaubert'),
(195, '<br/><br/><br/><br/><br/><br/>         La raison peut nous avertir de ce qu\'\'il faut éviter; <br/><br/>                                     l\'\'intuition seule nous dit ce qu\'\'il faut faire.<br/><br/><br/><br/>             J. Joubert'),
(196, '<br/><br/><br/><br/><br/><br/><br/>    <br/><br/><br/><br/>                           Ne pas prévoir, c\'\'est déjà gémir.<br/><br/><br/>                                                                                               Leonarrd de Vinci'),
(197, '<br/><br/><br/><br/><br/><br/><br/>  <br/><br/>                                     Les faits sont têtus.<br/><br/>                                                                                      Lénine'),
(198, '<br/><br/><br/><br/><br/>         Quand tout a été dit, quand tout a été fait, la plus grande qualité <br/>         nécessaire chez le commandant est \\\"l\'\'esprit de décision\\\".<br/><br/><br/><br/>                                                                                                  B. L. Montgomery<br/><br/><br/><br/>'),
(199, '<br/><br/><br/><br/><br/><br/><br/><br/><br/>   Assurons nous bien du fait avant de nous inquiéter de la cause.<br/>  La méthode est bien lente pour  la plupart des gens, qui courent à la cause et <br/>   passe par dessus la vérité du fait.<br/><br/>                                                                                     Napoléon<br/><br/>                                                                        '),
(200, '<br/><br/><br/><br/><br/><br/>           Nous courons sans souci dans le précipice, après que nous ayons mis <br/>                   quelque chose pour empêcher de le voir. <br/><br/><br/><br/><br/>                                                                                                        Blaise Pascal'),
(201, '<br/><br/><br/><br/><br/><br/><br/><br/><br/>               C\'\'est une maladie naturelle à l\'\'homme de croire qu\'\'il possède la vérité.<br/><br/><br/><br/>                                                                                Blaise Pascal'),
(202, '<br/><br/><br/><br/><br/><br/>       Ô mon ami, n\'\'aspire point à la vie du possible mais épuise le champ du possible.<br/><br/><br/><br/>         Pindare<br/><br/><br/><br/>'),
(203, '<br/><br/><br/><br/><br/><br/><br/>       Un bon général doit non seulement connaître le moyen de vaincre mais aussi<br/>       savoir quand la victoire est impossible.<br/><br/><br/>       Polybe'),
(204, '<br/><br/><br/><br/><br/><br/><br/>     Rêve de grandes choses, cela te permettra au moins d\'\'en faire de toutes petites.<br/><br/><br/>   J. Renard'),
(205, '<br/><br/><br/><br/><br/><br/>       Les seules limites de nos réalisations de demain, <br/><br/>                            ce sont nos doutes et nos hésitations d\'\'aujourd\'\'hui.<br/><br/><br/>       F. D. Roosevelt'),
(206, '<br/><br/><br/><br/><br/><br/>     <br/><br/><br/><br/><br/>     <br/><br/><br/>     Avant de rêver il faut savoir.<br/><br/>                                                                                                      J. Rostand'),
(207, '<br/><br/><br/><br/><br/><br/><br/><br/>   L\'\'expérience ne permet jamais d\'\'atteindre la certitude absolue.<br/>   Nous ne devons pas chercher à obtenir autre chose qu\'\'une probabilité.<br/><br/><br/>   B. Russel'),
(208, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>     Ne pas rire, ne pas se lamenter, ni haïr mais comprendre.<br/><br/><br/>                                                                                                                     B. Spinoza'),
(209, '<br/><br/><br/><br/><br/><br/><br/><br/><br/>    Face au monde qui change, <br/>                     il vaut mieux penser le changement <br/>                                                que changer  le pansement.<br/><br/><br/>                                                                                                       F. Blanche'),
(210, ' <br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>        Il n\'\'y a aucun mal à changer d\'\'avis. Pourvu que ce soit dans le bon sens.<br/><br/>        Sir W. Churchill  '),
(211, '<br/><br/><br/><br/><br/><br/><br/><br/>     Il faut faire sentir la nécessité pour nous de sortir de nous-mêmes, <br/>            pour nous mettre à l\'\'école des choses, <br/>                     si nous voulons les connaître et les comprendre. <br/><br/>                                       E. Durkheim'),
(212, '<br/><br/><br/><br/><br/><br/><br/>       <br/><br/><br/><br/><br/><br/><br/>          Une pause pour respirer<br/>'),
(213, '<br/><br/><br/><br/><br/><br/><br/><br/>            Nos meilleures idées viennent des autres. <br/><br/><br/>                                                                                                            R.W. Emerson'),
(214, '<br/><br/><br/><br/><br/><br/><br/><br/><br/>   Celui qui reconnaît consciemment ses limites est le plus profond de la perfection. <br/><br/><br/>                                                                                                                                   J.W. von Goethe'),
(215, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>    <br/><br/>           Rien n\'\'est permanent, sauf le changement. <br/><br/><br/>                                                                                                           Héraclite'),
(216, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>            Peu de gens sont assez sages pour préférer la critique <br/>                                       qui leur est utile à la louange qui les trahit. <br/><br/><br/><br/>          F. de La Rochefoucauld'),
(217, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>                       '),
(218, '<br/><br/><br/><br/><br/><br/><br/>     <br/><br/><br/>   Entreprendre consiste à changer un ordre existant. <br/> <br/>                                                                                                      J. A. Shumpeter'),
(219, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>       Une vie sans examen ne vaut pas la peine d\'\'être vécue. <br/><br/><br/>                                                                                                                    Socrate'),
(220, '<br/><br/><br/><br/><br/><br/>   <br/><br/><br/><br/>     Il vaut mieux vivre le bon chemin en boîtant que le mauvais d\'\'un pas ferme. <br/><br/>          Saint Augustin'),
(221, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>                                               Agir en homme de pensée et penser en homme d\'\'action. <br/><br/>   H. Bergson<br/><br/>'),
(222, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>             Il n\'\'y a pas d\'\'ordre sans justice. <br/><br/>                                                                                         A. Camus'),
(223, '<br/><br/><br/><br/>    <br/><br/><br/><br/><br/>     N\'\'est-il pas nécessaire que nous ayons les pieds sur terre, <br/>        et que nos têtes ne restent pas au niveau du sol? <br/><br/>                                                                                                         Nehru'),
(224, '<br/><br/><br/><br/><br/><br/><br/>     <br/><br/><br/><br/>     Si vous ne savez pas où aller, n\'\'importe quel chemin peut vous y conduire.<br/><br/><br/>     Talmud'),
(225, '<br/><br/><br/><br/><br/><br/><br/><br/>    Si la règle est le désordre, tu paieras d\'\'avoir mis de l\'\'ordre.<br/><br/><br/><br/><br/><br/><br/>                                                                                                                 P. Valéry<br/>   <br/><br/><br/><br/><br/><br/>     '),
(226, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>         Il ne peut y avoir que du profit dans une entente, <br/>                                                        que du préjudice dans un conflit.<br/><br/><br/>                                                                                                          A. Gide'),
(227, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>                    Etre placé au dessus des autres n\'\'est qu\'\'une obligation plus étroite<br/><br/>de travailler pour eux et les servir.<br/><br/><br/>                                                                                                                           L. Bourdaloue'),
(228, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>     Comment voulez-vous <br/>        qu\'\'à l\'\'émancipation politique ne vienne pas s\'\'ajouter, pour les travailleurs, <br/>                                                                                                             l\'\'émancipation sociale <br/>        quand vous avez décrété et préparé vous -même <br/>                                                                                           leur émancipation intellectuelle?<br/><br/>       J. Jaurès<br/>      '),
(229, '<br/><br/><br/><br/><br/><br/>     Celui qui exécute de bonne grâce les ordres échappe <br/><br/>                          au côté pénible de la soumission:  faire ce qui nous rebute.<br/><br/><br/><br/><br/>                                                                                                                    Sénèque'),
(230, '<br/><br/><br/><br/><br/><br/><br/><br/>    Si tu veux être obéi, donne des ordres raisonnables.<br/><br/><br/>                                                                                                            Proverbe arabe  '),
(231, '<br/><br/><br/><br/><br/><br/><br/><br/>              S\'\'il travaille pour toi, tu travailles pour lui.<br/><br/>                                                                                             Proverbe japonais<br/><br/>            '),
(232, '<br/><br/><br/><br/><br/><br/>       <br/>                            Tout pouvoir sans contrôle rend fou.<br/><br/><br/><br/>                                                                                                        Alain'),
(233, '<br/><br/><br/><br/><br/><br/><br/><br/>            Parler est un besoin, écouter est un art. <br/><br/><br/>                                                                                           J. W. von Goethe'),
(234, '<br/><br/><br/><br/><br/><br/><br/><br/><br/> <br/>            Rien n\'\'est si contagieux que l\'\'exemple.<br/><br/><br/>                                                                                         F. de La Rochefoucault'),
(235, '<br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>        On se lasse de tout, excepté de comprendre. <br/><br/><br/>                                                                                                      Virgile'),
(236, '<br><br>J\'aime à penser qu\'est encore possible entre une conversation qui passe d\'une divergence à une entente.<br><br><br><br>Erri de Luca<br>Première heure&nbsp; - Payot et Rivages 2000<br>'),
(237, '');
INSERT INTO `question_qst` (`qst_id`, `cou_id`, `qst_question`, `qst_first`) VALUES
(-19, 52, '<p><br></p><p>La délégation offre-t-elle plus d\'avantages que d\'inconvénients pour l\'intéressé ? <br></p><p><br></p><p>Est-il nécessaire de vous rappeler que l\'intérêt de ces écrans tient d\'abord dans la réponse qu\'il aura générée autre que par une réponse fermée (oui/non).</p>', 1),
(-18, 49, '<p>La direction tient-elle compte de l\'individualisation des tâches</p><p>- pour déléguer tout ou partie de la responsabilité du travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - pour avaliser les compétences et requalifier en conséquence ?&nbsp;</p><p><span style=\"font-size: 10pt;\"><br></span></p><p><span style=\"font-size: 10pt;\">Faites-nous part de toutes vos suggestions . Cela vaut pour tous les écrans.Gardez vous de répondre par des réponses fermées (oui/non). Rechercher par contre des applications à donner aux questions est une démarche bien plus intéressante et souhaitable. L\'utilité de cet EAO repose sur cette modalité. Les questions soumises contiennent et offrent des axes de réflexions</span></p>', 1),
(-17, 48, '<p>Êtes vous</p><p>- homme d\'autorité ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - homme de compréhension ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - homme d\'ombre ? <br></p><p>Si oui, tirez-vous entière satisfaction de ces traits de l\'un ou plusieurs de ces caractères ? <br></p><p><br></p><p>Gardez vous de répondre par des réponses fermées (oui/non). Rechercher par contre des applications à donner aux questions est une démarche bien plus intéressante et souhaitable. L\'utilité de cet EAO repose sur cette modalité.Les questions soumises contiennent et offrent des axes de réflexions</p>', 1),
(-16, 47, '<p>Le pouvoir est-il nécessaire ? <br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Faut-il qu\'il se conçoive concentré ? <br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; Peut-on en imaginer d\'autres formes ?</p><p><br></p><p> Les écrans proposés, malgré leur propension à répondre par une réponse fermée, doivent inviter à trouver des applications. Ces écrans ne demandent qu\'à être enrichis. Faites nous part de toutes vos réflexions, de toutes vos idées, remarques et critiques. <br></p>', 1),
(-15, 44, '<p>Une partie des causes de conflit est-elle réalisée par une politique relationnelle ? Une partie des causes de conflit est-elle traitée par l\'effacement concerté des signes liés</p><p>- au pouvoir ?</p><p>- à la rémunération ?</p><p>- au savoir ?</p><p>- à la propriété ? Gardez vous de répondre par des réponses fermées (oui/non). Rechercher par contre des applications à donner aux questions est une démarche bien plus intéressante et souhaitable. L\'utilité de cet EAO repose sur cette modalité. Les questions soumises contiennent et offrent des axes de réflexions</p>', 1),
(-14, 43, '<p>Le sentiment d\'une rémunération perçue comme insuffisante</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- au regard de la répartition des salaires,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- au regard des capacités et compétences d\'autrui,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- au regard du travail fourni</p><p>est-il, selon vous, une source de tension et une cause de conflit ? <br></p><p>Cet écran n\'appelle pas de réponse fermée. Il ne vous est que soumis. Tirez en toutes les ressources, toutes les applications, toutes les questions possibles.Comme tous les écrans il est à prolonger, à commenter.Il importe de lui trouver des applications à travers les questions soumises.</p>', 1),
(-13, 34, '<p>La démotivation ne puiserait-elle pas ses causes</p><p>- dans la sur-qualification des collaborateurs ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- dans le sous emploi d\'un personnel trop formé ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- dans le décalage entre la formation reçue et la fonction assumée ? <br></p><p>La démotivation ne trouverait-elle pas ses origines</p><p>- dans la sous-utilisation du personnel non diplômé mais doté de bonnes capacités intellectuelles et professionnelles ?</p><p>- dans le décalage entre l\'emploi et les potentialités de ces personnes ? <br></p><p><br></p><p>Gardez vous de répondre par des réponses fermées (oui/non). Rechercher par contre des applications à donner aux questions est une démarche bien plus intéressante et souhaitable. L\'utilité de cet EAO repose sur cette modalité. Les questions soumises contiennent et offrent des axes de réflexions</p>', 1),
(-12, 32, '<p>La motivation sait-elle se gagner sur les friches de la démotivation notamment</p><p>- en réduisant les pesanteurs sociales (relations conventionnelles alors que le personnel est jeune, etc...) ?</p><p>- en usant (du tutoiement à bon escient ou) du vouvoiement, etc... ?</p><p>- en levant les rigidités sociales ? <br></p><p>Cet écran n\'appelle pas de réponse fermée. Il ne vous est que soumis.Pour être pleinement profitable, abstenez vous de vous satisfaire d\' un oui ou d\'un non, mais recherchez des applications aux questions posées. Prolongez la question pour trouver des réponses en émettant des nouvelles questions. </p>', 1),
(-11, 31, '<p>Adhéreriez-vous au constat suivant: \\Il ne saurait y avoir de service efficace sans prestataire prêt et disposé à le donner, sans preneur capable de le recevoir?. Hervé de Syriex (interview radiophonique - France Culture)</p><p><br></p><p>Gardez vous de répondre par des réponses fermées (oui/non). Rechercher par contre des applications à donner aux questions est une démarche bien plus intéressante et souhaitable. L\'utilité de cet EAO repose sur cette modalité. Les questions soumises contiennent et offrent des axes de réflexions</p>', 1),
(-10, 29, '<p><br></p><p>&nbsp; &nbsp; &nbsp; Peut-on motiver ? </p>', 1),
(-9, 20, '<p><br></p><p>La formation est-elle détachée d\'une absence d\'a priori quant à ses bénéficiaires ?</p><p><br></p><p> Gardez vous de répondre par des réponses fermées (oui/non). Rechercher par contre des applications à donner aux questions est une démarche bien plus intéressante et souhaitable. L\'utilité de cet EAO repose sur cette modalité. Les questions soumises contiennent et offrent des axes de réflexions</p>', 1),
(-8, 15, '<p><br></p><p>La formation peut-elle être un objectif en soi ? <br></p><p><br></p><p>Conseil: gardez vous de répondre par oui ou par non.Une telle réponse ne vous apportera que peu de chose.Creusez la question, cherchez à lui donner des applications.Seules ces dernières seront utiles profitables.Ce message sera renouvelé uniquement pour vous le remettre en mémoire. </p>', 1),
(-7, 12, '<p><br></p><p>L\'éthique est-elle un ingrédient de votre Gestion des Ressources Humaines ? <br></p><p><br></p><p>Gardez vous de répondre par des réponses fermées (oui/non). Rechercher par contre des applications à donner aux questions est une démarche bien plus intéressante et souhaitable. L\'utilité de cet EAO repose sur cette modalité. Les questions soumises contiennent et offrent des axes de réflexions</p>', 1),
(-6, 11, '<p>Dans le cas d\'un départ volontaire, les raisons suivantes</p><p>- humaines et relationnelles (ex: hiérarchie pesante, climat lourd),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- psychologiques (inadaptation culturelle, incompatibilité...),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - structurelles (absence d\'innovation, rigidité des structures...),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - sociales (perspectives de carrière réduite ou inexistante, ...),</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - financières</p><p>sont-elles recherchées, analysées ? <br></p><p>Gardez vous de répondre par des réponses fermées (oui/non). Rechercher par contre des applications à donner aux questions est une démarche bien plus intéressante et souhaitable. L\'utilité de cet EAO repose sur cette modalité. Les questions soumises contiennent et offrent des axes de réflexions.</p><p>Développez le. Donnez vous les moyens de rechercher les causes les plus cachées. Donnez vous le temps de la réflexion.</p>', 1),
(-5, 9, '<p>Gérer les Ressources Humaines requiert-il <br></p><p>&nbsp; 1) de ne pas rechercher à tout prix un résultat mais de fournir les conditions permettant la réalisation de ce résultat ?</p><p>&nbsp; 2) d\'appliquer des recettes ? <br></p><p>&nbsp; 3) de développer une politique non assortie d\'une approche globale ? <br></p><p>Pourquoi selon vous ? <br></p><p>Gardez vous de répondre par des réponses fermées (oui/non). Rechercher par contre des applications à donner aux questions est une démarche bien plus intéressante et souhaitable. L\'utilité de cet EAO repose sur cette modalité. Les questions soumises contiennent et offrent des axes de réflexions.</p>', 1),
(-4, 7, '<p>Pour induire des nouvelles représentations des individus instillez-vous des des prises de conscience enclines</p><p>- à relativiser le savoir acquis ?</p><p>- à insuffler le doute ?</p><p>- à admettre le relatif ?</p><p>- à faire reconnaître la méconnaissance ou l\'ignorance ? sans culpabiliser ? Gardez vous de répondre par des réponses fermées (oui/non). Rechercher par contre des applications à donner aux questions est une démarche bien plus intéressante et souhaitable. L\'utilité de cet EAO repose sur cette modalité. Les questions soumises contiennent et offrent des axes de réflexions.N\'hésitez pas à revenir sur votre choix et à répondre non si vous avez répondu oui.Les deux itinéraires peuvent être instructifs. </p>', 1),
(-3, 4, '<p>Le travail est-il rendu aussi agréable que possible ? <br></p><p>Prenez soin de chercher à exploiter tous les aspects de cette question: <br></p><p>le travail est-il rendu psychologiquement satisfaisant, supportable ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;socialement attrayant ? <br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; physiquement satisfaisant ? <br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;intellectuellement intéressant ? <br></p><p>Gardez vous de répondre par des réponses fermées (oui/non). Rechercher par contre des applications à donner aux questions est une démarche bien plus intéressante et souhaitable. L\'utilité de cet EAO repose sur cette modalité. Les questions soumises contiennent et offrent des axes de réflexions.</p>', 1),
(-2, 3, '<p>Les conditions matérielles apportées à la bonne exécution du travail passent-elles par une attention aux aspects techniques tels que:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- une protection des machines ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - des outils et des moyens toujours adaptés ?</p><p>- des installations parfaitement entretenues et donc constamment disponibles ?</p><p>- un aménagement des installations réalisé avec le concours des salariés, celui du CHSCT, d\'un technicien ou d\'un ergonome ? <br></p><p>Gardez vous de répondre par des réponses fermées (oui/non). Rechercher par contre des applications à donner aux questions est une démarche bien plus intéressante et souhaitable. L\'utilité de cet EAO repose sur cette modalité. Les questions soumises contiennent et offrent des axes de réflexions</p>', 1),
(-1, -1, 'default', 0),
(1, 1, '<p><br></p><p>Ressentez-vous le besoin d\'améliorer la Gestion des Ressources Humaines que vous utilisez aujourd\'hui ?&nbsp;</p><p><br></p><p>Gardez vous de répondre par des réponses fermées (oui/non). Rechercher par contre des applications à donner aux questions est une démarche bien plus intéressante et souhaitable. <br></p><p>L\'utilité de cet EAO repose sur cette modalité. Les questions soumises contiennent et offrent des axes de réflexions.</p>', 1),
(2, 1, '<p>Ce besoin de novation incite-t-il à revoir</p><p>- plutôt peu,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- beaucoup,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- globalement </p><p>la Gestion des Ressources Humaines dans l\'entreprise ? <br></p><p><br></p><p>Bien que composé d\'une question fermée, cet écran est laissé à votre entière appréciation. Il convient de rechercher l\'application que vous pouvez tirer de cette question dans votre entreprise.</p>', 0),
(3, 1, '<p><br></p><p>Cette rénovation potentielle de la Gestion des Ressources Humaines (GRH) peut-elle amener à réviser vos modes de fonctionnement suite à une observation critique ? <br></p><p><br></p><p>Cet EAO ne trouve son entière application que dans les modalités de réponses que vous lui donnerez. Il ne peut y avoir de meilleures conclusions aux questions qui vous sont soumises. </p>', 0),
(4, 1, '<p>La novation de la Gestion des Ressources Humaines</p><p>- inclinerait-elle à éliminer des comportements ?</p><p>- inclinerait-elle à éliminer certains comportements sans en introduire d\'autres ? <br></p><p><br></p><p>Prendre le soin de méditer la question et de lui trouver une application possible est donc essentiel.Ce n\'est qu\'à ce prix que l\'optimisation de cette consultation trouve sa concrétisation.</p>', 0),
(5, 1, '<p>Cette remise en question de la GRH prendrait-elle en compte</p><p>- de nouveaux comportements psychologiques ?</p><p>- de nouveaux comportements sociaux ?</p><p>- un dialogue et une attitude plus poussés ?</p><p>- un désir de négociation ?</p><p>- une réelle volonté de changement ?</p><p>- des attitudes plus pragmatiques ? <br></p><p>Les questions invitent à une introspection. Ne l\'évitez pas. C\'est là une de leur utilité.</p>', 0),
(6, 1, '<p>Ce besoin de nover la GRH passe-t-il par une réflexion sur le fonctionnement de l\'entreprise via</p><p>- l\'organisation qu\'elle ordonne,</p><p>- les conditions de travail qu\'elle décide,</p><p>- la distribution du pouvoir qu\'elle impose,</p><p>- l\'insertion des hommes dans la vie de l\'entreprise qu\'elle invite,</p><p>- le mode de direction qu\'elle conduit ? </p><p>Tous les écrans invitent à une réflexion. Prenez le temps que requiert chaque ligne. Écrivez les initiatives qu\'elles vous inspirent dans le cadre réponse qui vous est offert. Faites nous part de toutes vos remarques, suggestions, compléments.</p>', 0),
(7, 1, '<p>Le besoin de nover la GRH peut-il se passer de l\'examen des structures sociales de l\'entreprise:</p><p>- la sociologie qui la sous-tend,</p><p>- la pyramide des âges dont elle est tributaire,</p><p>- la pyramide d\'ancienneté dont elle est marquée ? <br></p><p>Les principales invitations vous ont été faites dans les précédents écrans. La méthode socratique à laquelle il est recouru nous paraît être le meilleur moyen d\'assurer la solution la plus optimale du changement. </p>', 0),
(8, 1, '<p>Le besoin de revoir la GRH intègre-t-il l\'examen des structures de l\'entreprise à travers:</p><p>- les mentalités ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- le niveau culturel général existant ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- le niveau technique ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - l\'histoire technique et sociale de l\'entreprise ?&nbsp;</p><p><br></p><p>L\'essentiel étant dit quant à l\'utilisation et à l\'exploitation de cette longue suite d\'interrogations. Nous vous souhaitons bonne continuation.</p>', 0),
(9, 1, '<p><br></p><p>Le besoin de novation de la Gestion des Ressources Humaines (GRH) s\'attachera-t-il à introduire un environnement plus performant: cadre de travail, système de rémunérations reconnu, etc... ? <br></p><p><br></p><p><br></p><p>Cet écran est laissé à votre seule appréciation. </p>', 0),
(10, 1, '<p>Cependant, la réponse exprime-t-elle une satisfaction complète des comportements existants ? La réponse doit-elle être entendue comme une inexistence de dysfonctionnements comportementaux ? GRH-dialogue vous convie à poursuivre ce dialogue.</p>', 0),
(11, 1, '<p>Faut-il accepter les individus en s\'en accommodant ? Faut-il s\'accommoder de cette solution ? </p>', 0),
(12, 1, '<p><br></p><p>L\'intention d\'accepter les individus se manifeste-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par une écoute plus grande ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par une information plus largement divulguée ?&nbsp; </p><p><br></p><p>Montrez vous d\'autres manifestations d\'intégration et de reconnaissance ? </p>', 0),
(13, 1, '<p>L\'intention d\'accepter les individus tels qu\'ils sont, se manifeste-t-elle dans le quotidien des relations</p><p>- par une confiance lucide et mesurée ?</p><p>- par un intérêt pour la vie sociale des collaborateurs ?</p><p>- par une reconnaissance sociale de chacun ? </p>', 0),
(14, 1, '<p>L\'intention d\'accepter les individus tels qu\'ils sont, se manifeste-t-elle dans le quotidien des relations existantes</p><p>- par une réserve des a priori sociaux et culturels ou mieux</p><p>- par une absence de ces mêmes a priori sociaux, voire</p><p>- par une culture de l\'image positive de chacun ? </p>', 0),
(15, 1, '<p><br></p><p>Cette capacité à accepter les hommes se traduit-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - en déléguant une part de pouvoir et d\'autorité ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- en reconnaissant les compétences pour déléguer ? <br></p><p><br></p><p>Donnez vous d\'autres signes d\'acceptation des hommes dans des registres encore peu fréquents comme ceux de l\'autorité ? </p>', 0),
(16, 1, '<p><br></p><p>Cette capacité à accepter les hommes se traduit-elle dans les actions de formation</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- en manifestant une volonté de \\ faire\\ avec les hommes ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- en sollicitant des efforts tout en les accompagnant ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - en privilégiant les aspects humains aux aspects techniques ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - autres ? </p>', 0),
(17, 1, '<p>Le principe d\'accepter les individus tels qu\'ils sont, recherche-t-il</p><p>- une meilleure adéquation de l\'emploi à l\'homme plutôt que de l\'homme à l\'emploi ?</p><p>- une expression aussi aboutie que possible des compétences et des capacités de chacun ? Ce principe améliore-t-il</p><p>- qualitativement et quantitativement le travail produit ? </p>', 0),
(18, 1, '<p><br></p><p>Le principe d\'accepter les individus tels qu\'ils sont, inclut-il</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- d\'intégrer l\'expression orale comme une nécessité psychologique ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - de favoriser cette expression orale ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- de tirer profit de ces expressions ? </p>', 0),
(19, 1, '<p><br></p><p>La réalité des situations globales peut-elle faire oublier la réalité de <br></p><p>l\'individualisation ? <br></p><p>Le souhait probable de bien gérer les RH autorise-t-il à s\'en accommoder seulement par dépit ? <br></p><p>La participation n\'exige-t-elle pas d\'intégrer cette donnée sans l\'exagérer, sans en faire le pivot unique de la GRH ni l\'omettre ? </p>', 0),
(20, 1, '<p>L\'entreprise peut-elle être et rester une entité indépendante des hommes <br></p><p>qui la constituent, <br></p><p>qui partagent une part de son devenir, <br></p><p>qui ont besoin d\'elles comme celle-ci a besoin d\'eux ? <br></p><p><br></p><p>Peut-elle faire abstraction de l\'évolution de la société et donc des hommes ? </p>', 0),
(21, 1, '<p>L\'entreprise peut-elle se priver de tirer tout le parti des hommes ?&nbsp;</p><p><br></p><p>Peut-elle négliger de s\'enrichir de tous les caractères de cette micro-société ?&nbsp;</p><p><br></p><p>Ne peut s\'inviter à accepter les différences présentes dans l\'entreprise plus pour le meilleur que pour le pire ? </p>', 0),
(22, 1, '<p><br></p><p>Faites-vous de la gestion des compétences un élément essentiel de votre Gestion <br></p><p>des Ressources Humaines ? </p>', 0),
(23, 1, '<p><br></p><p><br></p><p>La gestion des compétences optimise-t-elle l\'adéquation des hommes aux emplois ? </p>', 0),
(24, 1, '<p><br></p><p>La gestion des compétences optimise-t-elle les savoirs acquis par une distribution satisfaisante des emplois ? <br></p><p>Optimise-t-elle les savoirs apportés ? <br></p><p>Potentialise-telle les capacités observées ? <br></p><p>Développe-t-elle les potentialités ? <br></p><p>Cultive-t-elle l\'optimisation du couple homme/production ? </p>', 0),
(25, 1, '<p>Le non-emploi des potentialités, des acquis, des capacités, les contre-emplois ne risquent-ils pas d\'être frustrants et donc démotivants ? Cette inadéquation n\'est-elle pas le ferment</p><p>- d\'une mauvaise image de l\'entreprise ?</p><p>- d\'un mauvais climat de travail ?</p><p>- des conditions d\'une mauvaise production, de rebuts... ?</p><p>- d\'une non participation ? </p>', 0),
(26, 1, '<p><br></p><p><br></p><p>Cette inadéquation ne reflète-t-elle pas une gestion insuffisante des RH ? </p>', 0),
(27, 1, '<p><br></p><p><br></p><p>La satisfaction (réalisation) des individus est-elle une composante de la Gestion des Ressources Humaines en place ? </p>', 0),
(28, 1, '<p>La GRH s\'applique-t-elle</p><p><span style=\"font-size: 10pt;\">&nbsp; - à faire des hommes contents de leur travail ?</span></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à rendre les hommes satisfaits dans leur travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à produire des conditions relationnelles satisfaisantes ? <br></p><p><br></p><p>Cet écran n\'appelle pas de réponse fermée. Se contenter d\'une telle réponse manquerait l\'intérêt de ce compagnon de réflexion.Rechercher des applications à chacune des interrogations semble bien plus judicieux. </p>', 0),
(29, 1, '<p><br></p><p><br></p><p>Si l\'osmose des hommes dans le travail est réalisée, l\'harmonie des hommes entre eux est-elle aussi recherchée ? </p>', 0),
(30, 1, '<p><br></p><p><br></p><p>Quelle que soit sa place (son niveau hiérarchique), la satisfaction des collaborateurs est-elle constamment recherchée ? </p>', 0),
(31, 1, '<p><br></p><p><br></p><p>La gestion des RH peut-elle éviter d\'analyser la satisfaction éventuelle que peut donner le travail aux individus ? </p>', 0),
(32, 1, '<p><br></p><p>La gestion des Ressources Humaines peut-elle occulter raisonnablement les aspects relationnels comme composante de satisfaction du travail sans que l\'entreprise ait à en souffrir ? <br></p><p>Peut-on oublier que l\'homme est un \"animal\" social qui s\'accommode mal du silence, du manque d\'attention, de l\'indifférence, de l\'ignorance d\'autrui, de l\'injustice, du manque de confiance.</p><p> Et qu\'il a par dessous tout besoin qu\'on lui reconnaisse de l\'attention, de l\'affection ? </p>', 0),
(33, 1, '<p>Dans la logique de la Gestion des Ressources Humaines, l\'épanouissement de l\'homme au travail est-il une condition </p><ul><li>1. utile, mais non indispensable à l\'efficacité économique ? </li><li>2. souhaitable, mais non nécessaire à l\'efficacité économique ? </li><li>3. utile, mais non indispensable à l\'efficacité économique ?</li> <br>Selon vous pourquoi ? <p></p></ul>', 0),
(34, 1, '<p>Si l\'épanouissement de l\'homme au travail est utile mais non indispensable, faut-il conforter cet épanouissement par</p><p>&nbsp; - des attentions à l\'égard de l\'ensemble du corps social ?</p><p>&nbsp; - un projet social proposé à l\'assentiment de tous ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; -&nbsp; une organisation du travail plus dynamique et plus innovante ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- un projet professionnel évolutif pour le personnel y adhérant ? </p>', 0),
(35, 1, '<p>Si l\'épanouissement de l\'homme au travail est une condition nécessaire à l\'efficacité économique, faut-il l\'exprimer et l\'entretenir par un projet professionnel, personnel et social laissé à l\'appréciation de chacun ?</p><p> Les aspects matériels et psychologiques sont-ils des éléments auxquels vous faites appel <br></p><p>a- quelquefois ? <br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; b- souvent ? <br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; c- en permanence ?</p><p> Cet écran n\'appelle pas de réponse fermée, malgré son apparence.</p>', 0),
(36, 1, '<p><br></p><p>Si l\'épanouissement de l\'homme au travail n\'est pas indispensable à l\'efficacité économique, l\'entreprise ne gagne-t-elle pas à intégrer cette composante pour s\'attacher une plus grande participation des hommes ? </p>', 0),
(37, 1, '<p>Des hommes bien en eux, dans leur travail et au sein de l\'entreprise ne sont-ils pas</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - la garantie d\'une meilleure production ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - d\'une plus grande efficacité ?</p><p>- la base d\'un consensus plus probable sinon d\'une conflictualité moins marquée ? </p>', 0),
(38, 1, '<p><br></p><p>La Gestion des Ressources Humaines serait-elle davantage affaire de conditions que de mesures ? </p>', 0),
(39, 1, '<p>Si une bonne gestion des Ressources Humaines tenait à des conditions, les dispositions de cette optimisation des RH passeraient-elles</p><p>- par des perspectives d\'une meilleure rémunération ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par des perspectives de carrière ? </p>', 0),
(40, 1, '<p>Si une bonne gestion des Ressources Humaines tenait à des conditions, les dispositions de cette optimisation des RH passeraient-elles</p><p>- par une politique de formation inventive et novatrice soucieuse autant des demandes professionnelles que personnelles ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par un aménagement des conditions de travail ?</p><p>- par une adhésion à un système de travail ? </p>', 0),
(41, 1, '<p>Si une bonne gestion des Ressources Humaines tenait à des conditions, les dispositions en faveur d\'une gestion optimale des RH passeraient-elles par le filtre</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - de notions de plaisir et de satisfaction ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - de l\'intérêt offert par le travail présent et à venir ? <br></p><p><br></p><p>Cet écran n\'appelle pas de réponse fermée comme d\'ailleurs toutes les autres.</p>', 0),
(42, 1, '<p>Si une bonne gestion des Ressources Humaines tenait à des conditions, les dispositions en faveur d\'une gestion optimale des RH passeraient-elles par le filtre</p><p>- d\'un sentiment de compter, d\'être utile ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- d\'une plus large information ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- d\'un épanouissement professionnel ? <br></p><p><br></p><p>N\'oubliez pas: ne vous contentez pas d\'une réponse fermée. </p>', 0),
(43, 1, '<p>Si une bonne gestion des Ressources Humaines tenait à des conditions, les dispositions d\'une gestion optimale des RH passeraient-elles selon vous</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par l\'absence de pesanteurs sociales ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par des attributions définies mais souples ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par une autonomie et une responsabilisation de chacun ? </p>', 0),
(44, 1, '<p>Si une bonne gestion des Ressources Humaines tenait à des conditions, les dispositions d\'une gestion optimale des RH passeraient-elles selon vous</p><p>&nbsp; &nbsp;- par l\'instauration d\'une confiance réciproque et mutuelle ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par une direction forte dans ses objectifs et ses décisions ? <br></p><p><br></p><p>Cet écran n\'appelle pas de réponse fermée. Poursuivez votre réflexion, gage d\'une action envisageable.</p>', 0),
(45, 1, '<p>Si une bonne gestion des Ressources Humaines tenait à des conditions, les dispositions d\'une gestion optimale des RH passeraient-elles selon vous</p><p>&nbsp; &nbsp;- par un contrôle du travail, supervisé par le biais d\'un encadrement reconnu compétent ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par des projets à court et moyen termes ?</p><p> Cette liste non exhaustive mériterait certainement d\'être complétée. Vous pouvez nous faire parvenir vos contributions. Nous en nourrirons cet EAO.</p>', 0),
(46, 1, '<p>Si une bonne gestion des Ressources Humaines tenait à des conditions, la solidarité entre l\'entreprise et le salarié est-elle établie</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- en restant proches du personnel ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - en construisant une relation interpersonnelle riche ? <br></p><p><br></p><p>La solidarité entre l\'entreprise et le salarié est-elle établie</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - en montrant une disponibilité constante ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- en instituant l\'exemplarité comme pierre angulaire de la participation ? </p>', 0),
(47, 1, '<p>Outre la proximité avec le personnel, la disponibilité, l\'exemplarité, les liens avec le personnel sont-ils entretenus par un jeu de demandes et de concessions ? <br></p><p>L\'entreprise et les salariés s\'attachent-ils à être redevables réciproquement ? </p>', 0),
(48, 1, '<p>En somme, la situation juridico-sociale (contrat de travail) est-elle confortée par une situation socio-psychologique (d\'être à être) afin d\'optimiser les liens et les contrats sociaux nécessaires au bon fonctionnement de l\'entreprise ? </p>', 0),
(49, 1, '<p>Ecarter les liens de solidarité entre l\'entreprise et les salariés n\'exclut-il pas, de fait, toute relation contractuelle hormis l\'échange salaire / travail ? <br></p><p><br></p><p>Les réciprocités ne sont-elles pas</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - un des ciments des liens sociaux ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- les ferments de toutes les relations sociales de par le monde ? </p>', 0),
(50, 1, '<p>Si la gestion des Ressources Humaines tient plus à l\'instauration de conditions qu\'à l\'instauration de mesures, la réduction des barrières de toute nature (sociales, culturelles...) créée par un style relationnel plus personnalisé compterait-elle parmi les conditions mises en place ou à introduire ? </p>', 0),
(51, 1, '<p>L\'analyse et l\'observation de l\'organisation sociale ne permettent-elles pas d\'enrichir et de compléter ces quelques évocations ? </p>', 0),
(52, 1, '<p>Le maintien de barrières ne risque-t-il pas d\'affecter sinon d\'empêcher la participation ? <br></p><p>L\'échange et par conséquent la productivité ou/et la qualité ne risquent-ils pas de s\'en ressentir ? </p>', 0),
(53, 1, '<p>La réduction des barrières de toute nature comme condition à la gestion des RH, n\'affecte-t-elle pas sensiblement la nature des Relations Humaines et Sociales ? En somme, la Gestion des Ressources Humaines n\'en est-elle pas affectée ? </p>', 0),
(54, 1, '<p>Si la Gestion des Ressources Humaines relève plus de l\'instauration de conditions que de l\'instauration de mesures, compteriez vous la réduction des barrières de toute nature (sociales, culturelles...) créée</p><p>- par une atmosphère sociale autorisant une plus grande expressivité,</p><p>- une meilleure connaissance des hommes entre eux compte-t-elle parmi les conditions mises en place,comme l\'une de ces mesures. <br></p><p><br></p><p>Poursuivez la lecture de ce panel .</p>', 0),
(55, 1, '<p>Si la gestion des Ressources Humaines relève plus de l\'instauration de conditions que de l\'instauration de mesures, la mise à jour d\'expressions cachées, de non dits, de revendications non exprimées compterait-elle parmi les moyens existants pour optimiser la Gestion des Ressources Humaines ? <br></p><p><br></p><p>Poursuivez la lecture de ce panel .</p>', 0),
(56, 1, '<p>Si la Gestion des Ressources Humaines relève plus de l\'instauration de conditions que de l\'instauration de mesures, l\'autonomie et les latitudes d\'appréciation accordées compteraient- elles parmi ces conditions de la Gestion des Ressources Humaines ? </p>', 0),
(57, 1, '<p>L\'analyse et l\'observation de l\'organisation sociale ne permettent-elles pas d\'enrichir et de compléter l\'atmosphère de travail créée ? </p>', 0),
(58, 1, '<p>L\'autonomie et les latitudes d\'appréciation accordées comme conditions de la Gestion des Ressources Humaines, sont-elles accompagnées par une transformation du statut d\'acteur passif (de simple exécutant) à celui de créateur actif (responsable de ses actes) ? </p>', 0),
(59, 1, '<p>L\'autonomie et les latitudes d\'appréciation comme conditions de la Gestion des Ressources Humaines, seraient-elles accompagnées d\'une attitude débitrice vis à vis de vos collaborateurs ? </p>', 0),
(60, 1, '<p>Ecarter l\'autonomie et les latitudes d\'appréciation comme conditions de la GRH, ne contraint-il pas</p><p>&nbsp; &nbsp; &nbsp; - les salariés à s\'auto-censurer ?</p><p>&nbsp; &nbsp; &nbsp; - à les priver d\'une liberté peut être préjudiciable à une meilleure efficacité de l\'entreprise ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - en définitive à se conduire comme de simples exécutants ? <br></p><p>Cette auto-censure et cette privation d\'une liberté ne restreignent-elles pas les possibilités de profit pour l\'entreprise ? </p>', 0),
(61, 1, '<p>Si la Gestion des Ressources Humaines relève plus de l\'instauration de conditions que de l\'instauration de mesures, pour produire une gestion optimale des RH, l\'entreprise sait-elle rester ouverte aux expériences induisant</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - des changements dans les méthodes de travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - des ruptures dans les modes traditionnels de fonctionnement ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - des adaptations des hommes au travail ? </p>', 0),
(62, 1, '<p>Si la Gestion des Ressources Humaines relève plus de l\'instauration des conditions que de l\'instauration de mesures, outre les conditions précédemment évoquées et les causes suivantes</p><p>&nbsp; &nbsp;  - mauvaise organisation,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - équipements vétustes ou inadaptés aux conditions normales du travail,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - absence de participation,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - cadre relationnel figé,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - politique motivationnelle peu opérante,</p><p>quelles autres raisons grèvent, selon vous, le climat relationnel, la productivité et la qualité ? <br></p><p>Accordez vous du temps (l\'écran suivant vous aidera) ou mieux soumettez c', 0),
(63, 1, '<p>Si la Gestion des Ressources Humaines relève plus de l\'instauration de conditions que de l\'instauration de mesures, pour s\'optimiser la Gestion des Ressources Humaines et des Relations Sociales s\'emploie-t-elle à écarter l\'application de solutions standards au profit de solutions plus spécifiques ? <br></p><p><br></p><p>Cet écran n\'en appelle qu\'à votre jugement et à celui de vos collaborateurs. </p>', 0),
(64, 1, '<p>Faute de conditions, quels aspects constitueraient, selon vous, les préalables d\'une gestion optimale des Ressources Humaines ?</p><p><br></p><p> GRH-Dialogue espère contribuer à compléter ces aspects.</p>', 0),
(65, 1, '<p>Est-il appliqué à la Gestion des Ressources Humaines un soin identique sinon supérieur à celui attribué aux finances de l\'entreprise ? </p>', 0),
(66, 1, '<p><br></p><p>Poursuivez votre dialogue avec GRH-dialogue. Il ne peut que vous être profitable. La méthode employée, socratique , permet de vous convaincre de l\'utilité de quelques mesures utiles à introduire ou d\'attitudes à changer. Les unes et les autres marqueront les esprits. L\'atmosphère régnante pourra être modifiée. Les perceptions seront rénovées.</p>', 0),
(67, 1, '<p>Les Ressources Humaines ne sont-elles pas reconnues comme un capital particulièrement important ? <br></p><p>Leur importance ne croît-elle pas avec le renouvellement des techniques et des technologies ? <br></p><p>Comment marquez vous leur importance pour la première question ? <br></p><p>Comment utilisez vous les nouvelles technologies pour donner aux individus plus d\'importance ? <br></p><p>Malgré sa formulation, cet écran n\'appelle pas de réponse fermée. </p>', 0),
(68, 1, '<p>Est-il appliqué à la Gestion des Ressources Humaines un soin identique sinon supérieur à celui attribué aux investissements à court, moyen ou long terme ? </p>', 0),
(69, 1, '<p>Une pause avant de poursuivre votre dialogue avec l\'introduction de GRH-dialogue. Vous avez tout le loisir de vous accorder des respirations nécessaires. Vous pouvez pour cela aller cueillir une pause dans l\'index du même nom. </p>', 0),
(70, 1, '<p><br></p><p>Il vous appartient de retenir ce choix. Cependant, la négligence portée à la Gestion des Ressources Humaines ne fragilise-t-elle pas la portée de vos investissements ? </p>', 0),
(71, 1, '<p>Est-il appliquée à la gestion des Ressources Humaines une attention identique sinon plus grande que celle accordée aux matériels ? </p>', 0),
(72, 1, '<p>GRH-dialogue espère vivement conforter cette situation en vous soumettant parfois un éclairage critique, une mise en perspective des composantes de la Gestion des Ressources Humaines. GRH-dialogue entend contribuer à la rendre encore plus active et plus innovante. <br></p><p>Donnez vous les moyens de fatiguer toute question. </p>', 0),
(73, 1, '<p>Ne pas appliquer un même soin à la gestion du personnel qu\'à ceux accordés à la gestion des finances ou des matériels n\'est-il pas susceptible d\'affecter la productivité, la compétitivité ? <br></p><p>Une Gestion des Ressources Humaines délaissée ne doit-elle pas s\'appréhender comme un désinvestissement ? </p>', 0),
(74, 1, '<p>La Gestion des Ressources Humaines ne doit-elle pas être pensée en termes d\'investissement privilégié ? <br></p><p>Le capital immatériel peut-il échapper aux critères classiques des investissements ex: de temps (court, moyen et long termes de rentabilité, etc...) ? </p>', 0),
(75, 1, '<p>Délaisser la gestion du personnel au profit des seuls aspects financiers ou matériels n\'exclut-il pas les gains marginaux liés</p><p>&nbsp;  &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  - à la productivité ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  &nbsp; &nbsp;  &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - à la qualité des produits ?</p><p>&nbsp; &nbsp; &nbsp;  - à la qualité des services ? <br></p><p>Ces gains peuvent-ils se passer du facteur humain ? </p>', 0),
(76, 1, '<p>Délaisser la gestion du personnel au profit des seuls aspects financiers et matériels ne produit-il pas un désinvestissement d\'une part de capital de l\'entreprise ? <br></p><p>Ce désinvestissement n\'est-il pas un manque à gagner ? <br></p><p>Ne met-il pas en danger l\'entreprise ? </p>', 0),
(77, 1, '<p>Délaisser la gestion du personnel au profit des seuls aspects financiers ou matériels, n\'est-ce pas nier tout ce qui a trait à l\'homme ou ce qui ne lui est pas étranger ? <br></p><p>Le facteur humain dans l\'entreprise n\'est-il pas une source de profit à générer, à valoriser, à amplifier constamment ? <br></p><p>Le négliger n\'est-il pas, par contrecoup, une perte ? <br></p><p>Le maintien permanent de la sociabilité n\'est-il pas indispensable ?</p><p> La motivation n\'est-elle pas à ce prix ? </p>', 0),
(78, 1, '<p>En conclusion, la gestion des Ressources Humaines peut-elle faire l\'économie des différences qui la constitue, sans se nier elle-même ? </p>', 0),
(79, 1, '<p>Si la Gestion des Ressources Humaines relève plus de l\'instauration des conditions que de l\'instauration de mesures, outre les conditions précédemment évoquées et les causes suivantes</p><p>&nbsp; &nbsp;  - mauvaise organisation,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - équipements vétustes ou inadaptés aux conditions normales du travail,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - absence de participation,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - cadre relationnel figé,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - politique motivationnelle peu opérante,</p><p>quelles autres raisons grèvent, selon vous, le climat relationnel, la productivité et la qualité ? <br></p><p>Accordez vous du temps (l\'écran suivant vous aidera) ou mieux soumettez cette question à votre entourage.</p>', 0),
(80, 1, '<p>La Gestion des Ressources Humaines peut-elle rester indifférente</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - à l\'individualisation des rémunérations et</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - à la personnalisation du travail ? </p>', 0),
(81, 1, '<p>Tout en intégrant l\'individualisation des rémunérations et la personnalisation du travail, la Gestion des Ressources Humaines s\'emploie-t-elle aussi à associer</p><p>&nbsp; &nbsp; &nbsp; &nbsp;  - le travail en équipe ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp;  - la conjugaison des métiers, des compétences, des compatibilités d\'humeurs,&nbsp; &nbsp; &nbsp; &nbsp;  des bonnes relations, des amitiés, ? </p>', 0),
(82, 1, '<p>Est-il possible de nier des demandes sociales ( en les aménageant éventuellement) sans porter préjudice à l\'entreprise toute entière ? </p>', 0),
(83, 1, '<p>La Gestion des Ressources Humaines intègre-t-elle des éléments quantifiables ? Quels sont-ils ? <br></p><p>Quelle est votre façon de les mesurer ? <br></p><p>Ce mode de mesure vous paraît-il satisfaisant ? <br></p><p>Vous semble-t-il juste ? </p>', 0),
(84, 1, '<p>Selon vous, la Gestion des Ressources Humaines se doit-elle d\'intégrer des composantes affectives ? <br></p><p>Pour vous, la GRH se doit-elle de rechercher une \\alliance conflictuelle\\ (association d\'intérêts bien compris) au dépens d\'une \\situation conflictuelle\\ ? <br></p><p>Ce choix permet-il de considérer les conflits comme une marque de bonne santé ? <br></p><p><br></p><p>Ces questions n\'en appellent comme les précédentes qu\'à votre sagacité.</p>', 0),
(85, 1, '<p>Vous venez de terminer le module introductif à GRH-dialogue. Nous souhaitons vivement que vous avez trouvé plaisir à ce remue méninges personnel. <br></p><p>Nous souhaitons que votre perception ait trouvé matière à se renouveler. <br></p><p>Notre satisfaction se mesure à l\'aune de la vôtre. <br></p><p>GRH-dialogue vous souhaite une agréable et une fructueuse consultation pour les autres modules spécialisés. </p>', 0),
(86, 1, '<p>Peut-on en fait motiver ex nihilo ? </p>', 0),
(87, 1, '<p>Peut-on motiver alors que la motivation ne dépend que des individus donc de considérations personnelles ? </p>', 0),
(88, 1, '<p>Peut-on solliciter un élément ne figurant pas dans le contrat de travail ? sans se voir objecter</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - son absence ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - sa subjectivité ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  -&nbsp;  son imprécision ? <br></p><p>bien que souhaité ? </p>', 0),
(89, 1, '<p>Peut-on demander plus que ce qui n\'a jamais été formulé ou écrit dans le contrat de travail ? </p>', 0),
(90, 1, '<p>Accepteriez vous que l\'idée d\'un contrat est un engagement entre deux personnes sur un contenu en l’occurrence<br></p><p>&nbsp; &nbsp; &nbsp;  - des tâches à effectuer ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - un emploi à tenir et à assurer ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - des responsabilités à assumer ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - une exigence de qualité attendue ? <br></p><p>mais sans que cela ne fasse référence à une quelconque notion de motivation, notion toujours absente du contrat de travail, mais implicitement demandée ? </p>', 0),
(91, 1, '<p>Admettez vous néanmoins de vous en tenir à un emploi sans référence à de la motivation ? </p>', 0);
INSERT INTO `question_qst` (`qst_id`, `cou_id`, `qst_question`, `qst_first`) VALUES
(92, 1, '<p><br></p><p>Peut-on solliciter un élément supplémentaire alors que l’exécution du travail n\'appelle aucune remarque&nbsp; alors qu\'aucune contrepartie ne mentionne une forme de rémunération matérielle (rémunération) ou immatérielle (formation, temps libre...) ? </p>', 0),
(93, 1, '<p>Permettez vous à l\'ensemble de vos collaborateurs</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - de s\'améliorer professionnellement ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - de se développer personnellement ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - de montrer leur s qualités ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - de s\'exprimer dans le travail ( par ce qu\'ils réalisent, par ce qu\'ils disent) ?</p><p>&nbsp; &nbsp; &nbsp;  - de sentir au mieux dans la structure ? <br></p><p>Ces écrans appellent de la confrontation aux pratiques donc à interroger pour innover, transformer. </p>', 0),
(94, 1, '<p>Mettez vous en place les conditions susceptibles d\'amener les salariés à s\'impliquer davantage dans leur travail ? <br></p><p>L\'entreprise crée-t-elle un cadre incitant chacun à être acteur responsable de son emploi en impulsant</p><p>&nbsp; &nbsp; &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  - en instituant un esprit d\'innovation ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - en restant intègre en toute circonstances ?</p><p>&nbsp; - en adoptant des règles sociales conformes à la loi et et principes moraux communément acceptés ?</p><p>- en adoptant un esprit de responsabilité sociale ? </p>', 0),
(95, 1, '<p>En somme, plus que de chercher à motiver , créez vous</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - les conditions favorables au travail ?</p><p>&nbsp; &nbsp;  &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - un environnement en faveur au désir de travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  &nbsp; - à la participation au travail de chacun ? </p>', 0),
(96, 1, '<p>Un sondage (la Tribune 15/05/2006) donnait à lire que</p><p>- 51% des salariés considéraient les conditions de travail comme la chose la plus importante quand on a un emploi,</p><p>- 50% revenait au niveau des rémunérations,</p><p>- 45% à l\'intérêt du métier. <br></p><p>même si la confiance accordée aux dirigeants pour leur déléguer ces aspects, seriez-vous enclin à améliorer ce taux de confiance en les sollicitant davantage ? </p>', 0),
(97, 1, '<p>De façon générale, veillez-vous à ce que les critiques ou les affirmations telles que</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - manque d\'écoute,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - manque de visibilité de l\'évolution professionnelle,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - désintérêt manifeste,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- aveuglement,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - insouciance,</p><p>- détachement,</p><p>- manque d\'ambition soient des reproches susceptibles d\'être exclues par vos efforts ? </p>', 0),
(98, 1, '<p>L\'intention d\'accepter les individus tels qu\'ils sont, se manifeste-t-elle dans le quotidien des relations</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - par une participation à la réalité quotidienne du travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - par une reconnaissance non hiérarchique des relations de travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - autres ? </p>', 0),
(99, 1, '<p>L\'intention d\'accepter les individus se manifeste-t-elle</p><p>- par des formes de consultation plus participatives ?</p><p>- par une diffusion d\'informations jugées utiles, quelle qu\'en soit la teneur ? </p>', 0),
(100, 1, '<p>Cette capacité à accepter les hommes se traduit-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - en rendant autonome des (les) collectifs de travail,</p><p>&nbsp;  - en acceptant la recomposition éventuelle des équipes pour une recherche&nbsp; &nbsp;  constante de l\'optimum humain et industriel ? <br></p><p>Vous donneriez vous d\'étudier les possibilités offertes par ce type de réflexion ? </p>', 0),
(101, 1, '<p><br></p><p>L\'entreprise peut-elle se passer des hommes qui la font ? <br></p><p>L\'entreprise peut-elle ignorer de faire au mieux avec ceux qui la composent et qui en constituent un capital essentiel ? </p>', 0),
(102, 1, '<p>La gestion des Ressources Humaines peut-elle renoncer à prendre en compte les satisfactions que donne la participation ou l\'appropriation du travail ? <br></p><p>Peut-on omettre que l\'idée que le plaisir d\'appropriation (communément traduite dans le souhait des plus créateurs, des plus individualistes de créer leur propre entreprise) reste une donnée toujours actuelle ? </p>', 0),
(103, 1, 'Prenez le temps de contempler cet écran blanc ', 0),
(104, 3, '<p><br></p><p>Veillez vous à corréler l\'équilibre psychologique d\'une personne et son aptitude à occuper le poste ? <br></p><p>Il est sans doute bien d\'autres développements que vous pourriez donner à cette mise en perspective. Montrez vous perspicace, observateur, innovateur.</p>', 0),
(105, 2, '<p>Le travail est-il adapté aux hommes par</p><p>&nbsp; &nbsp; &nbsp;  - considération éthique ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp;  &nbsp; &nbsp;  &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - préoccupation psychologique ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - investissement économique ? <br></p><p>Gardez vous de répondre par des réponses fermées (oui/non). Rechercher par contre des applications à donner aux questions est une démarche bien plus intéressante et souhaitable. L\'utilité de cet EAO repose sur cette modalité. Les questions soumises contiennent et offrent des axes de réflexions.</p>', 1),
(106, 2, '<p>Ce souci d\'adaptation du travail aux hommes s\'est-il manifesté par des réalisations afin d\'harmoniser paroles et actes ? <br></p><p>Cette intention modère-t-elle les revendications par une compréhension des efforts consentis ? <br></p><p>Ce choix traduit-il la volonté réelle ou une volonté diffuse:</p><p>&nbsp; &nbsp;  - de faire sans dire ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  &nbsp;  &nbsp; &nbsp;  &nbsp; &nbsp; &nbsp; &nbsp;  - d\'investir sans demander de contreparties expresses ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - d\'agir sans attendre ? </p>', 0),
(107, 2, '<p><br></p><p>La contribution d\'adaptation du travail à l\'homme n\'est-elle pas un langage des plus accessibles à tout salarié ? <br></p><p>En s\'abstenant d\'y contribuer ne manifeste-t-on pas la volonté de laisser en l\'état la Gestion des Ressources Humaines ? </p>', 0),
(108, 2, '<p><br></p><p>A s\'abstenir d\'adapter le travail aux hommes, ne risque-t-on pas de nuire sinon de gêner voire de réduire à néant une part sensible de la gestion des motivations en particulier, et de la gestion des RH en général ? </p>', 0),
(109, 2, '<p><br></p><p>A s\'abstenir d\'adapter le travail aux hommes, n\'accroît-on le coût du travail plus que n\'aurait coûté cette adaptation ?</p><p> En ce sens, négliger cette adaptation n\'est-il pas plus un coût qu\'un investissement ? </p>', 0),
(110, 2, '<p>L\'aménagement du travail passe-t-il par</p><p>- la mise en perspective du travail de chacun avec l\'ensemble de la ligne de production, précisant où, pourquoi, quand, comment chacun intervient, doit intervenir ou faire intervenir ?</p><p>- la définition d\'objectifs définis en temps, qualité, quantité... ? </p>', 0),
(111, 2, '<p>Si l\'aménagement du travail de chacun passe par une mise en perspective et une définition des attributions, l\'appropriation du travail est-elle encouragée par</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - l\'acceptation des initiatives ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - l\'acceptation d\'efforts servant les objectifs précisés ? </p>', 0),
(112, 2, '<p>Si l\'aménagement du travail de chacun passe par une mise en perspective et une définition des attributions, l\'appropriation du travail est-elle encouragée par</p><p>&nbsp; &nbsp; &nbsp;  - l\'utilisation de toutes les technologies au service de la finalité recherchée ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - l\'aménagement du travail dès lors qu\'il sert les objectifs visés ? </p>', 0),
(113, 2, '<p>L\'absence d\'une définition du travail général et particulier de chacun, de son cadre et de ses objectifs</p><p> - n\'accroît-elle pas le sentiment d\'une indifférence ressentie ?</p><p>- ne laisse-t-elle pas en jachère une participation à laquelle les salariés concourent &nbsp; presque naturellement ?</p><p>- ne réduit-elle pas tout investissement personnel par désintérêt ? </p>', 0),
(114, 2, '<p>Le travail est-il défini en terme d\'objectifs ? <br></p><p>Le travail est-il défini comme la réalisation d\'une tâche précise ou comme une mission à l\'instar de celles confiées à l\'encadrement ?</p><p> Le travail est-il parfois déconnecté d\'une durée prescrite pour autant que la qualité soit assurée ? <br></p><p><br></p><p>Cet écran n\'appelle pas de réponse fermée.. Invitez vous à vous poser des questions qui prolongent ces interrogations. Par exemple:Comment encourager la qualité au dépens d\'une tâche mal finie qui aura des coûts ultérieurs ? </p>', 0),
(115, 2, '<p>Le travail est-il dégagé de la notion de temps de présence sur le lieu de l\'entreprise pour autant que le travail à effectuer est réalisé ?</p><p> Cet écran n\'appelle pas de réponse fermée. Par une telle approche ,certes un peu iconoclaste dans notre contexte, ne pourriez vous pas encourager à faire que le travail soit réalisé plus vite, mais sans pour autant que l\'entreprise ne veuille forcément récupérer l\'intégralité du temps gagné ? <br></p><p>Ne saurait-il pas y avoir de contrat sans donnant- donnant, gagnant-gagnant, sans référence à la seule notion de contrat de travail.</p>', 0),
(116, 2, '<p><br></p><p>L\'aménagement du travail est-il accompagné de délégations ? <br></p><p>Associeriez-vous ces questions de conditions de travail à la question du pouvoir dans l\'entreprise, dans une part liberté de concevoir le travail pour autant que les objectifs soient déterminés, arrêtés, acceptés ? </p>', 0),
(117, 2, '<p><br></p><p>Bien qu\'octroyés, le sens et les contours des mandats sont-ils régulièrement rappelés pour n\'être pas pervertis ?</p><p> Les marges de ces délégations sont-elles clairement explicitées pour autoriser ainsi des initiatives implicites ? </p>', 0),
(118, 2, '<p>Celles ci sont-elles rappelées régulièrement pour éviter tout débordement ? </p>', 0),
(119, 2, '<p><br></p><p>L\'action de délégations régulièrement et nettement redéfinies n\'est-elle pas une mesure souhaitable autant pour en marquer les limites que pour en manifester la précarité ? <br></p><p>Rappeler les contours d\'une délégation octroyée, n\'est ce pas aussi prévenir trop de conservatisme, trop d\'abus de pouvoir ?</p><p> Ces questions ne vous invitent-elles pas à repenser un mode d\'action et de pensée ? </p>', 0),
(120, 2, '<p>Rappeler les contours des délégations accordées n\'est-il pas utile sinon nécessaire autant pour les délégataires que pour les assujettis ? </p>', 0),
(121, 2, '<p><br></p><p>Comment vivriez-vous cette situation d\'aménagement de travail non assortie de délégation si vous aviez à la vivre comme subalterne ? <br></p><p>Cette analyse vous incline-t-elle à maintenir le choix opéré ? </p>', 0),
(122, 2, 'L\'aménagement du travail prend-il en considération les aspects extra-professionnels tels que :<p></p><p>- la desserte de l\'entreprise par des transports collectifs ?</p><p>- l\'invitation faite aux salariés d\'organiser leurs transports par l\'affichage d\'une liste des communes dont sont issus les salariés, etc... ? <br></p><p><br></p><p>Poursuivez la consultation de ce panel .</p>', 0),
(123, 2, 'L\'aménagement du travail prend-il en considération les aspects extra-professionnels tels que</p><p>- l\'aide au logement apportée par une intervention auprès d\'un office HLM ou des rapports suivis avec des syndics locaux ?</p><p>- la participation de l\'entreprise à la création d\'équipements collectifs tels que: crèche, halte-garderie en collaboration avec la municipalité ou d\'autres entreprises ? ', 0),
(124, 2, '<p><br></p><p>D\'autres formules permettant d\'accroître la disponibilité psychologique du personnel ne sont-elles pas encore à imaginer ? <br></p><p>Par exemple en favorisant le co-voiturage au sein de l\'entreprise, en adhérant à une restauration inter-entreprises, en permettant à tout salarié de bénéficier des ressources internes de l\'entreprise (ex mise à disposition à disposition du juriste, d\'un ingénieur pour une question personnelle) ? </p>', 0),
(125, 2, '<p>S\'abstenir de prendre en compte des aspects sociaux, n\'affecte-t-il pas le bien-être des employés, donc</p><p>- leur disponibilité ?</p><p>- leur présence au travail ? La qualité et la productivité du travail peuvent-elles ignorer les aspects périphériques sociaux et psychologiques du travail ? </p>', 0),
(126, 2, '<p>L\'attention portée à la qualité de la vie au travail transite-t-elle par</p><p>&nbsp; &nbsp; &nbsp;  - l\'agencement d\'espaces extérieurs (pelouses, jardin arboré, etc...) ?</p><p>&nbsp; &nbsp; &nbsp;  - la mise à disposition de sièges lors de la pause de midi dans ces mêmes lieux ?</p><p>&nbsp; &nbsp; &nbsp;  - la mise à disposition de terrains de sport inter-entreprises ? <br></p><p>Cet écran n\'appelle pas de réponse fermée. Cependant, voyez vous d\'autres possibilités ? </p>', 0),
(127, 2, '<p>L\'attention portée à la qualité de vie au travail transite-t-elle par</p><p>- l\'installation de distributeurs de boissons, d\'un coin repas, d\'une cafétéria ?</p><p>- de points, de temps téléphoniques permettant à chacun de régler ses contraintes sociales et d\'éviter une surcharge de stress ? (même en temps de généralisation de portables)</p><p>- de l\'utilisation limitée d\' internet pour des utilisations privées ?</p><p>- de points de libre affichage ? </p>', 0),
(128, 2, '<p>L\'entreprise encourage-t-elle l\'installation de ces équipements ? <br></p><p>L\'entreprise sait-elle anticiper les demandes de ces installations ? <br></p><p>Comment pourrait-elle les compléter ? les encourager ? les développer ? <br></p><p>Cet écran est laissé à votre seule appréciation. Pour illustrer concrètement ces initiatives, l\'entreprise ne pourrait-elle envisager de mettre un véhicule non utilisé de sa flotte à la disposition des salariés en guise de dépannage imprévu mais justifié. </p>', 0),
(129, 2, '<p>L\'espace social mis à disposition des salariés peut-il n\'avoir aucun effet</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - sur la productivité ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - sur la qualité du travail rendu ? <br></p><p><br></p><p>Bien qu\'annexe, l\'espace social peut-il être dénué d\'effets</p><p>&nbsp; &nbsp; &nbsp;  - sur les qualités relationnelles ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - sur la forme, la nature des relations nouées ? </p>', 0),
(130, 2, '<p>Vous venez de terminer le questionnaire intitulé \\ aspects périphériques du travail\\inséré dans le thème CONDITIONS de TRAVAIL. <br></p><p>Les conditions de travail restent un aspect encore largement mal exploité. <br></p><p>Elles doivent receler des améliorations sensibles, il serait dommage de ne pas puiser dans ce registre qui ne coûte parfois rien, mais qui peut apporter beaucoup en terme de retours et d\'image.</p>', 0),
(131, 4, '<p><br></p><p>Est-il fait que toute personne trouve plaisir ( ou profit ) à compter parmi les salariés de l\'entreprise (par les perfectionnements proposés, par la qualité de son recrutement, par la qualité de ses prestations, etc...) ? <br></p><p>Cherchez vous à rendre fier les hommes de faire partie de l\'entreprise surtout depuis que le désenchantement gagne ou a gagné l\'entreprise ? </p>', 0),
(133, 3, '<p>L\'amélioration des conditions de travail inclut-elle les recommandations émises par les utilisateurs</p><p>&nbsp; &nbsp; &nbsp;  - pour prévenir les résistances aux changements,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - pour responsabiliser les utilisateurs,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - pour faire accepter le matériel fourni,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - pour donner le sentiment d\'une appropriation,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - pour créer le sens d\'une réelle participation ? </p>', 0),
(134, 3, '<p><br></p><p>Se désintéresser des conditions matérielles du travail, n\'est-il pas ressenti comme une indifférence aux hommes qui ne manqueront pas d\'y être sensibles ? <br></p><p>N\'est-ce pas ruiner une part des efforts entrepris par ailleurs par la Direction des Ressources Humaines ? </p>', 0),
(135, 3, '<p>Négliger les conditions matérielles régissant le travail,</p><p>&nbsp;  - cela n\'accroît-il pas une résistance aux changements ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - cela ne réduit-il pas une participation ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - cela ne frustre-t-il pas les travailleurs d\'une connaissance ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - cela n\'accroît-il pas le sentiment d\'une désappropriation ? <br></p><p>Ces aspects n\'éloignent-ils pas les travailleurs de l\'entreprise ? </p>', 0),
(136, 3, '<p>Consentez vous une attention aux conditions de travail</p><p>- par une coordination des équipes évitant les décalages, la simultanéité ou l\'incompatibilité des travaux ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - par une bonne organisation des processus ? <br></p><p>Recherchez d\'autres moyens susceptibles d\'exploiter ces questions. <br></p><p>Complétez les. Creusez les. </p>', 0),
(137, 3, '<p><br></p><p>L\'attention aux conditions de travail est-elle assurée- par un déroulement chronologique des opérations assurant une totale fluidité des opérations ? <br></p><p>Ainsi, avez-vous demandé aux producteurs leur perception des modalités de travail ? <br></p><p>Leur avez-vous sollicité des remarques sur les process appliqués ? </p>', 0),
(138, 3, '<p>Assurez vous une veille mécanique ou de maintenance des installations ? </p>', 0),
(139, 3, '<p>L\'attention à la qualité de vie au travail est-elle prise en considération par l\'aménagement du cadre extérieur des locaux par exemple: panneaux d\'affichage autres que syndicaux, des murs ouverts à l\'expression, des lieux de graffitis contrôlés, des possibilités de murs peints, etc... ? <br></p><p>Est-il des aspects donnant ou créant le sentiment d\'une vie dans l\'entreprise ?</p><p> Les habitudes de comportement de la vie courante sont-elles acceptées, introduites ou encouragées ? </p>', 0),
(140, 3, '<p><br></p><p>Introduisez vous une qualité de vie au travail - par la fonctionnalité des locaux et des espaces de travail ? <br></p><p>La traduisez-vous&nbsp; par l\'aménagement et la disposition des machines, des bureaux tant d\'un point de vue ergonomique que d\'un point de vue social (espace de déplacement aisé autour des machines, des postes de travail limitation délibérée d\'un contrôle social assez manifeste pour être perçue par les producteurs...) ? </p>', 0),
(141, 3, '<p>Contribuez vous à la qualité de vie au travail</p><p>&nbsp; &nbsp;  - par l\'embellissement des lieux de travail ( cadre, posters, plantes, couleurs harmonisées...) ?</p><p>&nbsp; &nbsp;  - par la personnalisation des lieux de travail (plan de travail) en l\'autorisant et en l\'invitant ?</p><p>&nbsp; &nbsp;  - par une volonté délibérée de rendre les rapports hiérarchiques pas plus pesants que nécessaires ?</p><p>&nbsp; &nbsp;  - par l\'introduction de comportements de la vie courante ? </p>', 0),
(142, 3, '<p>Veillez-vous à la qualité de vie au travail</p><p>&nbsp;  - par la recherche de clarté dans les locaux ?</p><p>&nbsp; &nbsp;  - par l\'autorisation d\'un fond musical pour rompre l\'isolement ou pour aménager les travaux répétitifs ou par des protections en cas de nuisances sonores ?</p><p>&nbsp;  - par l\'instauration de salles de repos accueillantes, de vestiaires, de sanitaires propres et entretenus ?</p><p>&nbsp;  - par une sociabilité toujours présente qui ne dispense pas le commandement ? </p>', 0),
(143, 3, '<br>Pour éviter de générer des tensions inutiles, des dysfonctionnements inopportuns, des griefs tant sociaux que professionnels veillez-vous à ne pas produire des ordres contradictoires, des interruptions trop fréquentes un sentiment de travail mal coordonné ? ', 0),
(144, 3, '<p>Vous attachez-vous à exclure</p><p>&nbsp; &nbsp; &nbsp; &nbsp;  - toute infantilisation des rapports ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - toute forme de paternalisme ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - tout autoritarisme gratuit ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - toute volonté d\'harcèlement moral ? <br></p><p>Faites vous en sorte de combattre ces déviances qui ne peuvent que plomber l\'atmosphère ? </p>', 0),
(145, 3, '<p>Vous attachez-vous et veillez-vous</p><p>&nbsp; - à considérer les savoirs acquis sur le tas fussent-ils limités et décalés au regard des nouvelles techniques comme des atouts ? Vous employez-vous</p><p>&nbsp; - à rénover sans cesse les appréciations professionnelles du personnel ?</p><p>&nbsp; - à valoriser le personnel notamment quand celui-ci le mérite indéniablement ? <br></p><p><br></p><p>Malgré sa question fermée, cet écran n\'appelle pas de réponse fermée complétez cette série d\'items ? </p>', 0),
(146, 3, '<p>Vous attachez-vous</p><p>- à inviter à une interdisciplinarité ? Vous accordez-vous</p><p>- de favoriser la polyvalence des individus par eux-mêmes ? Cet écran mérite amplement d\'être complété. Voyez vous d\'autres éléments susceptibles de l\'enrichir ? </p>', 0),
(147, 3, '<p>Concevez-vous au départ d\'entrevoir une corrélation entre les possibilités physiques et psychologiques de la personne à occuper le poste ? <br></p><p>Prenez-vous soin d\'adapter momentanément un poste de travail aux temps de réponse d\'un salarié face aux sollicitations d\'une machine ? <br></p><p>Par exemple, rappelez-vous à l\'encadrement qu\'un temps d\'apprentissage est toujours nécessaire et qu\'on ne saurait accorder du temps pour expliquer correctement le travail. <br></p><p>C\'est à dire admettre que le travail mal réalisé vient souvent d\'un travail mal expliqué.</p>', 0),
(148, 3, '<p>Vous sentiriez-vous sensible aux remarques suivantes <br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  \"Je ne dispose pas de temps suffisant\"</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  \" je ne dispose pas d\'informations suffisantes\"</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  \" je trouve les informations floues, imprécises, incertaines\" </p>', 0),
(149, 3, '<p>L\'intérêt dans le travail est-il crée</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - en soumettant des défis difficiles mais possibles ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - en encourageant les adaptations au travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;  - en levant les rigueurs sociales et administratives dans le cadre d\'ateliers expérimentaux ou d\'unités de recherche ?</p><p> Imaginez d\'autres prolongements à ces aspects.</p>', 0),
(150, 3, '<p>Vous sentiriez-vous sensible à l\'appréciation suivante:</p><p>\" Je n\'ai pas le sentiment que mon responsable direct semble concerné&nbsp;</p><p>&nbsp; &nbsp; &nbsp;par la situation de ses collaborateurs, &nbsp;</p><p>&nbsp; &nbsp; &nbsp;par leur moral,&nbsp;</p><p>&nbsp; &nbsp; &nbsp;par leur bien-être\"</p>', 0),
(151, 3, '<p>L\'intérêt dans le travail est-il créé</p><p>&nbsp; &nbsp; &nbsp;- par une participation des salariés à la rédaction des cahiers des charges ?</p><p>&nbsp; &nbsp; &nbsp;- par la participation d\'un technicien au choix du matériel ?</p><p>&nbsp; &nbsp; - par la sollicitation de remarques lors de la réception d\'une machine ou d\'un atelier ?</p><p>&nbsp; &nbsp; - par la participation des opérateurs à la montée en puissance des machines ?</p><p>&nbsp;Pour mémoire, ce type d\' écran n\'appelle surtout pas de réponse fermée. </p>', 0),
(152, 3, '<p>L\'intérêt dans le travail est-il créé</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - par un contrôle des productions par les producteurs ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - par une communication d\'informations ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par une information constante de l\'état d\'un projet ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par une information quant au produit fabriqué et ses applications?&nbsp;</p><p>Votre connaissance de l\'entreprise ne peut que vous donner les moyens de compléter ces quelques items. </p>', 0),
(153, 3, '<p><br></p><p>L\'intérêt dans le travail est-il créé</p><p>- en introduisant du désordre dans le travail afin de stimuler des adaptations ?</p><p>- en soumettant aux salariés la réalisation de travaux urgents pour leur rappeler leurs capacités et maintenir leur satisfaction ?&nbsp;</p><p><span style=\"font-size: 10pt;\">Verriez-vous d\'autres aspects ?</span></p>', 0),
(154, 3, '<p><br></p><p>Une affinité relationnelle des personnes autour de valeurs professionnelles partagées ou susceptibles de l\'être (conscience professionnelle, professionnalisme, curiosité professionnelle...) est-elle instituée pour créer un cadre psychologique favorable au travail ? </p>', 0),
(155, 3, '<p><br></p><p>Ces aspects relationnels sont-ils confortés en laissant aux acteurs le choix de leur service pour autant que les raisons de service le permettent ? </p>', 0),
(156, 3, '<p>L\'absence de concordance relationnelle n\'est-elle pas par nature privatrice d\'efforts volontaires, de synergies inconscientes ?&nbsp;</p><p>L\'entreprise peut-elle se passer de telles considérations psychologiques constamment présentes dans une relation sociale,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - qu\'y gagne-t-elle ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - qu\'y perd-elle ?&nbsp;</p><p>Ces relations sociales ne constituent-elles pas un domaine privilégié de la GRH ? </p>', 0),
(157, 3, '<p>Considéreriez-vous tous les temps morts comme des temps volés ?&nbsp;</p><p>Ne conviendrait-il pas de les considérer comme de possibles temps utiles , indispensables à défaut pour tous au moins pour certains salariés ?&nbsp;</p><p>Ne peut-on les analyser</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - comme des alertes après observations ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - comme des signes, des expressions ? </p>', 0),
(158, 3, '<p>Accorderiez-vous une oreille attentive aux remarques suivantes:</p><p>&nbsp; &nbsp; &nbsp; - \"les changements d\'organigramme sont mal expliquées\"</p><p>&nbsp; &nbsp; &nbsp; - \"l\'organigramme n\'est pas conforme à ce qui est vécu\"</p><p>&nbsp; &nbsp; &nbsp; - \"l\'organigramme officiel n\'est pas assez rappelé et soutenu\"?</p>', 0),
(159, 3, '<p>Accorderiez-vous une oreille attentive aux remarques suivantes:</p><p>&nbsp; &nbsp; &nbsp; &nbsp;\"les changements d\'organisation sont</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- expliqués avec retard,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- sont mal expliqués,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - sont mal mis en oeuvre\". </p>', 0),
(160, 3, '<p>Le travail est-il entrecoupé&nbsp;<span style=\"font-size: 10pt;\">&nbsp;de pauses (café, cigarette),&nbsp;</span></p><p><span style=\"font-size: 10pt;\">L\'entreverriez vous</span></p><p>- d\'intervalles récréatifs assortis de jeux vidéo ou manuels (babyfoot, jeux électroniques ) ?</p><p>&nbsp;L\'imagineriez vous entrecoupé<span style=\"font-size: 10pt;\">&nbsp;</span></p><p><span style=\"font-size: 10pt;\">&nbsp; &nbsp; &nbsp; &nbsp;-de pauses de décontraction (gymnastique active statique),</span></p><p>&nbsp; &nbsp; &nbsp; &nbsp;- d\'exercices de relaxation (yoga), de respiration ?&nbsp;</p><p>voire de toute autre forme de relaxation ou de décontraction ? </p>', 0),
(161, 3, '<p><br></p><p>Le respect scrupuleux des temps de pause dès lors qu\'ils ont été négociés ou reconnus, est-il observé ?<br></p>', 0),
(162, 3, '<p>Lors de la pause déjeuner, les activités sportives, ludiques, relaxatives sont-elles encouragées par la mise à disposition</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- d\'espaces privilégiés ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - de matériels adéquats ?</p><p><span style=\"font-size: 10pt;\">- d\'une mise à disposition des moyens d\'information de l\'entreprise ?</span></p>', 0),
(163, 3, '<p>Ces pauses sont-elles</p><p>&nbsp; &nbsp;- autogérées individuellement (appropriation) collectivement (sentiment d\'existence du groupe) ?</p><p>&nbsp; &nbsp;- individualisées (donc laissées au contrôle social) plutôt que collectives ? </p>', 0),
(164, 3, '<p><br></p><p>La plage de repas atteint-elle ou dépasse-t-elle les 30 à 40 minutes pour créer le sentiment d\'une vraie coupure et permettre une meilleure récupération psychologique ? </p>', 0),
(165, 3, '<p><br></p><p>L\'absence de pauses officielles signifie-t-elle pour autant l\'absence de temps de récupération physiologique ?&nbsp;</p><p>Les fuites ou les temps de non travail, les prétextes quelconques, les coulages, les pannes réelles ou simulées ne seraient-ils pas des réponses indirectes mais précises à cette absence de coupures ? </p>', 0),
(166, 3, '<p>Atténuer les fatigues corporelles et psychiques par des pauses ne permettrait-il pas de restaurer l\'énergie des collaborateurs ?&nbsp;</p><p>La productivité ne se trouverait-elle pas confortée</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par l\'instauration de ces pauses ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par une introduction des celles-ci ?&nbsp;</p><p>La GRH ne s\'en trouverait-elle pas améliorée ?&nbsp;</p><p>L\'entreprise n\'en serait-elle l\'ultime bénéficiaire ? </p>', 0),
(167, 3, '<p><br></p><p>L\'entreprise gagne-t-elle plus en refusant qu\'en instituant ces plages de repos dans le cadre du processus du travail et des 35 heures ? </p>', 0),
(168, 3, '<p><br></p><p>L\'absence de pauses&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;n\'affecte-t-elle pas la disponibilité du personnel ?&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;ne ternit-elle pas l\'image, l\'estime, la considération ou la sympathie ?</p><p>que les salariés peuvent attribuer à leur direction et à leur encadrement ? </p>', 0),
(169, 3, '<p><br></p><p>Le travail est-il aménagé pour palier aux conditions d\'environnement (chaleur, humidité, froid, bruit, luminosité...) lié au travail ? </p>', 0),
(170, 3, '<p>En portant une attention particulière à l\'environnement du travail, cette attention est-elle plus préventive que corrective ?</p><p>&nbsp;Cette prévention passe-t-elle</p><p>&nbsp; &nbsp;- par une étude de tout aménagement sur plan ?</p><p>&nbsp; &nbsp;- par une préparation de ces aménagements après consultation des intéressés ?</p><p>&nbsp; &nbsp;- par une étude ergonomique des lieux et des espaces de travail ? </p>', 0),
(171, 3, '<p><br></p><p>Les conditions de la réalisation du travail seraient-elles sans influence sur la qualité et le volume de travail rendu ?&nbsp;</p><p>S\'abstenir d\'attentions particulières quant à l\'environnement du travail, n\'est-ce pas affecter d\'une manière ou d\'une autre la disponibilité du salarié ? </p>', 0),
(172, 4, '<p><br></p><p>Permettez vous aux salariés d\'être créatifs</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- du fait d\'une capacité d\'écoute ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- du fait d\'une volonté d\'étude de leurs propositions ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- du fait d\'un volontarisme conscient ? </p>', 0),
(173, 3, '<p>Le travail est-il aménagé pour prévenir</p><p>&nbsp; &nbsp; &nbsp; - les efforts physiques ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- la pénibilité des gestes et des postures ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- la fatigue du personnel ?&nbsp;</p><p>Une attention portée au personnel est aussi profitable à l\'entreprise par la réduction de ses coûts cachés ? </p>', 0),
(174, 3, '<p><br></p><p>La prévention des risques liés aux postures et à la fatigue est-elle traitée à la source via une formation, un matériel ergonomique adéquat, une réflexion en amont, des essais, des avis des utilisateurs.  . ? </p>', 0),
(175, 3, '<p><br></p><p>Prévenez vous les risques physiques par la diffusion</p><p>- d\'informations audio-visuelles (diapos, montage vidéo, etc...) projetées à proximité des ateliers, dans les couloirs de circulation obligés et autres lieux de passage (cafétaria, etc...) ?</p><p>- d\'informations affichées de façon permanente et efficace ? </p>', 0),
(176, 3, '<p><br></p><p>Cette prévention des accidents physiques donne-t-elle lieu</p><p>&nbsp; &nbsp;- à une formation à la manipulation des objets lourds et encombrants (industrie, transports) ?</p><p>&nbsp; - à la mise à disposition d\'un matériel de transport, plateau élévateur ?&nbsp;</p><p>Une veille ergonomique est-elle instillée, instaurée dans l\'entreprise ? </p>', 0),
(177, 3, '<p>Cette prévention des accidents physiques passe-t-elle par la prise en considération&nbsp;<span style=\"font-size: 10pt;\">des propositions et des études réalisées par le CHSCT ?&nbsp;</span></p><p>Consentez-vous la prévention des accidents physiques par la prise en considération</p><p>&nbsp; - des propositions émanant des salariés ou</p><p>&nbsp; - des études connues de l\'ANACT (Agence Nationale pour l\'Amélioration des Conditions de Travail) ou autres centres de recherche ? </p>', 0),
(178, 3, '<p>Les accidents du travail par leur tribut sur la gestion de l\'entreprise</p><p>&nbsp; - du fait de la désorganisation du travail qu\'ils imposent,</p><p>&nbsp; - du fait des coûts directs ( remplacement, intérim, augmentation du taux de cotisation de sécurité sociale...) qu\'ils occasionnent,</p><p>&nbsp; - du fait des frais indirects (perte de qualité, coût d\'efficacité du personnel intérimaire, etc...)&nbsp;<span style=\"font-size: 10pt;\">qu\'ils entraînent,&nbsp;</span></p><p><span style=\"font-size: 10pt;\">ne doivent-ils pas être pris en compte de façon constante et efficace ?&nbsp;</span></p><p>La prévention des accidents du travail ne mérite-t-elle pas d\'être considérée comme un facteur d\'économies substantielles ? </p>', 0),
(179, 3, '<p><br></p><p>Veillez-vous à aménager les postes de travail afin de diminuer les agressions physiques dues aux matériels ou aux machines( secousses, vibrations, rayonnements divers, aux émanations, aux bruits, à l\'éclairage, aux gestes répétitifs, etc...) ? </p>', 0),
(180, 3, '<p>Cette question n\'appelant que les solutions permises à ce jour, l\'action entreprise prend-elle le problème à sa source, eu égard aux moyens disponibles pour l\'heure ?&nbsp;</p><p>L\'encadrement est-il convié à suivre de près et de façon permanente les progrès réalisés sur ces matériels ?&nbsp;</p><p>Est-il offert aux responsables de suivre ces questions par des abonnements, des congrès ou des conférences ? </p>', 0),
(181, 3, '<p><br></p><p>Seriez-vous sensible aux remarques suivantes les changements de responsables directs&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;sont mal expliqués ?&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;sont trop fréquents ? </p>', 0),
(182, 3, '<p><br></p><p>La gestion des Hommes gagne-t-elle à négliger ces considérations physiques ?&nbsp;</p><p>La motivation peut-elle naître d\'un tel terreau ?&nbsp;</p><p>Cette suite d\'interrogations vous incline-t-elle à modifier votre approche dans ce domaine ? </p>', 0),
(183, 3, '<p><br></p><p>Le travail est-il aménagé pour réduire les agressions dues aux particules (poussières minérales telles la silice, source de silicose, organiques telles l\'amiante, d\'oxyde de fer) et aux poussières en général, sources d\'infections respiratoires ? </p>', 0),
(184, 3, '<p><br></p><p>Bien que les conditions de travail se soient améliorées, des mesures de protection sont-elles mises en place pour prévenir toute atteinte reconnue ?&nbsp;</p><p>Des mesures sont-elles instaurées pour prévenir les dangers issus&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;de nouveaux produits ?&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; de nouvelles techniques de travail ? </p>', 0),
(185, 3, '<p>Autant par éthique que par considération sociale, cet aménagement peut-il ne pas exister ?&nbsp;</p><p>Aménager le travail pour prévenir des affections encore aujourd\'hui préoccupantes, n\'est-ce pas capitaliser la considération du personnel qui reste l\'un des moteurs même de l\'entreprise ?&nbsp;</p><p>Cela ne contribue-t-il pas à privilégier autant la composante humaine que matérielle ? </p>', 0),
(186, 3, '<p><br></p><p>L\'entreprise n\'a-t-elle pas avantage à prévenir les conséquences fâcheuses liées aux pollutions minérales ou organiques tant comme investissement matériel (coûts directs ou indirects) que psychologiques (coûts social et humain) ? </p>', 0),
(187, 3, '<p>La prévention des agressions chimiques (intoxications) est-elle assurée</p><p>- individuellement (explosions, brûlures, intoxication, etc...) dans l\'entreprise par une formation pratique à l\'emploi d\'extincteurs, de couvertures, de douches, à la ranimation des blessés... ?&nbsp;</p><p>La prévention des agression chimiques (intoxications) est-elle traitée&nbsp;<span style=\"font-size: 10pt;\">collectivement par des exercices ( simulations d\'évacuation... notamment incendie) ?</span></p>', 0),
(188, 3, '<p>Outre les situations évoquées, une information est-elle développée sur les risques, les produits, les particularités des produits employés ?&nbsp;</p><p>Les études réalisées par le CHSCT, les propositions émanant de salariés (cadres, techniciens ou autres) ou non (pompiers), les études connues de l\'ANACT et autres centres sont-elles prises en compte pour l\'élaboration d\'une politique de prévention face à ces risques ? </p>', 0),
(189, 3, '<p><br></p><p><br></p><p><span style=\"font-size: 10pt;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Ce choix est laissé à votre appréciation.</span></p>', 0),
(190, 3, '<p>La prévention des fatigues visuelles dues à l\'éblouissement, à l\'éclairage artificiel, etc... est-elle traitée par</p><p>&nbsp; - une ergonomie des matériels ( respect des normes européennes ) ?</p><p>&nbsp; &nbsp; &nbsp; - une meilleure disposition des matériels ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- une adaptation des éclairages artificiels ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- une écoute des doléances et de toutes les remarques du personnel ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - autres ? </p>', 0),
(191, 3, '<p><br></p><p>Tout en vous attachant à prévenir les fatigues visuelles, votre action est-elle avant tout davantage préventive que correctrice ?&nbsp;</p><p>Les études réalisées par le CHSCT et ses propositions, les travaux connus de l\'ANACT et autres centres sont-ils prises en compte pour l\'élaboration de meilleures conditions encore mal connues ? </p>', 0),
(192, 3, '<p><br></p><p>Pensez-vous qu\'il soit possible d\'améliorer votre situation ?&nbsp;</p><p>ou la pensez-vous comme non perfectible au regard des investissements que vous avez déjà consentis ?&nbsp;</p><p>N\'y aurait-il pas d\'autres sources de prévention moins ou non onéreux à mettre en place ? </p>', 0),
(193, 3, '<p>Les fatigues auditives sont-elles prévenues par l\'absorption des sons</p><p>&nbsp; &nbsp; - sur le plan de travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par des filtres acoustiques ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par des protections individuelles ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - autres ? </p>', 0),
(194, 3, '<p><br></p><p>Tout dispositif permettant de réduire les bruits est-il de fait étudié</p><p>&nbsp; &nbsp; &nbsp; - lors de la conception des ateliers, etc... ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- lors de l\'étude du poste de travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- lors de l\'achat du matériel ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- etc... ? </p>', 0),
(195, 3, '<p><br></p><p>A machine de qualité égale, le choix est-il porté sur la machine la moins bruyante même à prix légèrement supérieur ?&nbsp;</p><p>Les analyses de rapport qualité/prix prennent-elles systématiquement en considération le bruit ?&nbsp;</p><p>Le bruit source d\'une grande fatigue est traité à parité égale des autres aspects des conditions de travail plus visibles ? </p>', 0),
(196, 3, '<p>Il vous revient d\'arrêter des choix. Cependant, ces choix ne sont pas sans incidence</p><p>&nbsp; &nbsp;- sur la productivité,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - sur l\'atmosphère de travail,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- sur les relations sociales,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- sur les perceptions de l\'entreprise et donc son image,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- sur la vôtre en définitif.</p>', 0),
(197, 3, '<p><br></p><p>Les fatigues psychologiques (dues aux individualisations maximalisées) font-elles l\'objet de mesures préventives afin de limiter les dysfonctionnements (absentéisme, turn over et autres) en concédant aux aménagements proposés par le médecin du travail, les psychologues du travail ou les ergonomes ? </p>', 0),
(198, 3, '<p>Cette prévention des fatigues psychologiques passe-t-elle par une réponse au besoin de communication des hommes afin de lever ou d\'atténuer les contraintes vécues par certains travailleurs ?&nbsp;</p><p>La communication est-elle assurée</p><p>&nbsp; - par la faculté d\'échanger directement et ponctuellement des propos en cours de journée,</p><p>&nbsp; - par l\'entremise d\'intermédiaires (interphones, d\'oreillettes, de téléphones) ou</p><p>&nbsp; - par l\'intermédiaire de moyens visuels ? </p>', 0),
(199, 3, '<p><br></p><p>Les acquis de la sociologie des organisations, de la psychologie etc... sont-ils introduits et appliqués dans la gestion des RH ? </p>', 0),
(200, 3, '<p>L\'absence de mesures préventives en faveur des fatigues psychologiques, ne crée-t-elle pas une situation contraignante</p><p>&nbsp; - gênant l\'action dans le travail,</p><p>&nbsp; - générant une forme d\'absentéisme actif ou passif assez difficilement mesurable ?&nbsp;</p><p>Si vous le pensez, quelles dispositions metteriez vous en application pour la pallier ? </p>', 0),
(201, 3, '<p><br></p><p><br></p><p>Les dispositions en faveur des conditions de travail intègrent-elles une vigilance quant à la propreté des locaux et des lieux de circulation ? </p>', 0),
(202, 3, '<p>Pour assurer cette propreté, les salariés sont-ils appelés à entretenir leurs locaux ?&nbsp;</p><p>Sont-ils sollicités</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à respecter les règles élémentaires de propreté ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à faciliter le travail du personnel d\'entretien ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à faire perdurer leur travail ? </p>', 0);
INSERT INTO `question_qst` (`qst_id`, `cou_id`, `qst_question`, `qst_first`) VALUES
(203, 3, '<p><br></p><p>Si cet élément ne figurait pas au rang de vos préoccupations, ne serait-il pas souhaitable de l\'intégrer ?&nbsp;</p><p>N\'est-il pas symptomatique de constater que c\'est l\'un des aspects auquel les investisseurs japonais remédient immédiatement dès lors qu\'il fait défaut ? </p>', 0),
(204, 3, '<p>Les causes des incidents et accidents de travail sont-elles recensées, analysées</p><p>&nbsp; - pour produire un arbre des causes aussi complet que possible,</p><p>&nbsp; - pour recueillir toutes les idées disponibles et toutes les propositions,</p><p>&nbsp; - pour développer une sécurité des installations moins faillible en contribuant ainsi à l\'intégrité de vos collaborateurs permanents ou passagers ? </p>', 0),
(205, 3, '<div><br></div>L\'analyse des causes des accidents du travail convie-t-elle à<p></p><p>&nbsp; - à de nouveaux aménagements des lieux de travail (lors d\'un changement de matériel par exemple) ?</p><p>&nbsp; - à une organisation différente du travail, financièrement contenue et tout aussi productive ? </p>', 0),
(206, 3, '<p><br></p><p>S\'abstenir d\'analyser les causes des incidents et accidents du travail, ne revient-il pas à se priver de la bonne connaissance des salariés de leur cadre de travail, de leurs aptitudes à prévenir à peu de frais les risques inhérents au travail ? </p>', 0),
(207, 3, '<p><br></p><p>S\'abstenir d\'analyser les causes des incidents et accidents du travail, n\'est-ce pas aussi</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - entretenir l\'absentéisme ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- se refuser de rechercher la considération du personnel ? </p>', 0),
(208, 3, '<p>Pour répondre aux conditions autant physiologiques que psychologiques, un aménagement du temps de travail des salariés prenant en considération</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - leur physiologie (matin et soir) ou</p><p>&nbsp; &nbsp; &nbsp;- leurs convenances personnelles (charges familiales ou sociales)</p><p>ne serait-il pas à entrevoir sous réserve de satisfaire</p><p>&nbsp; &nbsp; &nbsp;- les temps de travail impartis,</p><p>&nbsp; &nbsp; &nbsp;- les heures d\'ouverture de l\'entreprise,</p><p>- la réalisation des objectifs assignés ? </p>', 0),
(209, 3, '<p><br></p><p>Est-il pleinement tiré profit de cet aménagement par l\'une et l\'autre des parties ?&nbsp;</p><p>Une plus grande disponibilité des acteurs est-elle l\'un des principaux avantages tirés par l\'entreprise ?</p><p>&nbsp;Ces aménagements du temps de travail liés à des considérations physiologiques ou personnelles n\'offriraient-ils pas d\'autres avantages que ceux énoncés ? </p>', 0),
(210, 3, '<p>Cet aménagement ne permet-il pas</p><p>&nbsp; - un meilleur emploi des machines et des matériels coûteux (ordinateurs très performants et autres matériels informatiques pointus) ?</p><p>&nbsp; - un meilleur amortissement du fait d\'une utilisation plus intense des matériels ?</p><p>&nbsp; - des plages de travail ou d\'ouverture de l\'entreprise étendues ou revues ? </p>', 0),
(211, 3, '<p><br></p><p>Le sentiment d\'une plus grande liberté du temps de travail et donc du temps social est-il accompagné d\'autres dispositions permettant d\'entretenir la satisfaction des salariés ?&nbsp;</p><p>Ne pensez-vous pas que des salariés satisfaits de leur environnement ( et reconnaissants de ce qui leur est donné ) ne puissent pas participer plus aisément à vos demandes ? </p>', 0),
(212, 3, '<p><br></p><p><br></p><p>Ces propositions ne méritent-elles pas d\'être au moins soumises à défaut d\'être retenues ? </p>', 0),
(213, 3, '<p><br></p><p>Si l\'organisation du temps de travail reste une prérogative entière de l\'employeur, celui ci n\'a-t-il pas à chercher</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- une meilleure disponibilité de ses collaborateurs ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- une meilleure utilisation des matériels ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- une meilleure rentabilité des investissements fixes ? </p>', 0),
(214, 3, '<p><br></p><p>Moins bien reconnus que les administratifs, les producteurs et les exploitants sont-ils valorisés aussi souvent que possible ? </p>', 0),
(215, 3, '<p>Pour induire quelques signes de valorisation, est-il sollicité aux administratifs de se déplacer plutôt que de requérir les producteurs à le faire ?&nbsp;</p><p>L\'expression est-elle régulièrement offerte aux producteurs</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- régulièrement ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- souvent ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- peu souvent ?&nbsp;</p><p>Est-il porté une particulière attention aux producteurs ? </p>', 0),
(216, 3, '<p>L\'entreprise peut-elle omettre que la production reste l\'oeuvre des exploitants et des producteurs ? L\'entreprise n\'a-t-elle pas à rappeler que la production de services et de biens reste sa principale source de profit ? L\'entreprise peut-elle oublier ses producteurs ? </p>', 0),
(217, 3, '<p><br></p><p>Vous attachez-vous à restituer le mérite de la production à ses réalisateurs en les valorisant aussi souvent que possible ?&nbsp;</p><p>Veillez-vous à réparer les frustrations possibles, en rappelant l\'importance des producteurs et des exploitants ? </p>', 0),
(218, 3, '<p><br></p><p>Pour illustration Les tenues de travail sont-elles aussi fonctionnelles que valorisantes ?&nbsp;</p><p>Les locaux de production sont-ils entretenus régulièrement malgré les raisons qui peuvent être invoquées ? </p>', 0),
(219, 3, '<p><br></p><p><br></p><p>L\'attention pour le personnel n\'est-elle pas aussi à manifester par des éléments de ce type ? </p>', 0),
(220, 3, '<p><br></p><p>Ecarter ces aspects symboliques et matériels relativement peu coûteux, ne prive-t-il pas l\'entreprise d\'une précieuse collaboration ?&nbsp;</p><p>L\'appréciation de la valeur des choses ne relève-t-elle pas en dernier ressort plus des attributaires que de l\'attributeur ? </p>', 0),
(221, 3, '<p>Vous venez de terminer la consultation du module \" Aspects immédiats du travail\" inclus dans le thème \"Conditions de travail\".&nbsp;</p><p>C \'est là un thème qui ne demande qu\'à être complété. Votre aide nous serait extrêmement précieuse. Nous répercuterions vos contributions dans des mises à jour.D\'avance merci de votre collaboration.&nbsp;</p><p>GRH-dialogue vous souhaite d\'avoir passé et de poursuivre une agréable et fructueuse consultation.</p>', 0),
(223, 4, '<p>Le travail est-il rendu aussi agréable que possible<span style=\"font-size: 10pt;\">&nbsp;en levant les obstacles à la bonne réalisation du travail ?&nbsp;</span></p><p>Quelles dispositions en faveur de cela ces dernières semaines avez vous prise ? vous inclineriez vous à prendre ?&nbsp;</p><p>Le travail est-il rendu aussi agréable que possible<span style=\"font-size: 10pt;\">&nbsp;en variant ou en spécialisant les travaux selon les individus ?&nbsp;</span></p><p><span style=\"font-size: 10pt;\">Cette approche sociale est-elle courante, encouragée ?</span></p>', 0),
(224, 4, '<p>La satisfaction des personnes est-elle créée</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par un retour des propos de satisfaction des clients ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par une présentation des réalisations à l\'ensemble du personnel ?&nbsp;</p><p>Est-il d\'autres initiatives prises pour rendre compte sans attendre qu\'il vous soit rendu compte ?&nbsp;</p><p>La satisfaction des individus est-elle réalisée</p><p>&nbsp; &nbsp; &nbsp; - par un compte-rendu régulier des résultats commerciaux obtenus ?</p><p>&nbsp; &nbsp; &nbsp; - par une diffusion des chiffres à l\'exportation, etc... ? </p>', 0),
(225, 4, '<p>Le contenu du travail est-il amélioré par</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - la polyvalence effective des salariés ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - la rotation du personnel ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - un enrichissement effectif des tâches ?&nbsp;</p><p>Ce travail est régulièrement fait sans attendre la lassitude; et est-il précédé d\'une large information sur le bénéfice que peut en tirer l\'entreprise ou l\'obligation qui lui est faite de procéder ainsi; mais aussi le bénéfice que peut en tirer le salarié qui augmente ses chances de retrouver un travail en cas de défaillance ? </p>', 0),
(226, 4, '<p>Les mesures suivantes&nbsp;<span style=\"font-size: 10pt;\">polyvalence, rotation des tâches, leur enrichissement&nbsp;</span><span style=\"font-size: 10pt;\">sont-elles complétées</span></p><p>&nbsp; &nbsp;- par un renouvellement du travail aussi souhaitable que possible ?</p><p>&nbsp; &nbsp;- par un pool d\'activités valorisant le travailleur ?</p><p>&nbsp; &nbsp;- par l\'attribution de tâches ou de missions exceptionnelles ?&nbsp;</p><p>Peut-on imaginer que certains salariés ne puissent exercer des travaux , si ceux-ci ne leur ont jamais été proposés ? </p>', 0),
(227, 4, '<p>Complétez vous les mesures suivantes -<span style=\"font-size: 10pt;\">&nbsp;polyvalence, rotation des tâches, enrichissement-</span></p><p>&nbsp;- par l\'attribution de tâches en rapport avec les passe-temps favoris, les formations initiales, les capacités particulières des uns et des autres ?</p><p>&nbsp;- par l\'exercice d\'un rôle de conseiller ou de consultant technique ?&nbsp;</p><p>Vous donnez-vous de donner la conduite d\'une mission au plus qualifié fût-il le moins élevé dans la hiérarchie en sachant néanmoins cadrer la délégation de compétence ? </p>', 0),
(228, 4, '<p><br></p><p>Les mesures suivantes&nbsp;<span style=\"font-size: 10pt;\">- polyvalence, rotation des tâches,enrichissement&nbsp;</span><span style=\"font-size: 10pt;\">- sont-elles associées à une modulation de la rémunération ?&nbsp;</span></p><p><span style=\"font-size: 10pt;\">Vous donnez-vous d\'accompagner les conséquences de vos initiatives ou les innovations que vous pourriez mettre en place ?</span></p>', 0),
(229, 4, '<p>Les mesures suivantes&nbsp;<span style=\"font-size: 10pt;\">- polyvalence, rotation des tâches, enrichissement&nbsp;</span><span style=\"font-size: 10pt;\">- sont-elles complétées par une alternance</span></p><p>&nbsp; - des tâches de surveillance et d\'exploitation,</p><p>&nbsp; - des tâches d\'exploitation et de maintenance ?&nbsp;</p><p>Les mesures suivantes&nbsp;<span style=\"font-size: 10pt;\">- polyvalence, rotation des tâches, enrichissement&nbsp;</span><span style=\"font-size: 10pt;\">- sont-elles complétées par une alternance</span></p><p>&nbsp; - des tâches de production et de Service Après Vente,</p><p>&nbsp; - des tâches administratives et manuelles (pour autant que cela soit possible) ? </p>', 0),
(230, 4, '<p>Ne craignez-vous pas que l\'absence d\'attentions particulières dans le contenu du travail n\'accroissent les pesanteurs psychologiques du travail ?&nbsp;</p><p>Cette absence n\'est-elle pas préjudiciable</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à la production ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à la qualité des produits ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à la qualité de vie dans l\'entreprise ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- au présentéisme des salariés ? </p>', 0),
(231, 4, '<p>Le désintérêt quant au contenu du travail n\'accroît-il pas son coût psychologique et, de ce fait, la fatigue, la lassitude, la démotivation du salarié ?&nbsp;</p><p>Cette pesanteur psychologique n\'est-elle pas dès lors un coût caché du travail, une perte de participation, d\'adhésion, d\'implication&nbsp;<span style=\"font-size: 10pt;\">pour l\'entreprise ?&nbsp;</span></p><p><span style=\"font-size: 10pt;\">Peut-on dès lors se détourner de ces aspects ?&nbsp;</span></p><p><span style=\"font-size: 10pt;\">Ces aspects ne viennent-ils contrecarrer la notion de Direction ou de Gestion des Ressources Humaines ?</span></p>', 0),
(232, 4, '<p><br></p><p>Pour atténuer les contraintes psychologiques du travail, les salariés sont-ils partiellement (semi-autonomes) ou totalement ( autonomes) auteurs dans leur organisation (dès lors que les objectifs ont été explicités et compris pour être partagés) ? </p>', 0),
(233, 4, '<p>Cette gestion de l\'organisation du travail par les salariés est-elle consacrée</p><p>&nbsp; &nbsp;- par la reconnaissance de fait des aménagements apportés à l\'organisation officielle ?</p><p>&nbsp; &nbsp;- par l\'officialisation des pratiques qui réussissent, fussent-elles peu conformes à la conception définie par l\'entreprise ?&nbsp;</p><p>Est-il d\'autres aspects contribuant à une gestion du travail plus satisfaisant pour tous ? </p>', 0),
(234, 4, '<p>Concevez vous d\'affecter cette appropriation de l\'organisation du travail par les salariés</p><p>&nbsp; &nbsp; - par la restitution partielle des gains de productivité réalisés sous la forme de primes, d\'intéressement ou autres ?</p><p>&nbsp; &nbsp;- par la responsabilité à former les autres salariés aux applications auxquelles ils ont ou auraient collaboré ? </p>', 0),
(235, 4, '<p><br></p><p>Cette appropriation de l\'organisation du travail par les salariés est-elle consacrée</p><p>&nbsp; &nbsp; - par l\'espérance de mener un projet les impliquant ?</p><p>&nbsp; &nbsp; - par leur participation même symbolique à l\'organisation du travail ?&nbsp;</p><p>Cette appropriation de l\'organisation du travail par les salariés est-elle consacrée</p><p>&nbsp; &nbsp;- par la faculté de participer à la révision de l\'organisation du travail ? </p>', 0),
(236, 4, '<p>Cette appropriation de l\'organisation du travail par les salariés donne-t-elle lieu</p><p>&nbsp; &nbsp; - à une libre répartition du travail entre les membres du groupe ?</p><p>&nbsp; &nbsp; - à une autonomie de gestion du travail sur une période donnée ?&nbsp;</p><p>Une organisation par objectifs négociés peut-elle s\'envisager ?&nbsp;</p><p>Ne peut-on partir du postulat que les salariés sont attachés à leur entreprise.&nbsp;</p><p>La garantie sociale qu\'elle leur apporte, ne les incline-t-elle pas à ne pas mettre en danger cette sécurité ? </p>', 0),
(237, 4, '<p>Cette appropriation de l\'organisation du travail par les salariés donne-t-elle lieu</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - à une autogestion des temps de travail quotidiens ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - à une autogestion des congés ?&nbsp;</p><p>Cette appropriation de l\'organisation du travail par les salariés permet-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - la gestion d\'un budget fonctionnel ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - l\'autogestion des relations ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - l\'autocontrôle des produits ?</p><p>&nbsp;Il vous est sans doute aisé de compléter cet écran.</p>', 0),
(238, 4, '<p><br></p><p>Ecarter la participation des salariés de l\'organisation du travail par le biais d\'une appropriation ou d\'une gestion personnalisée, cela ne conduit-il pas à gaspiller un savoir-faire et un potentiel d\'appréciation reposant sur une économie de gestes pour une efficacité reconnue ?</p><p>&nbsp;Les gains de productivité ne sont-ils aussi à rechercher dans ce registre ? </p>', 0),
(239, 4, '<p><br></p><p>Pour atténuer les contraintes psychologiques du travail, veillez vous à faire que les salariés soient partiellement auteurs de leurs conditions de travail ?&nbsp;</p><p>Peut-il y avoir implication, adhésion sans participation ? </p>', 0),
(240, 4, '<p>Cette gestion des conditions de travail par les salariés passe-t-elle</p><p>&nbsp; &nbsp; &nbsp;- par la présentation de situations aménageables ?</p><p>&nbsp; &nbsp; &nbsp;- par l\'octroi d\'éléments matériels et immatériels autorisant cette adaptation ?</p><p>&nbsp;Vous donnez vous d\'ouvrir à la discussion certains aspects du travail ? </p>', 0),
(241, 4, '<p>Cette gestion des conditions de travail par les salariés passe-t-elle</p><p>&nbsp; &nbsp; &nbsp; - par leur participation aux projets de rénovation des techniques ?</p><p>&nbsp; &nbsp; &nbsp; - par leur participation aux projets de rénovation des lieux de travail de l\'entreprise ?&nbsp;</p><p>Ne vous revient-il pas de participer à ce projet en levant toutes les pesanteurs qu\'impose la peur permanente du supérieur ? </p>', 0),
(242, 4, '<p>L\'absence de délégation d\'une part des conditions de travail n\'est-elle pas préjudiciable à l\'échange (donnant-donnant) ?&nbsp;</p><p>Un savoir-faire potentiel, un potentiel d\'économie d\'énergie ne sont-ils pas gaspillés ?&nbsp;</p><p>Le coût d\'une absence de délégation peut-il être négligé ? </p>', 0),
(243, 4, '<p>Dans le cadre du contenu du travail, le sentiment d\'être son propre entrepreneur est-il cultivé</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par l\'octroi d\'une autonomie d\'appréciation, de décision ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par l\'octroi de responsabilités ?&nbsp;</p><p>Dans le cadre du contenu du travail, le sentiment d\'être son propre entrepreneur est-il instillé</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par une sanction liée aux résultats (intéressement) ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par une définition des initiatives ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par une maîtrise des prestations ? </p>', 0),
(244, 4, '<p>Le sentiment d\'être son propre entrepreneur est-il associé</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- à une individualisation exacerbée ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à une grande permissivité d\'idées et d\'initiatives ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à un pari personnel ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- autres ? </p>', 0),
(245, 4, '<p><br></p><p>Toutefois, ces initiatives sont-elles tempérées</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par un contrôle a posteriori des initiatives entreprises ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par un contrôle a priori des initiatives financières ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par un contrôle de l\'investissement physique de la personne ? </p>', 0),
(246, 4, '<p><br></p><p>Cette faculté serait-elle refusée malgré l\'existence d\'un contrôle financier, temporel ou physique des individus ?&nbsp;</p><p>Cette faculté serait-elle refusée aux plus ambitieux, aux plus volontaires ?&nbsp;</p><p>Refuser le sentiment d\'être son propre entrepreneur ne prive-t-il pas les salariés d\'une ambition personnelle et de la satisfaction qu\'elle peut procurer et au delà ? </p>', 0),
(247, 4, '<p>La GRH s\'emploie-t-elle à utiliser les savoirs potentiels</p><p>&nbsp; &nbsp; - en cultivant le sens de l\'analyse de certains ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- en recherchant le sens critique des moins conformistes ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - en usant des qualités communicatives et relationnelles des autres ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - en sollicitant l\'esprit d\'ingéniosité des plus créatifs ? </p>', 0),
(248, 4, '<p><span style=\"font-size: 10pt;\">L\'utilisation des savoirs potentiels des Ressources de chacun est-elle réalisée</span></p><p>&nbsp; - en recherchant les centres d\'intérêts professionnels et personnels de chacun ?</p><p>&nbsp; &nbsp;- en reconnaissant des qualités manuelles, techniques ou artistiques ?</p><p>&nbsp; &nbsp;- en observant ?&nbsp;</p><p>Ces qualités sont-elles concrétisées en les employant éventuellement et occasionnellement par une sollicitation informelle ? </p>', 0),
(249, 4, '<p style=\"font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;\">En omettant toutes les potentialités des hommes, n\'est-il pas délaissé par négligence</p><p style=\"font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;\">&nbsp; &nbsp; &nbsp;- un savoir-faire ?</p><p style=\"font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;\">&nbsp; &nbsp; &nbsp;- une satisfaction des hommes à utiliser ce qui leur est toujours refusé ?</p><p style=\"font-family: Arial, Verdana; font-size: 10pt; font-style: normal; font-variant: normal; font-weight: normal; line-height: normal;\">&nbsp;Seriez-vous enclin à encourager l\'expression de ce savoir faire, à générer de nouveaux modes de gestion du travail ?&nbsp;</p><p><font face=\"Arial, Verdana\"><span style=\"font-size: 10pt;\">En omettant toutes les potentialités des hommes, n\'est-il pas délaissé, négligé&nbsp;une part de&nbsp;</span><span style=\"font-size: 13.3333330154419px;\">compétitivité</span><span style=\"font-size: 10pt;\">&nbsp;par l\'absence d\'intégration du plaisir, de considérations annexes du travail ?</span></font></p>', 0),
(250, 4, '<p>Rechercher les capacités occultées, les passions de ses collaborateurs pour les solliciter informellement et ponctuellement autant par flatterie que pour reconnaître leurs qualités cachées, n\'est-ce pas là une préoccupation du gestionnaire des Ressources Humaines ?&nbsp;</p><p>Lui trouveriez / vous trouveriez-vous d\'autres motifs de recherche ? </p>', 0),
(251, 4, '<p><br></p><p>Les échecs sont-ils reconnus ?&nbsp;</p><p>En est-il tiré des leçons ?&nbsp;</p><p>Ne faut-il pas s\'attacher aux aspects les plus désagréables plus qu\'aux aspects sans problèmes ? </p>', 0),
(252, 4, '<p><br></p><p><br></p><p>Est-il donné aux échecs une part de publicité autant pour les reconnaître et les assumer que pour manifester la compréhension de leurs raisons ? </p>', 0),
(253, 4, '<p><br></p><p>Un échec n\'est-il pas utile à plus d\'un titre ?&nbsp;</p><p>Cacher ou refuser un échec grandit-il le ou les responsables auprès du personnel ? </p>', 0),
(254, 4, '<p>Face à l\'individualisation croissante, l\'organisation du travail est-elle ordonnée collectivement pour atténuer les effets d\'isolement</p><p>&nbsp; &nbsp; &nbsp; - en resituant le travail de chacun dans l\'effort de tous ?</p><p>&nbsp; &nbsp; &nbsp; - en resituant l\'effort du groupe dans celui de l\'entreprise ?&nbsp;</p><p>&nbsp; &nbsp; &nbsp; - en les restituant ?&nbsp;</p><p>Veillez vous à atténuer des effets parfois obligatoires des nouvelles modalités du travail ?&nbsp;</p><p>Face à l\'individualisation croissante, l\'organisation du travail est-elle ordonnée collectivement pour atténuer les effets d\'isolement</p><p>&nbsp; &nbsp; &nbsp; - en diffusant la contribution de chaque unité et service ?</p><p>&nbsp; &nbsp; &nbsp; - en créant une communication plus facile entre producteurs et administratifs, entre commerciaux et techniciens ? </p>', 0),
(255, 4, '<p>L\'attention prêtée au collectif est-elle complétée par une conciliation de l\'individuel et du collectif via&nbsp;<span style=\"font-size: 10pt;\">- de petits aménagements quotidiens ( ex: postes, bureaux se regardant tout en préservant une certaine intimité )</span><span style=\"font-size: 10pt;\">?</span></p><p><span style=\"font-size: 10pt;\">&nbsp;Par quels autres aspects pourriez-vous compléter cette interrogation ?&nbsp;</span></p><p><span style=\"font-size: 10pt;\">L\'attention prêtée au collectif est-elle complétée par une conciliation de l\'individuel et du collectif via&nbsp;</span><span style=\"font-size: 10pt;\">- davantage de convialité tout en laissant des échappatoires psychologiques (ex: poste de travail isolé, faculté de permutations possibles et temporaires) ?</span></p>', 0),
(256, 4, '<p><br></p><p>L\'attention prêtée au collectif aménage-t-elle une individualisation autour d\'une organisation cellulaire, petits groupes alliant la cohésion à l\'effet de groupe, la solidarité à l\'efficacité ? </p>', 0),
(257, 4, '<p><br></p><p>L\'attention prêtée au collectif vise-t-elle</p><p>- à limiter les stress ( par l\'absence d\'ordres contradictoires, ou l\'abandon d\'ordre sans explications par exemple ) ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à maintenir l\'habitude du travail collectif ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à créer des formes de contrôle social gérées par le personnel ? </p>', 0),
(258, 4, '<p>Veillez vous à cadencer le travail pour qu\'il n\'épuise pas les producteurs dans le temps ?&nbsp;</p><p>Vous attachez vous à mesurer ou estimer objectivement les demandes, les temps de travail supposés pour les réaliser, la concentration imposée... ?&nbsp;</p><p>Si oui, Vous assurez-vous à cette fin des temps de récupération * physique ? * psychologique ?&nbsp;</p><p>Seriez vous enclin à penser que des temps de récupération et productivité vont de pair ? </p>', 0),
(259, 4, '<p><br></p><p>L\'individualisation peut-elle être la seule valeur que l\'entreprise ait à s\'attacher, à préconiser ?&nbsp;</p><p>Une somme d\'individualismes ou d\'individualités forge-t-elle l\'équipe dont l\'entreprise a besoin ?&nbsp;</p><p>La somme des parties est-elle forcément plus forte que le produit d\'un groupe visant les mêmes objectifs ? </p>', 0),
(260, 4, '<p><br></p><p>Le travail est-il assorti</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- (de) d\'une pluridisciplinarité ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- (d\') d\'une interdisciplinarité ? </p>', 0),
(261, 4, '<p><br></p><p>Poursuivez patiemment votre dialogue avec GRH-dialogue.&nbsp;</p><p>Ne comptez ni ne mesurez votre effort.</p><p>La récompense sera parfois tardive, mais elle sera présente. </p>', 0),
(262, 4, '<p>Le travail en équipe ne gagne-t-il pas à être compris comme une structure hétérogène</p><p>&nbsp; &nbsp; - dans les formations,</p><p>&nbsp; &nbsp; - dans les parcours professionnels, etc...pour intégrer des composantes nouvelles aux fabrications en cours ? &nbsp;</p><p>Cette approche pluridisciplinaire n\'est-elle pas aussi</p><p>&nbsp; &nbsp;- un moyen de former ?</p><p>&nbsp; &nbsp;- l\'outil d\'une meilleure communication, etc... ? </p>', 0),
(263, 4, '<p>Les pesanteurs psychologiques sont-elles réduites</p><p>&nbsp; &nbsp; - par des contraintes minimalisées pour les personnes autonomes ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par l\'élimination d\'inutiles rapports d\'autorité ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par une autonomie accrue ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par la réduction de la surveillance ?&nbsp;</p><p>Les pesanteurs psychologiques sont-elles réduites</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par l\'absence du soupçon comme règle de conduite ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par l\'allègement de contrôles a priori trop pointilleux... ? </p>', 0),
(264, 4, '<p>Les pesanteurs psychologiques sont-elles réduites (entre autres)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par la limitation et l\'aménagement du stress ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par l’aménagement ou la réalisation d\'un horaire libre ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par la gestion d\'un crédit limité d\'heures ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par un crédit de confiance ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par des possibilités de dialogue ? </p>', 0),
(265, 4, '<p><br></p><p>L\'allègement des pesanteurs psychologiques est-il obtenu</p><p>&nbsp; &nbsp;- en extériorisant les rivalités sur un facteur extérieur à l\'entreprise ( concurrent, etc... ) ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- en favorisant les synergies ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- en refusant tout chantage affectif ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - en excluant tout chantage à l\'emploi ? </p>', 0),
(266, 4, '<p>L\'allègement des pesanteurs psychologiques est-il obtenu</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - par l\'extinction des rivalités stériles ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par le refus de tout favoritisme de courtoisie ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par l\'absence de flatteries ?&nbsp;</p><p>L\'allègement des pesanteurs psychologiques est-il réalisé</p><p>&nbsp; &nbsp; &nbsp;- par l\'exclusion de tout sectarisme ou racisme ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par le rejet de tout sexisme ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par la sanction de tout harcèlement sexuel ? </p>', 0),
(267, 4, '<p><br></p><p>Outre les pesanteurs évoquées ci dessus, est-il d\'autres aspects méritant d\'être évoqués ?&nbsp;</p><p>Comme tous les autres écrans attachez vous à compléter les propositions par des invitations à agir, par de nouvelles questions qui déboucheront sur des réponses.</p>', 0),
(268, 4, '<p><br></p><p><br></p><p>Ces conditions psychologiques&nbsp;<span style=\"font-size: 10pt;\">du fait de leur portée&nbsp;</span><span style=\"font-size: 10pt;\">peuvent-elles être ignorées ?</span></p>', 0),
(269, 4, '<p>Vous venez de terminer le chapitre relatif au \"Contenu du travail\" inclus dans \"CONDITIONS de TRAVAIL\" .&nbsp;</p><p>GRH-dialogue demande indéniablement un effort. Il vous appartient de le rendre profitable. Cela ne dépend essentiellement que de vous.&nbsp;</p><p>Notre aide n\'est que marginale au regard de l\'effort que vous devez consentir pour accepter nos interrogations. Malgré la torture intellectuelle que certains peuvent ressentir, nous souhaitons vivement que vous avez passé un fructueux moment. Nous formons le vœu que la suite de la consultation sera tout aussi utile. </p>', 0),
(270, 5, '<p>Les horaires sont-ils considérés comme un outil de Gestion des Ressources Humaines pour tenir compte</p><p>&nbsp; - des évolutions sociologiques et sociales ( célibataires, familles mono-parentales...) ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- des occupations extra-professionnelles ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- des contingences familiales ?&nbsp;</p><p>Gardez vous de répondre par des réponses fermées (oui/non). Rechercher par contre des applications à donner aux questions est une démarche bien plus intéressante et souhaitable. L\'utilité de cet EAO repose sur cette modalité. Les questions soumises contiennent et offrent des axes de réflexions </p>', 1),
(271, 5, '<p>Les horaires sont-ils considérées comme un outil de Gestion des Ressources Humaines pour prendre en considération</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- des desiderata physiologiques ( personnes du soir ou du matin) ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- d\'une demande individualisée du temps de travail ?</p><p>&nbsp; &nbsp; &nbsp;- des possibilités ouvertes par les nouvelles technologies autorisant de nouvelles formes de travail ?</p><p>- des préférences personnelles liées à des habitudes sociales ou culturelles ? </p>', 0),
(272, 5, '<p>Les horaires comme outil de Gestion des Ressources Humaines, concourent-ils à réhabiliter le temps de travail en temps moins contraint (contraignant)</p><p>- afin d\'assouplir les temps extra-professionnels ( horaire variable avec ou sans crédit d\'heures) ?</p><p>- afin de donner le sentiment d\'une liberté plus grande ?&nbsp;</p><p>Des horaires appréciés par le personnel ne sont-ils pas, en soi, aussi profitables à l\'entreprise ?&nbsp;</p><p>Bien que l\'effet des 35 heures ait produit un nouvel aménagement du temps de travail rien n\'interdit de s\'intéresser à cette question.</p>', 0),
(273, 3, '<p>Montrez-vous de l\'écoute à l\'endroit des doléances telles que&nbsp;</p><p>- \"nous sommes en sous effectifs\",</p><p>- \"les moyens matériels sont</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - insuffisants,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- inadaptés</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - non fonctionnels\"</p><p>- \"les moyens fonctionnent mal\". </p>', 0),
(274, 5, '<p>L\'entreprise peut-elle rester sourde</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- aux évolutions sociales ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- aux demandes sociales ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- aux attentes sociales ?&nbsp;</p><p>L\'entreprise peut-elle ignorer les évolutions qu\'elle côtoie ?&nbsp;</p><p>Peut-elle ne pas les intégrer ou à défaut s\'y intéresser. </p>', 0),
(275, 5, '<p>Le travail à temps partiel est-il introduit comme temps choisi</p><p>&nbsp; &nbsp;- pour développer une disponibilité physique et psychologique ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - pour minimiser l\'absentéisme ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- pour minimiser certains acteurs de l\'entreprise qui souhaitent en bénéficier ?</p><p>Le travail à temps partiel est-il introduit pour accroître la flexibilité par la souplesse des heures supplémentaires qu\'il autorise ? </p>', 0),
(276, 5, '<p><br></p><p>Les fonctions partagées (deux personnes sur un même poste de travail ou une même fonction) sont-elles introduites dans l\'entreprise ?&nbsp;</p><p>Les fonctions partagées sont-elles encouragées, soutenues malgré les petits incidents auxquels elles peuvent donner lieu ?&nbsp;</p><p>Faites-vous preuve d\'une grande imagination en matière d\'horaires ? </p>', 0),
(277, 5, '<p>Toute idée originale d\'organisation du travail est-elle reçue, entendue, soutenue pour sous-tendre une stratégie de la conciliation ?&nbsp;</p><p>Donnez vous à penser que les bonnes idées n\'émanent pas forcément de l\'encadrement ?&nbsp;</p><p>Considéreriez vous possible l\'introduction de dispositions peu pratiquées ou jugées comme impossibles ?&nbsp;</p><p>Faites-vous preuve d\'une grande imagination en matière d\'horaires ? </p>', 0),
(278, 5, '<p>D\'autres formes atypiques de travail telles que le travail à domicile par télé-travail sont-elles introduites</p><p><span style=\"font-size: 10pt;\">&nbsp; &nbsp;- pour réduire les frais de fonctionnement ?</span></p><p>&nbsp; - pour satisfaire une éventuelle demande liée à la réduction de l\'amplitude de la journée de travail (temps de transport et de travail) ?</p><p>&nbsp; - pour favoriser un équilibre psychologique ou une disponibilité exceptionnelle ?</p><p>&nbsp;Est-il d\'autres formes qu\'il soit possible d\'introduire ? </p>', 0),
(279, 5, '<p>D\'autres formes atypiques de travail telles que le partage du lieu de travail (entreprise/ domicile) avec retour régulier en entreprise ou non sont-elles introduites ou envisagées ?</p><p>Toute autre forme atypique pour autant que les deux parties y trouvent avantages ne seraient-elles pas souhaitables ?&nbsp;</p><p>Ne convient-il pas de rester ouvert à toutes les idées tant pour laisser pénétrer les idées que de donner le sentiment d\'une vie dans l\'entreprise ? </p>', 0),
(280, 5, '<p>L\'auto-planification du travail</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par atelier,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par équipe,</p><p>- par durée de travail (journée, semaine, mois)ne serait-elle pas à entrevoir, à retenir ?&nbsp;</p><p>Vous donnez vous la contrainte de faire confiance et de l\'accorder ou en d\'autres termes de traiter les hommes en responsables de leurs actes ? </p>', 0),
(281, 5, '<p>L\'introduction de l’auto-planification du temps de travail dans le cadre de l\'unité ou de l\'atelier, a-t-elle pour but de concourir</p><p>&nbsp; &nbsp; - à une meilleure gestion du temps de travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à une plus grande satisfaction des employés ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à une auto-responsabilité ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à une meilleure compétitivité de l\'entreprise ? </p>', 0),
(282, 5, '<p>La délégation du temps de travail n\'encourage-t-elle pas</p><p>&nbsp; &nbsp;- une naturelle transformation des acteurs en auteurs ?</p><p>- une meilleure organisation du travail parce que pris en charge et sous la responsabilité des acteurs ? </p>', 0),
(283, 5, '<p>En renonçant à l’auto-planification du temps de travail dans le cadre de l\'unité ou de l\'atelier, ce choix tiendrait-il les collaborateurs incapables</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - de gérer un temps de travail imparti ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - d\'assumer des responsabilités, collectivement contrôlées ?&nbsp;</p><p>La délégation peut-elle être sans que ne soient consenties aussi des responsabilités individuelles et collectives ?&nbsp;</p><p>La délégation peut-elle se dispenser d\'un contrat de confiance ? </p>', 0),
(284, 5, '<p>La modulation du temps de travail négociable sur l\'année, le mois, la quinzaine ( par alternance de semaines comprimée et normale, de semaines de longue et faible durée) est-elle une donnée de l\'entreprise ?&nbsp;</p><p>Si non, l\'idée de l\'introduire est-elle exclue de votre fait ?&nbsp;<span style=\"font-size: 10pt;\">Pourquoi ?&nbsp;</span></p><p>Avez vous imaginé toutes les formes de travail possibles ?</p><p>&nbsp;L\'entreprise ne gagnerait-elle pas à introduire certaines dispositions susceptibles de satisfaire autant le personnel que l\'entreprise ?&nbsp;</p><p>Cette proposition mérite-t-elle d\'être balayée sans avoir été analysée ? </p>', 0),
(285, 5, '<p>La faculté d\'une retraite progressive est-elle négociée dans le cadre de la politique sociale de l\'entreprise ?&nbsp;</p><p>L\'entreprise fait-elle preuve d\'imagination et d\'audace pour transformer les horaires de travail ?&nbsp;</p><p><br></p><p>Cet écran n\'appelle pas de réponse fermée comme les autres. Refusez de répondre par oui ou non quand les questions sont ouvertes. S\'y refuser est parfois difficile, cette pratique est cependant à faire. Cherchez les applications possibles de toute question avant de passer à l\'écran suivant. </p>', 0),
(286, 5, '<p>Vous venez d\'achever la consultation du module \"Les horaires\" contenu dans le thème intitulé \"CONDITIONS de TRAVAIL\" .&nbsp;</p><p>Avez-vous le sentiment d\'avoir exploité les ressources de cette auto-formation que son schéma d’auto-expertise ?&nbsp;</p><p>Si vous souhaitez nous aider à le compléter, faites nous part de vos propositions d\'écrans. GRH-dialogue vous souhaite de poursuivre une fructueuse réflexion dans le questionnaire suivant. </p>', 0),
(287, 6, '<p>Au regard des propositions qui vous ont été soumises, des interrogations qui vous ont été avancées, seriez-vous enclin à revoir certains aspects de votre gestion de l\'organisation du travail et de ses périphériques les plus proches ?&nbsp;</p><p><br></p><p>Gardez vous de répondre par des réponses fermées (oui/non). Rechercher par contre des applications à donner aux questions est une démarche bien plus intéressante et souhaitable. L\'utilité de cet EAO repose sur cette modalité. Les questions soumises contiennent et offrent des axes de réflexions</p>', 1),
(288, 6, '<p>Cette réflexion vous incline-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à lutter contre le cloisonnement des structures ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à décloisonner les structures ?&nbsp;</p><p>Cette réflexion vous incite-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- à favoriser le travail en équipes horizontales et/ou verticales ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- à encourager les permutations de personnels ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- à privilégier l\'expression et la participation ? </p>', 0),
(289, 6, '<p>Cette réflexion vous incline-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à relativiser l\'appareil hiérarchique ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à écraser l\'échelle hiérarchique ?&nbsp;</p><p>Cette réflexion vous invite-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à concevoir de nouvelles répartitions ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à simplifier les procédures ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à transformer les formes, la notion d\'encadrement ? </p>', 0),
(290, 6, '<p>Cette réflexion vous incline-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à améliorer la communication dans la chaîne hiérarchique ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à favoriser les conditions de l\'expression ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à extérioriser les savoirs ?&nbsp;</p><p>Cette réflexion vous incite-t-elle</p><p>&nbsp; &nbsp; &nbsp; - à entrevoir sinon initier des expériences ?</p><p>&nbsp; &nbsp; &nbsp; - à ouvrir l\'entreprise ? </p>', 0),
(291, 6, '<p>Cette réflexion vous incline-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à associer la base exécutante au management de son travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à tolérer sinon incorporer des innovations trouvées par les salariés ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à conforter des formes et des modes du travail pourtant non prescrits ?</p><p>&nbsp;Vous donnez-vous le pouvoir d\'insuffler du changement ou plus simplement des modifications de comportement plus proches de la vie courantes, mais néanmoins, habitées de certaines contraintes qui tiennent au travail et aux conditions de sa réalisation. </p>', 0),
(292, 6, '<p>Cette réflexion vous incline-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à accepter et à inviter des remises en question structurelles ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à concevoir une transformation des ateliers ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à défier l\'imagination des travailleurs ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à renouveler les conceptions en place ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à produire de nouvelles perspectives de production ? </p>', 0),
(293, 6, '<p>Cette réflexion vous incline-t-elle</p><p>&nbsp; - à transformer des relations tant entre les salariés eux mêmes qu\'entre ces salariés et leurs cadres ?</p><p>&nbsp; - à favoriser les collaborations pluridisciplinaires ?</p><p>&nbsp; - à expérimenter ?&nbsp;</p><p>GRH-dialogue vous a proposé une réflexion. Il vous revient d\'en tirer des choix, des conclusions vous convenant.</p>', 0),
(294, 6, '<p>Vous venez de terminer la consultation du dernier questionnaire du thème intitulé \"CONDITIONS de TRAVAIL\".&nbsp;</p><p>Ce conversationnel n\'a que l\'ambition de vous conduire à une gestion des RH encore plus innovante.Il vous revient de l\'inventer. Nous y apportons notre concours par cette suite de questions qui peuvent trouver des applications non coûteuses et susceptibles d\'être fort appréciées.&nbsp;</p><p>Générer un climat social favorable au travail est un investissement qui produit des économies. Nous vous remercions de l\'attention que vous avez consentie à ce conversationnel un peu particulier.</p>', 0),
(295, 3, '<p>Accordez-vous de l\'attention aux doléances relatives à la formation insuffisante des personnels notamment à travers ses effets</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - sur la compréhension ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- sur leur collaboration (par ricochet)?</p>', 0),
(296, 3, '<p>Accordez-vous de l\'attention aux griefs relatifs</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à la pénibilité du travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à la charge de travail physique et mentale ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à son augmentation ? </p>', 0),
(297, 3, '<p>Au regard de ce qui est exprimé en matière d\'organisation, seriez-vous</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à vouloir expliquer ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à vouloir prendre en compte ces expressions ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à vouloir corriger ces éléments ? </p>', 0),
(298, 3, '<p>Estimez-vous ou observez-vous des difficultés de collaboration</p><p>&nbsp; &nbsp; &nbsp;- entre salariés d\'une équipe ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - entre collègues de travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - avec la hiérarchie ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - avec l\'équipe encadrée ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- avec les équipes sous-traitantes ? </p>', 0);
INSERT INTO `question_qst` (`qst_id`, `cou_id`, `qst_question`, `qst_first`) VALUES
(299, 3, '<p>Prêtez-vous un peu d\'attention aux griefs relatifs</p><p>&nbsp; &nbsp; - aux manques de considération ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- aux évocations de mauvaises ambiances ou difficiles ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - aux absences de dialogue ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- aux défaillances de communication ? </p>', 0),
(300, 3, '<p><br></p><p>Estimez-vous que l\'environnement de travail apporte des réponses satisfaisantes en matière de sécurité physique des salariés ?&nbsp;</p><p>La question n\'a que vocation à rappeler ce thème pour y réfléchir, à le relier avec les éventuelles demandes et recommandations émises par le CHSCT. </p>', 0),
(301, 3, '<p><br></p><p><br></p><p>Estimez-vous que l\'environnement de travail apporte des réponses satisfaisantes en matière de sécurité physique des salariés ? </p>', 0),
(302, 3, '<p>Prêtez-vous une attention aux remarques psychologiques du type</p><p>&nbsp; &nbsp; &nbsp;- \"Le travail ne me lâche plus.\"</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - \"J\'ai le sommeil perturbé par le travail\".</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- \"Mes proches disent que je sacrifie trop pour mon travail\".</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - \"quand je rentre à la maison - je n\'arrive pas à oublier\".</p>', 0),
(303, 3, '<p>Lors de difficultés, avez-vous le sentiment de pouvoir compter</p><p>&nbsp; &nbsp; &nbsp; - sur votre hiérarchie immédiate ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- sur votre hiérarchie plus lointaine ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - sur la direction générale ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - sur le service médical ? </p>', 0),
(304, 3, '<p>Lors de difficultés, pouvez-vous obtenir de l\'aide</p><p>&nbsp; &nbsp; - de vos collègues ?</p><p>&nbsp; &nbsp; - de l \'équipe de travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - de la médecine du travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - des représentants du personnel ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - des organisations syndicales ? </p>', 0),
(305, 2, '<p>En somme, intégrez vous l\'idée de bien être au travail</p><p>- pour réduire les coûts du stress ?</p><p>- pour diminuer l\'absentéisme de toutes natures ?</p><p>&nbsp;- pour améliorer la santé des salariés par l\'absence de troubles (mal de dos, insomnies, fatigue chronique) et au delà</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - pour réduire des coûts financiers,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - pour améliorer la disponibilité,</p><p>- pour accroître la productivité ? </p>', 0),
(306, 2, '<p><br></p><p>Les objectifs assignés ont-ils été négociés ?&nbsp;</p><p>Sont-ils mesurables ? atteignables ? réalistes ?&nbsp;</p><p>Sont-ils en adéquation avec d\'autres facteurs de la GRH ambiance de travail ? distribution/ répartition équitable du travail ? inscription dans la durée légale du travail ? </p>', 0),
(307, 3, '<p>Vérifiez-vous l\'adaptation de la personne à l\'emploi ?&nbsp;</p><p>Consentez-vous à la revoir en ajoutant ou en retranchant des aspects ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- au regard des évolutions du travail lui-même ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- au regard des demandes formulées par les personnes mêmes ?&nbsp;</p><p>Cette révision se fait-elle tant suite</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à des observations régulières ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à une écoute de l\'encadrement proche ?</p><p>- à une écoute des personnes lors de moments privilégiés formalisés ou non (entretien annuel..) ? </p>', 0),
(308, 3, '<p>Vous donnez-vous d\'être aussi réactif que possible dans l\'évolution des emplois ?&nbsp;</p><p>Cette dynamique est-elle constamment expliquée au point de faire partie de la culture, des choses normales, des intérêts et non des désagréments de l\'entreprise ?&nbsp;</p><p>Accompagnez-vous cette dynamique par<span style=\"font-size: 10pt;\">&nbsp;une association des salariés aux aspects entrepreunariaux:&nbsp;</span></p><p><span style=\"font-size: 10pt;\">&nbsp; &nbsp; &nbsp; * à ses résultats ?&nbsp;</span></p><p><span style=\"font-size: 10pt;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * à ses commandes ?</span></p><p><span style=\"font-size: 10pt;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * à ses recherches, à ses découvertes, à ses brevets ?</span></p>', 0),
(309, 5, '<p><br></p><p><br></p><p>Accordez-vous de la place à l\'autonomie tant comme outil de travail que pour le bien être des salariés ? </p>', 0),
(310, 5, '<p><br></p><p>Le faites-vous à l\'intention de tous les salariés ?&nbsp;</p><p>Le faites-vous pour assurer de la flexibilité pour gérer une part de la transformation de l\'organisation du travail ?&nbsp;</p><p>Acceptez vous pleinement le transfert de responsabilité ? une part de confiance ? une évolution de l\'emploi offert par les nouvelles techniques de communication ? </p>', 0),
(311, 5, '<p>Votre réticence tient-elle&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; -à l\'impossibilité de contrôle ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à la difficulté de gérer à distance ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à la maîtrise plus délicate de la gestion du temps de travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à la réticence à accorder davantage d\'autonomie ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à transférer des responsabilités ?</p><p>&nbsp; &nbsp;- à votre inquiétude de laisser trop d’initiatives aux bénéficiaires ?</p><p>&nbsp;- à craindre une moindre productivité&nbsp;</p><p>alors que toutes les études concluent au contraire à une bien meilleure ? </p>', 0),
(312, 5, '<p><br></p><p>La permissivité i<span style=\"font-size: 10pt;\">ssue de la lucidité d\'intérêts compris</span><span style=\"font-size: 10pt;\">&nbsp;gagnant du terrain,&nbsp;</span><span style=\"font-size: 10pt;\">seriez-vous enclin à entrevoir des organisation du travail sortant des sentiers balisés par des horaires précis ?</span></p>', 0),
(313, 5, '<p>Compte tenu de la modification de certains emplois, convenez vous d\'autoriser des salariés à gérer entièrement leur temps de travail</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - dès lors que le contenu du travail est fixé ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- dès lors que l\'objectif est arrêté et daté ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- dès lors que les termes du contrat sont acceptés ? </p>', 0),
(314, 5, '<p>Cette modalité nouvelle du travail peut-elle être négligée alors qu\'elle montre des avantages pour toutes les parties et notamment</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - un gain de locaux,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - une non nécessité de la présence permanent de salariés,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- un sentiment de liberté assorti du temps de travail moins ressenti,</p><p>- le développement des nouvelles technologies permettant une proximité quasi instantanée des salariés non présents ? </p>', 0),
(315, 4, '<p>Intégrez vous l\'idée de bien être au travail</p><p>&nbsp; &nbsp;- pour réduire les coûts du stress ?</p><p>&nbsp; &nbsp;- pour diminuer l\'absentéisme pour maladie ?</p><p>&nbsp; &nbsp;- pour améliorer la santé des salariés (mal de dos, insomnies, fatigue chronique) ? et au delà</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- pour réduire les coûts financiers ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- pour améliorer la disponibilité ?</p><p>- pour accroître la productiivté ? </p>', 0),
(316, 4, '<p>Compte tenu de l\'évolution des nouvelles organisation du travail</p><p>&nbsp; &nbsp; &nbsp; - salariés nomades, télétravail, équipes de travail transversales, ...</p><p>&nbsp; &nbsp; &nbsp; - conduisant à une fragmentation du tissu social,</p><p>- concevez-vous de rapprocher ces différents salariés pour les intégrer et donc casser l\'isolement créé</p><p>&nbsp; &nbsp; &nbsp; - pour faciliter leur travail ?</p><p>&nbsp; &nbsp; &nbsp; - pour leur permettre de se situer dans la complexité de l\'ensemble ?</p><p>&nbsp; &nbsp; &nbsp; - pour améliorer la fluidité de l\'information ? </p>', 0),
(317, 4, '<p>Pour générer de meilleures perceptions du travail de chacun</p><p>- instaurez-vous de lieux d\'échanges assez conviviaux dans le restaurant d\'entreprise, dans les lieux de repas, dans les points obligés ou classiques de rencontres</p><p>- photocopieuse, distributeurs des boissons, coins fumeurs</p><p>- afin de créer des échanges plus ouverts et moins conventionnels ?&nbsp;</p><p>afin de permettre la diffusion d\'informations et les échanges informels toujours précieux ? </p>', 0),
(318, 4, '<p>Concevriez-vous des espaces de détente qui soient aussi des lieux de travail ? Pourrait-on exclure que les idées puissent naître en dehors d\'une salle de réunion ou de travail classique ?&nbsp;</p><p>Les réticences, les pesanteurs, les blocages ne seraient-ils pas moins prégnants dans un cadre moins entaché de conventions ?&nbsp;</p><p>Les idées nouvelles, les propositions ne trouvent-elles pas plus aisément à s\'exprimer dans un cadre davantage dissocié de hiérarchie, d\'émotions, de rancœurs... ? </p>', 0),
(319, 2, '<p><br></p><p>Seriez vous enclin à penser que la qualité de vie au travail&nbsp;</p><p><span style=\"font-size: 10pt;\">&nbsp; &nbsp;- constitue un gisement de performance ?</span></p><p>&nbsp; &nbsp;- ne constitue pas un gisement de performance ? Si c\'est là votre choix, qu\'est ce qui vous fait penser cela ?&nbsp;<span style=\"font-size: 10pt;\">Maintiendriez vous cette appréciation après réflexion ?</span></p>', 0),
(320, 2, '<p><br></p><p>Estimeriez vous que l\'agencement des espaces de travail comme</p><p>&nbsp; &nbsp; - la bonne configuration</p><p>&nbsp; &nbsp; - la bonne conception des lieux de travail est un élément de l\'efficacité ? </p>', 0),
(321, 2, '<p><br></p><p>L\'inconfort, l\'inadéquation des moyens, les agencements inadaptés sont-ils traqués pour faire des lieux de travail des lieux agréables qui donne envie de satisfaire au travail demandé, voire plus.&nbsp;</p><p>Ces aspects sont-ils compatibles avec une perception occidentale de l\'entreprise ? </p>', 0),
(322, 2, '<p><br></p><p>En somme, veillez vous&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à associer aspects physiques et aspects psychologiques?&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à traiter simultanément aspects périphériques et aspects propres du travail ? </p>', 0),
(323, 2, '<p>Concevez vous que la lenteur puisse être un facteur de productivité ? si la lente maturation</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; -évite les gaspillages de toute nature ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- permet d\'aller très vite ensuite ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - écarte tout écueil, tout retard ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- assure la qualité recherchée sans imposer des contrôles? </p>', 0),
(324, 2, '<p><br></p><p>Seriez vous enclin à laisser peu de place à la précipitation pour privilégier la réflexion ?</p><p>Accordez-vous ce temps pour en gagner après, c\'est à dire, faites vous du temps un investissement ? </p>', 0),
(325, 2, '<p><br></p><p>Tendez-vous à encourager le personnel</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à développer la/ sa réflexion à la précipitation ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à identifier la complexité à la superficialité ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à préférer la chose bien développée à la chose facile ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à retenir la lenteur productive à l\'excitation pénalisante ? </p>', 0),
(326, 2, '<p>Pour compléter ces différences options, seriez vous enclin à encourager</p><p>&nbsp; &nbsp; &nbsp;- le télétravail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- des mixtes télétravail / travail classiquement organisés ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- voire des formes inédites d\'organisation ? </p>', 0),
(327, 2, '<p>Le travail tend à créer de l\' isolement, du nomadisme, de la spécialisation cherchez vous à réduire ce sentiment de solitude en créant de la transversalité en rappelant que le travail de chacun n\'est pas incidence sur celui d\'autrui ?&nbsp;</p><p>Veillez vous à rappeler la nécessité des collaborations pour dénouer la complexité ?&nbsp;</p><p>Contribuez à fluidifier la circulation de l\'information, des informations ?&nbsp;</p><p>Favorisez vous la relation pour chasser la solitude ? </p>', 0),
(328, 4, '<p>La satisfaction des individus est-elle réalisée</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par un compte-rendu régulier des résultats commerciaux obtenus ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par une diffusion des chiffres à l\'exportation, etc... ?&nbsp;</p><p>Vous donnez vous d\'être aussi redevable d\'informations que vous détenez pour faire ce que vous demandez de chacun ?&nbsp;</p><p>Ne convient-il pas de faire preuve d\'exemplarité de façon naturelle ? </p>', 0),
(329, 2, '<p>Favorisez vous l\'embauche de personnes handicapées parmi les personnels tant pour souscrire aux obligations légales que pour souligner une action sociale</p><p>- que pour inviter à un questionnement du personnel sur l\'importance des choses ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- que pour initier davantage de perspicacité ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- que pour favoriser l\'imagination,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- que pour déclencher l\'innovation ?</p><p>- que pour souscrire à l\'adaptabilité ?</p><p>- que pour encourager la prise de recul ?</p><p>- que pour accepter les différences ?</p><p>- pour remettre en question tout simplement ? </p>', 0),
(330, 3, '<p><br></p><p>Prenez-vous soin&nbsp;<span style=\"font-size: 10pt;\">d\'adapter momentanément un poste de travail aux temps de réponse d\'un salarié face aux sollicitations d\'une machine ?&nbsp;</span></p><p>Par exemple, rappelez-vous à l\'encadrement qu\'un temps d\'apprentissage est toujours nécessaire et qu\'on ne saurait accorder du temps pour expliquer correctement le travail. C\'est à dire admettre que le travail mal réalisé vient souvent d\'un travail mal expliqué. </p>', 0),
(331, 3, '<p>Vous donnez-vous le privilège d\'innover dans des aspects psychologiques</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- en remettant en cause les habitudes ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- en prenant à contre courant les propositions ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- en développant des chemins et des voies déroutantes ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- en instillant des formes de contre cultures bien que le terme soit excessif ? </p>', 0),
(332, 2, '<p>Le faites-vous à l\'intention de tous les salariés sachant que cette organisation du travail favorise tant les salariés qui souhaiteraient y souscrire que pour l\'entreprise ?&nbsp;</p><p>Cette intention vise-t-elle seulement la recherche d\'économies ou intègre-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- un transfert de responsabilité plus important ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- l\'octroi d\'une plus grande confiance ?</p><p>&nbsp; &nbsp; &nbsp;- la pratique permise par les nouvelles technologies de la communication ? </p>', 0),
(333, 2, '<p>Votre réticence tient-elle à</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à l\'impossibilité d\'un contrôle ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à la difficulté de gérer le travail à distance ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à la maîtrise plus délicate des la gestion du temps de travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à l\'attachement à un mode traditionnel d\'organisation ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à l\'inquiétude de générer de nouvelles habitudes chez les salariés ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à l\'éventuel impossibilité de revenir en arrière ? </p>', 0),
(334, 2, '<p>Si le travail est de plus en plus générateur de stress, veillez vous à l\'assortir&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* en créant des lieux de convivialité ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* en permettant ,&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* en facilitant,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* en provoquant&nbsp;<span style=\"font-size: 10pt;\">&nbsp;les échanges ?</span></p>', 0),
(335, 2, '<p><br></p><p>Accordez-vous à la décontraction au rire à la bonne humeur une place, comme condition de travail, comme un élément du travail, comme condition du travail ? </p>', 0),
(336, 2, '<p>Pour contenir et évacuer les tensions, les stress, disposez-vous</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - des espaces de travail informels ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- des lieux de réunion non conventionnels ?</p><p>- des temps de rencontres et d\'échanges d\'idées en vue de nouveaux produits, de nouvelles organisations.. ?&nbsp;</p><p>Accordez-vous à la médecine du travail une reconnaissance autre celle obligatoire? </p>', 0),
(337, 2, '<p>Portez vous une attention à accompagner l\'ensemble du personnel lors de changement important dans les processus de travail</p><p>&nbsp; &nbsp; - par une information sur les technologies à venir ?</p><p>&nbsp; &nbsp; - par une présentation des méthodes à instaurer ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par &nbsp;une &nbsp;préparation &nbsp;aux &nbsp;changements ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par une dédramatisation des nouveautés ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par une adaptation aux emplois ? </p>', 0),
(338, 2, '<p>Compte tenu des évolutions inéluctables du contenu des emplois&nbsp;</p><p>&nbsp; apportez- vous des explications quant à l\'environnement professionnel ?&nbsp;</p><p>&nbsp; intégrez- vous les remarques des personnels quand à ces nouveaux environnements ?&nbsp;</p><p>&nbsp; adaptez-vous des dispositions pour permettre une participation des salariés à l\'organisation de leur travail ? </p>', 0),
(339, 2, '<p>Faites-vous que la bonne humeur soit une condition de de votre ambiance de travail&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- en recrutant du personnel ayant en plus cette disposition ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- en permettant cet état d\'esprit ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- en ne stigmatisant pas les situations de fou rire ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- en encourageant la bonne humeur dans le travail ? </p>', 0),
(340, 4, '<p><br></p><p>En omettant toutes les potentialités des hommes, n\'est-il pas délaissé, négligé&nbsp;<span style=\"font-size: 10pt;\">une part de compétitivité par l\'absence d\'intégration du plaisir, de considérations annexes du travail ?&nbsp;</span></p><p>Cherchez vous à marier l\'homme et le travail et l\'homme social et la vie dans l\'entreprise ? </p>', 0),
(341, 4, '<p>Face à l\'individualisation croissante, l\'organisation du travail est-elle ordonnée collectivement pour atténuer les effets d\'isolement</p><p>&nbsp; - en diffusant la contribution de chaque unité et service ?</p><p>&nbsp; &nbsp;- en créant une communication plus facile entre producteurs et administratifs, entre commerciaux et techniciens ?&nbsp;</p><p>Veillez-vous à revaloriser les producteurs, à chercher une parité avec les catégories de salariés ? </p>', 0),
(342, 4, '<p><br></p><p>L\'attention prêtée au collectif est-elle complétée par une conciliation de l\'individuel et du collectif via<span style=\"font-size: 10pt;\">&nbsp;davantage de convivialité tout en laissant des échappatoires psychologiques (ex: poste de travail isolé, faculté de permutations possibles et temporaires) ?&nbsp;</span></p><p><span style=\"font-size: 10pt;\">Seriez-vous enclin à faire concevoir des emplois non figés en appliquant une polyvalence des hommes par une rotation imposée ?</span></p>', 0),
(343, 2, '<p>Portez- vous une écoute attentive aux énoncés des salariés ?&nbsp;</p><p>Décryptez-vous les manifestations d\'un mal être&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* absentéisme,&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * fuites, évitements,&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * non participation, attention minimale,&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * manifestations de refus, de délaissement,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * observation de non qualité,&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * tensions, affrontements larvés, altercations, etc... </p>', 0),
(344, 2, '<p><br></p><p>Vous efforcez-vous de surveiller toutes ces expressions incontrôlables mais visibles ?&nbsp;</p><p>Apportez vous une réponse concrète et rapide à toutes ces manifestations ?&nbsp;</p><p>Cherchez-vous des réponses seul et collectivement ? </p>', 0),
(345, 2, '<p><br></p><p>Faites-vous que la dérision que la caricature aient droit de cité pour autant&nbsp;</p><p>&nbsp; &nbsp; &nbsp; - qu\'elles soient taquines et non injurieuses ?&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- qu\'elles instillent une ambiance de travail assortie de convivialité, d\'esprit d\'équipe ? </p>', 0),
(346, 2, '<p><br></p><p>Instaureriez-vous le rire</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - pour créer des complicités entre les équipes ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - pour encourager les contacts, les échanges ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- pour réduire les barrières hiérarchiques ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- pour améliorer la communication ? </p>', 0),
(347, 2, '<p><br></p><p>Seriez vous enclin à introduire de l\'art dans l\'entreprise ? Par exemple&nbsp;</p><p>&nbsp; * par des expositions d’œuvres des salariés (peintures, photographies, sculptures....) ?&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * par des murs offerts à la peinture (tags compris) ?&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * par l\'accrochage d\'affiche de copies d\'oeuvre célèbres ou non ? </p>', 0),
(348, 2, '<p><br></p><p>Seriez vous enclin à reconnaître l\'entreprise comme un lieu de vie à part entière et à ce titre à lui donner des atours proches de ceux qui seraient souhaitables pour en faire un lieu de vie agréable ? </p>', 0),
(349, 3, '<p>Veillez-vous<span style=\"font-size: 10pt;\">&nbsp;à encourager des solidarités professionnelles ?&nbsp;</span></p><p>Recherchez v<span style=\"font-size: 10pt;\">ous à créer</span></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- des collectifs de travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- des équipes soudées ?&nbsp;</p><p>en acceptant de minimiser ou d\'accepter le risque qu\'est supposé contenir un tel choix ? </p>', 0),
(350, 6, '<p><br></p><p>Au terme de cette confrontation, l\'amélioration des conditions de travail vous semble-t-elle utile pour réduire</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- l\'absentéisme &nbsp;pour &nbsp;maladie &nbsp;ou autres &nbsp;motifs ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- la lassitude au travail, voire le dégoût du travail ? </p>', 0),
(351, 6, '<p><br></p><p>Cette première approche vous incline-t-elle à entrevoir les conditions de travail non plus forcément comme un coût mais comme un investissement rentable ?&nbsp;</p><p>S\'il fallait une preuve allant dans ce sens, ne faut-il pas s\'inspirer des réalisations japonaises, américaines ou autres qui donnent à montrer que ces investissements se montrent révélateurs d\'économies.</p>', 0),
(352, 9, '<p><br></p><p>Tout accueil d\'un collaborateur est-il accompagné</p><p>&nbsp; &nbsp;- par une prévenance: courtoisie, politesse, poignée de mains, mot de bienvenue, etc... ?</p><p>&nbsp; &nbsp;- par une attention verbale ou visuelle en guise de préliminaires ? </p>', 0),
(353, 7, '<p><br></p><p>Les RH sont-elles améliorées par une identité créée&nbsp;<span style=\"font-size: 10pt;\">&nbsp;autour de liens de solidarité employeur/employés par responsabilité réciproque ?&nbsp;</span></p><p><span style=\"font-size: 10pt;\">Les conditions du commerce international s\'étant tendues, l</span><span style=\"font-size: 10pt;\">es conséquences de cet état de choses se font sentir dans toutes les entreprises. Vous attachez-vous à montrer en permanence votre préoccupation de préserver les emplois ?</span></p>', 0),
(354, 7, '<p>Au regard des écrans déjà consultés, avez vous le sentiment d\'avoir crée</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - &nbsp;un &nbsp;climat,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - un contexte,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - &nbsp;une &nbsp;réalité&nbsp;</p><p>favorisant un état de satisfaction et de bien être dans l\'entreprise ? </p>', 0),
(355, 12, '<p><br></p><p><br></p><p>Vous donnez-vous une responsabilité dans la formation de vos salariés ? </p>', 0),
(356, 7, '<p><br></p><p><br></p><p><span style=\"font-size: 10pt;\">Estimez-vous recevoir le respect ou l\'estime que jugez mériter?</span></p>', 0),
(357, 7, '<div><br></div>Vous venez de terminer le volet introductif au thème intitulé \"Relations Humaines\". Nous souhaitons que cette \\confrontation\\ aura été fructueuse.&nbsp;<div><br></div><div>Nous vous souhaitons une agréable et intéressante consultation du thème suivant.&nbsp;</div><div>Rappel : prenez le temps de voir quelle(s) application(s) est susceptible d\'être donné(es)à chacune des interrogations. Prenez le temps de cette recherche.</div>', 0),
(358, 7, '<p><br></p><p>Vous donneriez-vous d\'inventer, d\'innover ?&nbsp;</p><p>Ainsi, vous inclineriez-vous à bousculer l\'ordre social pour donner un sens dynamique à votre gestion des relations humaines ?</p><p>Accepteriez-vous par exemple de mettre fin à des droits, à des signes de distinction sociale comme des places de parking réservées pour tous les cadres alors que seuls peuvent légitimement en bénéficier ceux fréquemment amenés à se déplacer ? </p>', 0),
(359, 7, '<p><br></p><p>Veillez-vous aussi que chacun puisse ressentir une certaine reconnaissance dans son travail ?&nbsp;</p><p>Vous attachez-vous à ce que cette reconnaissance ne soit pas seulement de l\'encadrement mais aussi des des autres salariés ?&nbsp;</p><p>Continuez votre chemin</p>', 0),
(360, 12, '<p><br></p><p><br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Prenez vous en considération la notion d\'équité ? </p>', 0),
(361, 7, '<p><br></p><p>Investir dans les RH se conjugue-t-il avec savoir prendre du temps ?&nbsp;</p><p>Quelles initiatives avez-vous prises ou prendriez vous pour illustrer ce propos ?&nbsp;</p><p>Quelles formes donneriez-vous pour rendre compte de cette démarche ? </p>', 0),
(362, 7, '<p>Cet investissement temporel est-il démontré:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par une patience ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par une écoute ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par l\'absence d\'une sécheresse de ton ?&nbsp;</p><p>Cet investissement est-il tempéré toutefois:</p><p>- par un budget temps consenti aux solliciteurs (accueil du personnel selon une plage horaire fixe, rendez-vous) ?</p><p>- par un crédit temps annoncé à l\'interlocuteur ?&nbsp;</p><p>Il ne peut être qu\'utile de rappeler de vous abstenir de répondre par oui ou par non. Recherchez dans votre quotidien les applications que pourraient susciter ces questions qui appellent des actions et non une réponse simple. Transfigurez la question.</p>', 0),
(363, 7, '<p><br></p><p>L\'impatience marquée par la nervosité, une certaine sécheresse de ton, l\'impréparation d\'un entretien, etc... peuvent-ils ne pas être perçus par le salarié ?&nbsp;</p><p>Les relations humaines ne risquent-elles pas de souffrir de leur traitement quotidien ?&nbsp;</p><p><br></p><p>Ici comme ailleurs gardez vous des oui et des non sans rechercher des actions possibles.</p>', 0),
(364, 7, '<p>Les RH sont-elles favorisées par une identité créée</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- autour d\'un modèle professionnel ( compagnons Bouygues, etc... ) ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - autour d\'un modèle d\'entreprise ?</p><p>&nbsp; &nbsp; - autour d\'un PDG ?</p><p>&nbsp; - autour d\'une tradition ( ex: compagnonnage, savoir-faire... ) ?&nbsp;</p><p>Les RH sont-elles favorisées par une identité créée</p><p>&nbsp; - autour des entreprises logos ( Boiron, IBM, Apple, Sony, etc... ) ?</p><p>&nbsp; - autour de produits fortement connotés socialement (pharmaceutiques électroniques, aéronautiques, etc...) ? </p>', 0),
(365, 7, '<p>Les RH sont-elles favorisées par une identité créée</p><p>&nbsp; &nbsp;- autour de projets industriels ambitieux (aéronautique, automobile, bâtiment, etc...) ?</p><p>&nbsp; &nbsp;- autour d\'une personne charismatique ou médiatique ?&nbsp;</p><p>Si le \"désenchantement de l\'entreprise\" est venue assombrir l\'image de l\'entreprise, il n\'en reste pas moins vrai &nbsp;qu\'elle n\' a rien perdu de sa représentation.&nbsp;</p><p>Vous donnez-vous de rechercher des synergies pour chercher à impliquer les hommes dans la pérennité de l\'entreprise ? </p>', 0),
(366, 7, '<p>La culture par identification est-elle aussi relayée</p><p>&nbsp; &nbsp;- par des solidarités vécues via des difficultés surmontées en commun ?</p><p>&nbsp; &nbsp;- par une intégration des aspirations individuelles dans la logique d\'entreprise ?&nbsp;</p><p>La culture par identification est-elle aussi relayée</p><p>- par une politique sociale de l\'entreprise ?</p><p>- par des formes originales de démocratie industrielle ex: \"élection, notation de l\'encadrement\", \"cogestion partielle\" ? </p>', 0),
(367, 12, '<p><br></p><p><br></p><p><br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Comment la traduisez vous ? </p>', 0),
(368, 7, '<p>L\'identité culturelle est-elle assise</p><p>&nbsp; - autour de valeurs partagées: loyauté, honnêteté, estime réciproque, solidarité dans le travail... ?</p><p>&nbsp; - autour de la réduction de signes de distinction sociale ?</p><p>&nbsp; &nbsp;- autour de valeurs professionnelles: ex: amélioration et innovation de produits ?&nbsp;</p><p>Ajouteriez-vous d\'autres aspects révélant des marques de distinction, des signes de culture d\'entreprise ? </p>', 0),
(369, 7, '<p><br></p><p>L\'identité est-elle assise</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - autour de formations la construisant ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - autour de participation à la qualité de vie au travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- autour de métiers ? </p>', 0),
(370, 7, '<p><br></p><p>Cela pèse-t-il sur votre moral ?&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Quelles conséquences cela-a-t-il ?&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Cherchez-vous à ce que cela ne rejaillisse pas ? </p>', 0),
(371, 7, '<p>Les hommes peuvent-ils se passer</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- de repères professionnels,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- de repères sociaux,</p><p>&nbsp; &nbsp; &nbsp;- de liens symboliques ou culturels pour transcender la simple relation d\'échange travail/rémunération ?&nbsp;</p><p>Vous attachez-vous à renforcer ces repères qui sont une part de fierté des salariés ?&nbsp;</p><p>Veillez-vous à créer des signes d\'adhésion à l\'entreprise ? </p>', 0),
(372, 7, '<p>L\'identité relationnelle est-elle élaborée</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- en souscrivant à des devoirs et des obligations ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- en créant des métiers nouveaux ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - en cultivant les valeurs du monde du travail ?&nbsp;</p><p>L\'identité relationnelle est-elle développée</p><p>&nbsp; &nbsp; &nbsp;- en autorisant un lissage des situations ?</p><p>&nbsp; &nbsp; &nbsp;- en autorisant un droit permettant à chacun de trouver le registre de son expression ? </p>', 0),
(373, 7, '<p><br></p><p>Il vous appartient de compléter cette liste non exhaustive. Tout dans cet outil de réflexion peut être développé, complété.&nbsp;</p><p>Il vous revient de trouver les formes d\'action les plus pertinentes à votre environnement.&nbsp;</p><p>Plus votre réflexion sera intense, pertinente plus vos réalisations auront de l\'écho.</p>', 0),
(374, 7, '<p><br></p><p>La gestion des RH n\'a-t-elle pas pour corollaire presque obligé la construction d\'une certaine identité ?&nbsp;</p><p>Créer cette gestion à travers un comportement reflétant les droits et obligations de chacun ne relève-t-il pas de la mission de la GRH ? </p>', 0),
(375, 7, '<p><br></p><p><br></p><p>Accréditeriez-vous l\'opinion suivante:\"Il importe de créer les conditions d\'une relation et d\'un cadre de travail plutôt que de plaquer des solutions toutes faites à un environnement social\\\" (dixit un DRH anonyme in Liaisons sociales).</p>', 0),
(376, 7, '<p><br></p><p>Eprouvez-vous de la fierté dans votre travail du fait que vous exercez</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - le travail pour lequel vous avez été formé ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- le travail que vous avez souhaité ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - le travail que vous faites avec plaisir ? </p>', 0),
(377, 7, '<p><br></p><p>GRH-Dialogue essayera de susciter quelques unes de ces conditions pour compléter celles instaurées.</p><p>Cet outil est une aide à la réflexion.Il contribue à faciliter le résultat recherché.&nbsp;</p><p>Tout ce qui est à construire relève de votre seule contribution. </p>', 0),
(378, 7, '<p><br></p><p><br></p><p>Il vous appartient de le penser.&nbsp;</p><p>GRH-dialogue vous propose une palette de conditions qu\'il vous reviendra de retenir ou non.</p>', 0),
(379, 7, '<p><br></p><p>Eprouvez-vous de la fierté dans la production de votre travail</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- du fait que vous effectuez des tâches intéressantes ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- du fait que vous effectuez des travaux correspondant à vos attentes ?</p><p>&nbsp; - du fait que vous exercez votre métier comme vous le concevez ? </p>', 0),
(380, 7, '<p>Les permutations du personnel visent-elles</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à rendre les échanges de service plus faciles ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à développer des relations implicites ?</p><p>&nbsp; &nbsp;- à cerner immédiatement le possible des demandes par les opérateurs eux-mêmes ?&nbsp;</p><p>Les permutations du personnel visent-elles</p><p>&nbsp; &nbsp; - à faire descendre les demandes potentielles d\'un degré hiérarchique et donc les faciliter ?</p><p>&nbsp; &nbsp; - à transformer la nature des demandes ? </p>', 0),
(381, 7, '<p>La permutation du personnel vise-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à créer des opportunités de rencontre ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à décloisonner les structures ?&nbsp;</p><p>La permutation du personnel vise-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à diminuer les tensions relationnelles ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à augmenter les relations sociales ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à réduire les appropriations de pouvoir ? </p>', 0),
(382, 7, '<p><br></p><p>Ces permutations sont-elles toutefois modérées</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par le bon fonctionnement d\'un service ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par le rodage des équipes ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par les expériences en cours ? </p>', 0),
(383, 7, '<p><br></p><p>L\'absence de renouvellement, de rénovation, de multiplication des relations humaines par l\'intermédiaire de permutations de personnel n\'encourage-t-elle pas</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- l\'inertie ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - le statu quo ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- la neutralisation des individus entre eux ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - la confinement, la rouille de l\'esprit ? </p>', 0),
(384, 7, '<p>L\'absence de renouvellement, de rénovation, de multiplication des relations humaines par l\'inexistence de permutations de personnel n\'encourage-t-elle pas</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - l\'absence d\'apprentissage sur le tas ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - la spécialisation des aptitudes ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- les situations acquises ? </p>', 0),
(385, 7, '<p><br></p><p>L\'efficacité du groupe est-elle aidée par</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - des objectifs clairement précisés ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - des procédures adéquates de prise de décision ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - une autorité compétente reconnue ? </p>', 0),
(386, 7, '<p>L\'efficacité du groupe est-elle complétée par</p><p>&nbsp; &nbsp;- le charisme du chef d\'entreprise ?</p><p>&nbsp; &nbsp;- une démonstration quasi permanente de ses qualités à manager les affaires et les hommes ?</p><p>&nbsp; - un halo d\'ombre sur la personnalité du PDG ?&nbsp;</p><p>L\'efficacité du groupe est-elle complétée par</p><p>&nbsp; &nbsp;- une cohérence de l\'équipe directoriale ?</p><p>&nbsp; &nbsp;- autres ? </p>', 0),
(387, 7, '<p><br></p><p>La gestion des hommes saurait-elle aussi se passer</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - de qualités techniques et humaines de l\'encadrement ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - d\'autorités reconnues par l\'ensemble du personnel ? </p>', 0);
INSERT INTO `question_qst` (`qst_id`, `cou_id`, `qst_question`, `qst_first`) VALUES
(388, 7, '<p>L\'efficacité du groupe est-elle facilitée</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par la clarté des communications ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par la précision de l\'origine des communications ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par leur concision ?&nbsp;</p><p>L\'efficacité du groupe est-elle facilitée</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par le volume réduit de notes ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par des communications horizontales et verticales ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par un retour d\'informations (feed back) ? </p>', 0),
(389, 7, '<p>Cette efficacité est-elle complétée&nbsp;<span style=\"font-size: 10pt;\">par l\'atténuation du droit au silence (et son appendice \"Silence sur le silence\"), traduite notamment</span></p><p>- par la mise en place des structures d\'échanges réels ( cercles de travail, de qualité, réunions au titre du droit d\'expression parfaitement libres) ?</p><p>- par la réunion de groupes de travail détaché te toute hiérarchie sociale ?&nbsp;</p><p>Cette efficacité est-elle complétée</p><p>- par un enrichissement des tâches pour que les individus aient quelque chose à dire de leur travail ?</p><p>- par l\'encouragement au droit à la contradiction ? </p>', 0),
(390, 7, '<p>Cette efficacité est-elle complétée</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par un écrasement de la chaîne hiérarchique ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par la diminution de l\'autoritarisme ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par une absence de toute infantilisation des personnels ?</p><p>- par une reconnaissance en tant que telle de toute personne ?&nbsp;</p><p>Cette efficacité est-elle poursuivie</p><p>- par la multiplication des dispositifs permettant aux salariés de s\'exprimer sans que cela ait un rapport direct avec le travail ?</p><p>- par l\'abstraction de toute considération sociale pendant ce temps et au delà ?&nbsp;</p><p>Il semble difficile de dissocier les espaces de droit : reconnaissance de droit dans certains cas et atténuation ou modification sensible de ceux-ci dans d\'autres. Veillez-vous à avoir une égalité de comportement ?&nbsp;</p><p>La sollicitez-vous de l\'ensemble de vos collaborateurs ? </p>', 0),
(391, 7, '<p>La communication peut-elle être occultée par l\'entreprise ?&nbsp;</p><p>Intégrez-vous la communication</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - comme &nbsp;élément &nbsp;de &nbsp;bien &nbsp;être ou de mieux être ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - comme facteur de gestion de relations humaines ?&nbsp;</p><p>Les formes données à la communication vous semblent-elles suffisantes ? satisfaisantes ? </p>', 0),
(392, 7, '<p>L\'efficacité est-elle réalisée par des groupes professionnellement soudés</p><p>- autour de compétences apprises ou acquises (mémoires d\'anciens) ?</p><p>- autour d\'autorités reconnues par tous ?</p><p>- autour de qualités professionnelles ( ingéniosité, méticulosité, métier, travail bien fait, débrouillardise ) admises et partagées de tous ?</p><p>- de valeurs culturelles communes ?&nbsp;</p><p>Pour rappel, épargnez vous les réponses fermées oui /non si souvent présentes à l\'esprit dans le cheminement de ce logiciel. </p>', 0),
(393, 7, '<p>La remarque suivante: \"Ce n\'est pas le travail qui est forcément déplaisant, ce sont les gens qui le rendent épouvantable\" est-elle ou vous paraît-elle</p><p><span style=\"font-size: 10pt;\">&nbsp; &nbsp; - juste ?</span></p><p>&nbsp; &nbsp; - totalement inexact ?&nbsp;</p><p>En lui accordant une part de vérité, la traduisez-vous par des actions ? des attentions pour lutter contre les initiateurs de ce climat ? </p>', 0),
(394, 7, '<p>En considérant que le travail est rendu partiellement difficile par les acteurs eux-mêmes, conviendriez-vous de considérer</p><p>&nbsp; - l\'organisation du travail éclaté en petites unités,</p><p>&nbsp; - une polyvalence discutée / négociée / adaptée / acceptée comme des possibilités ?&nbsp;</p><p>pourriez vous entrevoir le traitement de ces difficultés entre autre</p><p>&nbsp; &nbsp;- par une mixité du personnel ?</p><p>&nbsp; &nbsp;- par le maintien de noyaux * ethniques, * culturels ou * sociaux dans certains cas ? </p>', 0),
(395, 7, '<p><br></p><p>En considérant que le travail est rendu partiellement difficile par les acteurs eux-mêmes, imagineriez-vous de prévenir cet état de fait entre autre</p><p>&nbsp; - par une cohérence relationnelle des groupes obtenue, notamment,</p><p>&nbsp; - par la consultation de l\'équipe d\'accueil lors d\'un recrutement ? </p>', 0),
(396, 7, '<p><br></p><p>Si le travail est rendu partiellement difficile par les acteurs eux-mêmes, ce dysfonctionnement est-il prévenu entre autres</p><p>&nbsp; - par la responsabilité du groupe à gérer ses aspects matériels et professionnels autant qu\'humains ?</p><p>&nbsp; - par l\'invitation faite aux protagonistes d\'exprimer leurs différends et leurs velléités ? </p>', 0),
(397, 7, '<p>Si le travail est rendu partiellement difficile par les acteurs eux-mêmes,&nbsp;</p><p>&nbsp; &nbsp;* conviendriez-vous de prévenir ces aspects entre autre</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par l\'exercice d\'une franchise autant que faire se peut ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par l\'invitation à l\'expression des griefs ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par une invitation spontanée au mea culpa ?&nbsp;</p><p>* veilleriez-vous à prévenir ces troubles entre autre</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par l\'absence de rancunes durables ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par des admonestations exprimées mais non rappelées ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par le traitement à chaud des erreurs ? </p>', 0),
(398, 7, '<p><br></p><p>Au regard des sentiments vécus, vous attachez-vous</p><p>&nbsp; &nbsp; &nbsp; - à donner à chacun une réelle satisfaction au travail ?</p><p>&nbsp; &nbsp; &nbsp; - à faire que chacun ressente un peu de fierté à faire ce qu\'il fait ?</p><p>&nbsp; &nbsp; &nbsp; - à partager le sentiment d\'un travail partagé et donc d\'une participation ? </p>', 0),
(399, 7, '<p>Si le travail est rendu partiellement difficile par les acteurs eux-mêmes,&nbsp;</p><p>&nbsp; * ce dysfonctionnement est-il prévenu entre autre</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par le traitement des erreurs sous forme pédagogique ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par le sens des responsabilités ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par l\'équité ?&nbsp;</p><p>&nbsp; * ce dysfonctionnement est-il prévenu entre autre</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par la fermeté ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par l\'exigence de parole tenue ? </p>', 0),
(400, 7, '<p><br></p><p><br></p><p>Au regard de ce qui vous est agréable, vous attachez-vous à ce que chacun ait des relations sociales satisfaisantes ? </p>', 0),
(401, 7, '<p><br></p><p>Pour prolonger ces aspects, veillez-vous</p><p>&nbsp; - à faire que les tâches effectuées correspondent à la formation suivie (c\'est à dire des attentes immédiates )</p><p>&nbsp; - à satisfaire les attentes à terme des collaborateurs ? </p>', 0),
(402, 7, '<p><br></p><p>Si le travail est indépendant des gens qui l\'accomplissent, ce choix exclut-il</p><p>- que les relations inter-personnelles soient tout à fait étrangères au désagrément trouvé dans le travail ?</p><p>- qu\'un individu ne soit pas un désagrément pour un autre ?&nbsp;</p><p>Poursuivez à votre rythme l\'examen de ce chemin (peut être) de croix, mais dont vous percevez les fruits de votre effort ultérieurement. </p>', 0),
(403, 7, '<p>La gestion des Ressources Humaines s\'attache-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à drainer l\'efficacité personnelle ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à aménager les expressions professionnelles ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à favoriser les talents,les ambitions ?&nbsp;</p><p>La gestion des Ressources Humaines s\'attache-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à solliciter les imaginations ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à socialiser le personnel dans toutes ses composantes ? </p>', 0),
(404, 7, '<p><br></p><p>La gestion des Ressources Humaines s\'attache-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à lisser les aspérités sociales ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à aménager la production d\'aspects sociaux ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à attribuer aux salariés une part de la production ? </p>', 0),
(405, 7, '<p><br></p><p>La socialisation du personnel est-elle réalisée</p><p>&nbsp; &nbsp;- par l\'organisation de festivités formelles: fête d\'entreprise, arrosages divers (contrat, départ...) ?</p><p>&nbsp; &nbsp;- par l\'absence de discrimination de toute nature ? </p>', 0),
(406, 7, '<p>La socialisation du personnel est-elle encouragée par des activités issues</p><p>&nbsp; - du Comité d\'Entreprise ( activités sociales et culturelles ) ?</p><p>&nbsp; - d\'initiatives individuelles ?</p><p>&nbsp; - de la direction ?</p><p>&nbsp; &nbsp;- d\'initiatives inter-entreprises ex: construction d\'équipements collectifs (sportifs, sociaux, etc...) ? </p>', 0),
(407, 7, '<p><br></p><p>Cette socialisation du personnel est-elle entretenue en dehors de l\'entreprise</p><p>&nbsp; - par le biais d\'associations créées par le personnel ?</p><p>&nbsp; - par le jeu d\'associations locales autorisées à diffuser leurs activités sur les panneaux de libre expression de l\'entreprise ? </p>', 0),
(408, 7, '<p>Ces initiatives de socialisation ont-elles pour effet</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - d\'améliorer la communication ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - de réduire les tensions relationnelles ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- de permettre au personnel de se connaître ?&nbsp;</p><p>Ces initiatives de socialisation ont-elles pour effet</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - de diminuer des disparités ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- de favoriser les connivences ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- de rapprocher les individus par des liens ?&nbsp;</p><p>Verriez-vous d\'autres initiatives à mettre en place ? Si oui faites nous en part. </p>', 0),
(409, 7, '<p><br></p><p>Ces initiatives de socialisation sont-elles complétées</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par la constitution de groupes hétérogènes de formation ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par la composition variée des groupes de réunion ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par des groupes de travail pluridisciplinaires ? </p>', 0),
(410, 7, '<p>Cette socialisation du personnel est-elle confortée</p><p>&nbsp; &nbsp;- par la restitution du traitement des difficultés dans le groupe ?</p><p>&nbsp; &nbsp;- par une gestion autonome des difficultés rencontrées par le groupe lui-même ?&nbsp;</p><p>Cette socialisation du personnel est-elle soutenue</p><p>&nbsp; &nbsp;- par un encouragement aux pratiques collectives ( sport, etc... ) ?</p><p>&nbsp; &nbsp;- par l\'instauration de jardins ouvriers ?</p><p>- par la sollicitation du groupe à prendre en charge un ou plusieurs de ses membres fatigués, âgés ou handicapés conformément aux valeurs morales et sociales ? </p>', 0),
(411, 7, '<p>Cette socialisation du personnel est-elle appuyée</p><p>- par l\'encouragement aux pratiques collectives telles que les transports personnels partagés ?</p><p>- par des propositions de services entre salariés exprimées sur les panneaux de libre expression pour générer de la vie sociale dans l\'entreprise ?&nbsp;</p><p>Pourriez-vous nous faire part des initiatives que vous avez développées et dont vous pensez qu\'elles puissent partager par d\'autres et leur être aussi bénéfiques qu\'elles le sont pour vous ?&nbsp;</p>', 0),
(412, 12, '<p><br></p><p><br></p><p>Pensez-vous que s\'y attacher n\'obère d\'aucune sorte les résultats l\'atmosphère la convivialité la participation la coopération la conflictualité le sentiment d\'appartenance ? </p>', 0),
(413, 7, '<p>La socialisation du personnel est-elle induite</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par un (meilleur) partage de l\'information ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par une réception du personnel&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* selon une plage horaire précise ?&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* à tout moment ?&nbsp;</p><p>Cette forme incluant les moments informels.</p>', 0),
(414, 7, '<p>La socialisation du personnel est-elle renforcée</p><p>&nbsp;- par un comportement social (traduit notamment par une civilité, une courtoisie, une politesse marquant tous les rapports sociaux) ?</p><p>&nbsp;- par une distanciation mesurée ?</p><p>&nbsp;- par un rappel régulier de l\'encadrement à l\'écoute ?</p><p>&nbsp;- par un rappel de tout comportement infantilisant de la part de l\'encadrement ?</p><p>&nbsp;- par une sensibilisation permanente de l\'encadrement aux indicateurs sociaux ? </p>', 0),
(415, 7, '<p>L\'absence de socialisation ne risque-t-elle pas d\'être ressentie comme</p><p>- le sentiment d\'une indifférence à l\'égard du personnel ?</p><p>- l\'absence de partage social symbolique ?</p><p>- un manque de civilité ?</p><p>- le renforcement des distances sociales ? Cette absence n\'est-elle pas préjudiciable à la sociabilité et donc au climat social, à l\'atmosphère de travail, à la réalisation du travail ? </p>', 0),
(416, 7, '<p>L\'absence de prérogatives de socialisation du personnel n\'exclut-elle pas</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- la régulation des tensions humaines ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- l\'atténuation des ressentiments, des rancœurs ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- d\'un ressenti négatif ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - d\'un sentiment de malaise ?</p><p>&nbsp; &nbsp; &nbsp;- d\'une gestion des Ressources Humaines déphasée avec la réalité vécue ailleurs ? </p>', 0),
(417, 7, '<p>L\'absence de prérogatives de socialisation du personnel ne réduit-elle pas sensiblement</p><p>&nbsp; &nbsp; &nbsp;- des connivences ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - des complicités ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- une communication multi-directionnelle ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - une meilleure convivialité ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - autres ? </p>', 0),
(418, 11, '<p>Faites-vous</p><p>&nbsp; &nbsp;- du souhait, de l\'envie, du désir, de l\'ambition,</p><p>&nbsp; &nbsp;- de la curiosité,</p><p>&nbsp; &nbsp;- de l\'intérêt, du sentiment des éléments de recrutement aussi importants que la compétence&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * attestée ou&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * présentée ou</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* supposée&nbsp;</p><p>pour un recrutement (au moins dans sa première approche) ? </p>', 0),
(419, 7, '<p><br></p><p>La gestion des Relations Humaines est-elle traduite par</p><p>&nbsp; &nbsp;- des délégations ?</p><p>&nbsp; &nbsp;- par des délégations courtes et exceptionnelles (intérim ponctuel, etc...) ?&nbsp;</p><p>Vous donnez vous de consacrer la compétence de tous les collaborateurs quelle que soit leur situation hiérarchique en rappelant son caractère temporaire de prime abord ? </p>', 0),
(420, 7, '<p>La gestion des Relations Humaines passe-t-elle par des sollicitations envers certains de vos collaborateurs</p><p>&nbsp; - en partageant exceptionnellement leurs tâches ?</p><p>&nbsp; - en participant aux tâches peu reconnues avec les personnes en charge de les acquitter ?</p><p>&nbsp; - en reconnaissant l\'ingratitude de certains travaux par une revalorisation financière ou non ?</p><p>&nbsp; - en sachant partager des banalités sociales ? </p>', 0),
(421, 7, '<p>La gestion des Relations Humaines marque-t-elle de la considération aux salariés&nbsp;</p><p>- en leur attribuant ponctuellement des tâches reconnues et valorisantes telles que &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* la présidence d\'une réunion ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* le secrétariat d\'une séance de travail ?&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* la rédaction d\'un compte-rendu de séance ?</p><p>- l\'attribution d\'un travail exceptionnel ? </p>', 0),
(422, 7, '<p><br></p><p>Vous attachez-vous</p><p>&nbsp; &nbsp; - à donner le sentiment de travailler en parfaite intelligence ?</p><p>&nbsp; &nbsp; - à procurer les conditions de satisfaire les demandes formulées et attendues ? </p>', 0),
(423, 7, '<p>Tout en évitant les attitudes courtisanes, tout un chacun s\'emploie-t-il</p><p>&nbsp; - à toujours remercier ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à avoir des attitudes prévenantes ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à commander en sollicitant ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à manifester de l\'intérêt pour le travail demandé&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* en le suivant,&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;* en saluant les initiatives prises ? </p>', 0),
(424, 7, '<p><br></p><p>Une considération de fait est-elle accordée par l\'emploi d\'une sémantique affective telle que \"homme de confiance\", \"un de nos meilleurs connaisseurs ou ouvriers...\", \"je vous confie à...\",\"je sais que vous ne serez pas déçu par...\" \"je connais les qualités de M. ....\" etc...)sans que celle soit complaisante ou affectée ? </p>', 0),
(425, 7, '<p><br></p><p>La considération accordée est-elle détachée de tout paternalisme et/ou de toute démagogie ?</p><p><br></p><p>Cette liste ne demande qu\'à être complétée par des éléments de la réalité quotidienne ? Pourriez-vous l\'illustrer par des éléments tangibles ? </p>', 0),
(426, 7, '<p><br></p><p>L\'absence de toute considération de fait est-elle souhaitable ?</p><p>&nbsp;La Gestion des Relations Humaines peut-elle s\'accommoder de ce désinvestissement relationnel ?&nbsp;</p><p>Cette réponse est-elle conforme aux intérêts de l\'entreprise ?&nbsp;</p><p>Est-elle compatible avec une optimisation des Ressources Humaines comme capital ? </p>', 0),
(427, 7, '<p><br></p><p>La gestion des Relations Humaines</p><p>&nbsp; - reconnaît-elle les personnes dans leurs possibilités ?</p><p>&nbsp; - reconnaît-elle leurs compétences professionnelles ?</p><p>&nbsp; - satisfait-elle aux ambitions des individus ? ou essaie-t-elle ? ou analyse-t-elle cette possibilité ? </p>', 0),
(428, 7, '<p>La gestion des Relations Humaines</p><p>- répond-elle aussi à la notion inconsciente de travail-souffrance signe de considération du donneur d\'ordres pour le salarié ?</p><p>- manifeste-t-elle des signes de considération notamment lors d\'un rendu de travail ou de mission parfaitement finalisé ?&nbsp;</p><p>L\'encadrement sait-il reconnaître féliciter, encourager ? </p>', 0),
(429, 7, '<p><br></p><p>La gestion des Relations Humaines intègre-t-elle les formes sociales de la vie courante pour les associer aux relations sociales et professionnelles de l\'entreprise ?&nbsp;</p><p>Vous donnez-vous et invitez vous tout un chacun à reproduire des propos valant dans la vie de tous les jours et qui sont des éléments de sociabilité toujours bienvenus ? </p>', 0),
(430, 7, '<p>La gestion des Relations Humaines accorde-t-elle à la désobéissance un droit de cité dans l\'entreprise dès lors qu\'elle satisfait</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à la sécurité ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à une réponse-client ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à l\'identification d\'un anachronisme ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à l\'émergence d\'une idée originale ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à la non exécution d\'un ordre préjudiciable aux intérêts de l\'entreprise ? </p>', 0),
(431, 7, '<p><br></p><p>La gestion des RH est-elle reconnue</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - compétente pour forcer le respect ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- efficace pour revendiquer l\'efficacité ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- créative pour susciter aux salariés l\'envie de l\'être aussi ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - \"écouteuse\" pour être entendue ? </p>', 0),
(432, 7, '<p>La gestion des RH sait-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- donner pour recevoir ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - recevoir sans pour autant être redevable ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- recevoir pour être redevable ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- rendre pour être récompensée de retour ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- être juste/ honnête /impartiale pour susciter une reconnaissance ? </p>', 0),
(433, 7, '<p><br></p><p>Veillez-vous</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à mettre en relation conditions et critères d\'évaluation ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à satisfaire une certaine éthique &nbsp;au et &nbsp;dans le travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à satisfaire une certaine éthique &nbsp;de &nbsp;façon plus large ? </p>', 0),
(434, 7, '<p>La gestion des RH s\'attache-t-elle</p><p>- à respecter les valeurs du monde du travail (solidarité, esprit d\'équipe, une part d\'équité, l\'attente de reconnaissance du travail, des personnes, etc...) ?</p><p>- à traiter les hommes avec franchise ? avec considération ? avec humanité ? ( quelles soient les situations)</p><p>- à être intègre dans ses choix et décisions ? </p>', 0),
(435, 7, '<p>La gestion des RH sait-elle</p><p>&nbsp; &nbsp;- afficher une cohérence entre le discours énoncé et les actes ?</p><p>&nbsp; &nbsp;- ne dire que ce qui peut être tenu ?</p><p>&nbsp; &nbsp;- n\'avancer que ce qui est nullement contrarié par les faits ?</p><p>&nbsp; &nbsp;- n\'affirmer que ce qui est difficilement contestable ?&nbsp;</p><p>Ajouteriez-vous des compléments à cette liste ? Faites nous en part.</p>', 0),
(436, 7, '<p>La gestion des RH s\'attache-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à tisser des liens pour apprivoiser ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à chercher à épanouir pour auto-motiver ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à préserver la dignité de chacun ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à encourager ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à rappeler à l\'ordre tout un chacun quelle que soit sa situation hiérarchique ? </p>', 0),
(437, 7, '<p>La gestion des RH se soucie-t-elle de l\'intégrité mentale et physique de chacun</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par le renouvellement régulier du travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par un environnement agréable ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par une attention aux conditions de travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par une attention particulière aux attitudes de groupe ?</p><p>&nbsp; &nbsp;- par une intervention rigoureuse lors de dérives (telles les cabales menées envers un(e) collaborateur (trice)) ? </p>', 0),
(438, 7, '<p><br></p><p>La gestion des RH partage-t-elle le pouvoir</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - pour éviter son accaparement ?</p><p>&nbsp;- pour autonomiser, responsabiliser et impliquer ? </p>', 0),
(439, 7, '<p>La gestion des RH compense-t-elle les salaires</p><p>&nbsp;- par une satisfaction immatérielle (formation) ?</p><p>&nbsp;- par une satisfaction psychologique (autorité discrète, autonomie laissée dans le travail, conduite de projet, etc...) ? </p>', 0),
(440, 7, '<p>La gestion des RH compense-t-elle les salaires</p><p>&nbsp; &nbsp;- par le sentiment d\'être suffisamment et bien informé ?</p><p>&nbsp; - par le sentiment d\'une juste rémunération corrélativement rapportée au niveau des autres rémunérations de l\'entreprise ?&nbsp;</p><p>La gestion des RH compense-t-elle les salaires</p><p>- par des avantages sociaux ? </p>', 0),
(441, 7, '<p>La gestion des RH compense-t-elle les salaires</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par un intéressement associé aux bénéfices et à la productivité ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par une flexibilité des salaires, créatrice de liens sociaux ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- par des tassements des rémunérations ?</p><p>&nbsp; &nbsp;- autres ? </p>', 0),
(442, 7, '<p>La gestion des RH s\'attache-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à simplifier les structures ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à éviter les luttes intestines ?&nbsp;</p><p>La gestion des RH veille-t-elle</p><p>&nbsp; &nbsp; - à circonscrire la déperdition des savoirs ?</p><p>&nbsp; &nbsp; - à éliminer tout contre-emploi ?</p><p>&nbsp; &nbsp; - à améliorer les synergies ?</p><p>- à faciliter l\'expression des potentialités ?</p><p>- à économiser les énergies, les efforts fournis ? .</p>', 0),
(443, 7, '<p>La gestion des RH s\'attache-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à réduire les sources et les causes d\'agacements ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à limiter les stress inutiles ?&nbsp;</p><p>La gestion des RH se donne-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - d\'organiser une meilleure gestion du temps ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - de dissocier travail prescrit et réalisé et temps de présence ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - de développer des organisations du travail soustraites des justifications horaires ? </p>', 0),
(444, 8, '<p><br></p><p><br></p><p>La culture d\'entreprise est-elle largement définie afin d\'être crédible auprès de l\'ensemble du personnel ? </p>', 1),
(445, 8, '<p><br></p><p><br></p><p><br></p><p>Les différentes expressions présentes dans l\'entreprise trouvent-elles à s\'exprimer ? </p>', 0),
(446, 8, '<p><br></p><p>Les différentes sensibilités syndicales et sociales trouvent-elles des facilités d\'expression ?&nbsp;</p><p>La garantie de toutes les expressions, même celles qui vous sont le moins favorables, est-elle consentie, voire assurée ?&nbsp;</p><p>A l\'instar de certaines entreprises, les sections syndicales, peu soupçonnables d\'allégeance, sont-elles soutenues ? </p>', 0),
(447, 8, '<p><br></p><p>L\'absence d\'expression des différentes sensibilités ne risque-t-elle pas d\'entretenir du dépit chez les salariés, convaincus d\'un rapport de force trop défavorable ?</p><p>&nbsp;Cette absence ne vide-t-elle pas une partie substantielle de la notion de culture d\'entreprise ?&nbsp;</p><p>Cette absence n\'encourage-t-elle pas certains membres du personnel à la passivité ? </p>', 0),
(448, 8, '<p><br></p><p><br></p><p>L\'entreprise intègre-t-elle les valeurs culturelles de chacune des composantes sociales du personnel ? </p>', 0),
(449, 8, '<p><br></p><p>La diversité des populations ethniques est-elle assimilée comme une richesse ?</p><p>Y a-t-il des actions pour la favoriser ( via un recrutement établi sur critères objectifs de compétence par exemple) ?&nbsp;</p><p>Les valeurs qui composent ces cultures, sont-elles</p><p>&nbsp; &nbsp; &nbsp;- intégrées dans les relations quotidiennes ?</p><p>&nbsp; &nbsp; &nbsp;- cultivées ? </p>', 0),
(450, 8, '<p>Exclure l\'expression et l\'intégration culturelles des composantes sociales de l\'entreprise, n\'est-ce pas</p><p>- éloigner le personnel de toutes les initiatives proposées ?</p><p>- réduire sa réceptivité ?&nbsp;</p><p>Ce choix n\'exclut-il pas une part de communication marginale, mais cependant effective ?&nbsp;</p><p>Ce choix ne prive-t-il pas la création inconsciente d\'affectif ? </p>', 0),
(451, 8, '<p>La culture est-elle exprimée à travers</p><p>&nbsp; &nbsp;- les recrutements (cf le module recrutement) ?</p><p>&nbsp; &nbsp;- la formation ( cf le module formation ) ?&nbsp;</p><p>La culture est-elle exprimée à travers</p><p>&nbsp; &nbsp;- la communication (Cf le thème Communication) ?</p><p>&nbsp; - la recherche et développement ( écologie des produits, etc... ) ?</p><p>&nbsp; - l\'éthique (attachement à l\'intégrité des personnes comme principe) ? </p>', 0),
(452, 8, '<p>Outre ces aspects traditionnels de la Gestion des Ressources Humaines, la culture est-elle exprimée</p><p>&nbsp; - dans l\'architecture des locaux (cf nos publications) ?</p><p>&nbsp; - dans le design des ateliers et des bureaux ?&nbsp;</p><p>Outre ces aspects traditionnels de la Gestion des Ressources Humaines, la culture est-elle exprimée</p><p>&nbsp; - dans les lieux d\'implantation des établissements ?</p><p>&nbsp; - dans l\'engagement de l\'entreprise dans des activités culturelles ? </p>', 0),
(453, 8, '<p><br></p><p>La Gestion des Ressources Humaines gagne-t-elle à ignorer les phénomènes culturels dans les recrutements, la formation, la communication et leurs effets sur les relations dans l\'entreprise ?</p><p>&nbsp;La Gestion des Ressources Humaines peut-elle même ignorer cette dimension inconsciemment présente dans tous les rapports ? </p>', 0),
(454, 8, '<p>L\'action culturelle de l\'entreprise est-elle exprimée à travers<span style=\"font-size: 10pt;\">- des expositions photographiques, picturales d\'amateurs (salariés, clients ou riverains) ou de professionnels ?&nbsp;</span></p><p>L\'action culturelle de l\'entreprise est-elle exprimée à travers</p><p>&nbsp; - des œuvres réalisées à partir de matériaux offerts, ou récupérés par les salariés ?</p><p>&nbsp; - des expositions montées conjointement avec le Comité d\'Entreprise ? </p>', 0),
(455, 8, '<p><br></p><p>Est-il d\'autres aspects culturels complétant les initiatives présentées (ex: orchestre, théâtre, équipe sportive, club, etc...) ?&nbsp;</p><p>Donneriez vous des murs à \"tagger\" ?&nbsp;</p><p>Autoriseriez-vous des modes d\'expression (peinture murale, exposition de produits créés par les ateliers dans l\'entreprise...) ? </p>', 0),
(456, 8, '<p><br></p><p>L\'entreprise ne gagnerait-elle pas aussi à être un lieu de vie, un lieu social en même temps qu\'un lieu de travail ?</p><p>&nbsp;Pensez-vous que l\'on gagne à ignorer cette dimension ?&nbsp;</p><p>Le travail ne serait-il pas mieux perçu si cette dimension était acceptée, cultivée ? </p>', 0),
(457, 8, '<p>La notion de culture d\'entreprise inclut-elle la construction d\'une mémoire collective à travers</p><p>&nbsp;- l\'histoire de l\'entreprise (ses grandes dates</p><p>&nbsp;- brevets, etc...) ?</p><p>&nbsp;- un album de réalisations via des documents écrits, un montage audio-visuel, une bande vidéo, etc... ?&nbsp;</p><p>La notion de culture d\'entreprise inclut-elle la construction d\'une mémoire collective à travers</p><p>&nbsp;- un répertoire des contrats conquis, illustré des productions ?</p><p>&nbsp;- une mémoire sociale par ses accords, ses conflits, ses épreuves, ses efforts et ses expériences partagés ? </p>', 0),
(458, 8, '<p><br></p><p>Cette liste ne peut être complète sans votre participation. Par quels éléments seriez-vous enclin à la compléter</p><p>- prix reçus ?</p><p>- dates marquantes (ouverture, agrandissement, réalisations acquisitions, fusion, association, contribution...) ? </p>', 0),
(459, 8, '<p><br></p><p>L\'entreprise n\'a-t-elle pas à reconnaître son passé ?&nbsp;</p><p>N\'a-t-elle pas à admettre sont passé social qui ne peut être toujours linéaire ?&nbsp;</p><p>Ne gagne-t-elle pas à faire resurgir les conflits qui l\'ont traversée, qui l\'ont de ce fait marquée ?&nbsp;</p><p>Toute l\'histoire sociale de l\'entreprise ne gagne-t-elle pas à être partagée collectivement ? </p>', 0),
(460, 8, '<p>Un rite d\'accueil est-il instauré ? Si oui, ce rite est-il marqué, entre autres, par</p><p>&nbsp; - un mot de bienvenue du Directeur des Ressources Humaines, d\'un cadre supérieur ou du Directeur de l\'entreprise ?</p><p>&nbsp; - une visite de l\'entreprise ?</p><p>&nbsp; - une présentation de l\'encadrement et des collaborateurs ?</p><p>&nbsp;- la distribution du livret d\'accueil composé:&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * d\'un organigramme succinct, &nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * d\'un résumé du dernier bilan social,&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * d\'une présentation générale de l\'entreprise ou du groupe ? </p>', 0),
(461, 8, '<p>Ce rite d\'accueil est-il complété par</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - la distribution d\'une plaquette des réalisations de l\'entreprise ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - un historique de l\'entreprise ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - une projection de documents vidéo ou photographiques ?&nbsp;</p><p>Ce rite d\'accueil est-il complété par</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- une présentation des objectifs en cours ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- une annonce dans le journal d\'entreprise ?&nbsp;</p><p>Par quels autres éléments, cette liste pourrait-elle être complétée ?&nbsp;</p><p>Vous donnez vous de soigner la première impression du nouveau recruté ? </p>', 0),
(462, 8, '<p><br></p><p>La première impression n\'est-elle pas particulièrement à soigner ?&nbsp;</p><p>Compte tenu du fait que la première image de l\'entreprise reste longtemps présente vous donnez les moyens d\'y consacrer un peu de temps et d\'attention ?&nbsp;</p><p>L\'entreprise ne signe-t-elle pas, par cette étape, sa Gestion des Ressources Humaines ? </p>', 0),
(463, 8, '<p>Des liens et des codes sociaux sont-ils créés à travers</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- un &nbsp;respect &nbsp;des &nbsp;horaires ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- un ou des repas partagés ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- des voyages en commun ?&nbsp;</p><p>Des liens et des codes sociaux sont-ils créés à travers</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- des festivités partagées (arbre de noël, vœux de nouvel an...) ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- le partage d\'un savoir pour en faire un savoir collectif ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- une mémoire collective ? </p>', 0),
(464, 8, '<p>Ces codes sociaux sont-ils complétés</p><p>- par le port d\'une tenue de travail acceptée de tous, et valorisante ?</p><p>- par la revendication d\'une appartenance marquée par un signe de reconnaissance: pin\'s, badge, style d\'habillement, tenue signée, logo... ?</p><p>- par un langage connoté ?&nbsp;</p><p>Cette liste ne saurait être complète sans votre concours.</p>', 0),
(465, 8, '<p><br></p><p>La culture d\'entreprise est-elle comprise au sens large pour favoriser</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- les savoirs empiriques ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- les détenteurs de ces savoirs à s\'exprimer ?&nbsp;</p><p>Avez-vous connaissance de savoirs de ce type ?&nbsp;</p><p>Veillez-vous à solliciter ces personnes pour telle ou telle mission ? </p>', 0),
(466, 8, '<p>Ces codes et liens sociaux sont-ils complétés</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par des conduites admises de tous ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par des valeurs acceptées par tous, telles que:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * qualité, &nbsp; &nbsp; <span style=\"font-size: 10pt;\">&nbsp;* fiabilité, &nbsp; &nbsp; &nbsp; </span><span style=\"font-size: 10pt;\">&nbsp; * ponctualité, &nbsp; &nbsp; </span></p><p><span style=\"font-size: 10pt;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * irréprochabilité du Service après Vente, * disponibilité, * rapidité</span></p><p><span style=\"font-size: 10pt;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; * prise en charge par tout un chacun de toute réclamation ?</span></p>', 0),
(467, 8, '<p>Ces liens et codes sociaux seraient-ils complétés</p><p>&nbsp; &nbsp; - par une façon d\'être qui soit plus qu\'une façon de paraître ?</p><p>&nbsp; &nbsp; - par l\'instauration d\'un climat psychologique permettant l\'efficacité ?&nbsp;</p><p>Quels sont les initiatives que vous avez introduites pour améliorer les relations humaines, les détendre ?&nbsp;</p><p>Quelles seraient celles qui gagneraient à être introduites ? </p>', 0),
(468, 8, '<p><br></p><p>L\'entreprise se constitue-t-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- une vie sociale,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- des relations sociales en marge de l\'activité professionnelle ?&nbsp;</p><p>Se donne-t-elle d\'encourager, de faciliter toutes les initiatives en la matière ? </p>', 0),
(469, 8, '<p><span style=\"font-size: 10pt;\">Transparence des comportements:&nbsp;</span></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;portes ouvertes, bureaux partiellement paysagers...,&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; mesures réglementaires (tenues blanches, mesures de sécurité, etc...), &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;respect scrupuleux du travail d\'autrui,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; respect des locaux et du matériel ex: propreté pour faciliter l\'entretien &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;et la restitution des locaux en parfait état&nbsp;</p><p>sont-ils des façons d\'être communes à tous ? </p>', 0),
(470, 8, '<p>Ce panel de dispositions est-il complété par</p><p>&nbsp; &nbsp;- une courtoisie ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- une discrétion ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- un respect naturel ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - une attention naturelle ?&nbsp;</p><p>Comment compléteriez-vous ce panel de dispositions.</p>', 0),
(471, 8, '<p><br></p><p>L\'entreprise gagne-t-elle à éluder certaines règles élémentaires de la vie en société ?&nbsp;</p><p>L\'économie de dispositions utiles au travail est-elle à faire ? </p>', 0),
(472, 8, '<p>Vous donnez-vous de créer des liens symboliques</p><p>&nbsp;- par l\'autorisation d\'un arrosage de la part du salarié (départ en retraite, etc...), voire de l\'entreprise ?</p><p>&nbsp;- la remise d\'un cadeau, d\'une rétribution exceptionnelle... ?</p><p>&nbsp;- l\'aménagement de retraites progressives ?</p><p>&nbsp;- des signes de reconnaissance ? </p>', 0),
(473, 8, '<p>Ces dispositions courantes sont-elles complétées par</p><p>&nbsp;- un reclassement des personnes licenciées par nécessité économique dans les filiales du groupe ou le tissu industriel local ?</p><p>&nbsp;- une priorité d\'embauche pour les personnels licenciés pour raison économique dans les filiales du groupe ?&nbsp;</p><p>Pourriez-vous illustrer ces deux exemples par d\'autres actions ? </p>', 0),
(474, 8, '<p>Ces dispositions courantes sont-elles complétées par</p><p>&nbsp; &nbsp; - des recommandations du personnel par voie de presse ?</p><p>&nbsp; &nbsp; - Une aide à reconversion ou de rachat de structure?</p><p><span style=\"font-size: 10pt;\">&nbsp; &nbsp; - par un bilan/ analyse de compétence ?</span></p><p>&nbsp; &nbsp;- par la recherche de soutien des institutions publiques? </p>', 0),
(475, 8, '<p><br></p><p>Ces dispositions courantes sont-elles complétées par</p><p>&nbsp; - une implication personnelle de la direction dans cette volonté sociale ?</p><p>&nbsp; - l\'octroi de conseils en formation si congé de conversion, ou de conseils en reconversion en cas de refus du congé de formation.</p>', 0),
(476, 8, '<p>La culture n\'a-t-elle pas à s\'exprimer</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par des signes et des symboles sociaux ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par des attentions personnelles ?</p><p>La culture n\'a-t-elle pas à s\'exprimer</p><p>&nbsp; &nbsp; &nbsp;- par des dispositions sociales ?</p><p>&nbsp; &nbsp; &nbsp;- par des communications non verbales socialement codées ? </p>', 0),
(477, 8, '<p>La culture d\'entreprise reconnaît-elle</p><p>&nbsp;- les Savoir-Faire comme savoirs ?</p><p>&nbsp;- les acquisitions extra-professionnelles comme des assimilations profitables à l\'entreprise ?&nbsp;</p><p>Vous donnez vous de rechercher</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- les passe-temps, les violons d\'Ingres</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- les centres d\'intérêts,</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- les adhésions aux associations de vos collaborateurs ?&nbsp;</p><p>La culture d\'entreprise reconnaît-elle</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- les valeurs culturelles d\'origine étrangère ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- les valeurs sociales du travail ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- des valeurs éthiques ? </p>', 0),
(478, 8, '<p>La culture d\'entreprise est-elle comprise au sens large pour favoriser</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- toutes les expressions ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- toutes les transmissions ?&nbsp;</p><p>Comment illustrez-vous ces deux formes ?&nbsp;</p><p>Vous donnez-vous de faciliter toutes les transmissions de savoirs au point d\'en faire une obligation naturelle chez chacun ? </p>', 0),
(479, 8, '<p><br></p><p>Les savoirs reconnus sont-ils incorporés dans le travail du salarié concerné ?&nbsp;</p><p>Sont-ils diffusés par l\'intéressé ?&nbsp;</p><p>Sont-ils rémunérés ?&nbsp;</p><p>Les noms de initiateurs de savoirs sont-ils énoncés ? Sont-ils non appropriés ? </p>', 0),
(480, 8, '<p><br></p><p><br></p><p><br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;De bonnes raisons arrêtent sans doute votre choix.</p>', 0),
(481, 8, '<p>La culture (au sens large) est-elle soutenue</p><p>&nbsp; &nbsp;- par des réponses favorables aux demandes de formation générale ?</p><p>&nbsp; &nbsp;- par des manifestations communes CE/ entreprise ?&nbsp;</p><p>Vous inviteriez-vous à permettre aux salariés de faire part de leurs coups de cœur culturels ?&nbsp;</p><p>Demandez-vous aux chargés de la veille technologique de rendre compte de leurs interrogations, de leurs découvertes et de leurs étonnements ?&nbsp;</p><p>La culture (au sens large) est-elle soutenue</p><p>&nbsp; &nbsp;- par une promotion des consommations culturelles ?</p><p>&nbsp; - par une incitation à la curiosité culturelle et intellectuelle ? </p>', 0);
INSERT INTO `question_qst` (`qst_id`, `cou_id`, `qst_question`, `qst_first`) VALUES
(482, 8, 'Le soutien à la culture du personnel est-il assuré par l\'invitation qui est faite de rendre compte<p></p><p>&nbsp;- des modes de raisonnement étranger (cas des cadres internationaux et expatriés) ?</p><p>&nbsp;- des méthodes de travail rencontrés ici ou là (lors de visites, après des lectures spécialisées, ...) ? La culture du personnel est-elle soutenue</p><p>&nbsp;- par la reconnaissance des valeurs des communautés étrangères de l\'entreprise ?</p><p>&nbsp;- la variété des sensibilités de l\'entreprise (notamment par la perception de la notion et de la perception du travail qui a beaucoup évoluée...) ? </p>', 0),
(483, 8, '<p><br></p><p>La culture d\'entreprise est-elle entretenue par</p><p>&nbsp; - l\'initiation à la gestion d\'entreprise,</p><p>&nbsp; - la distribution d\'informations financières et économiques,</p><p>&nbsp; - l\'explication de données de gestion techniques et scientifiques afin d\'infléchir chez chacun un mode de pensée responsable ou tout au moins réaliste ? </p>', 0),
(484, 8, '<p>L\'entreprise gagne-t-elle à faire l\'économie</p><p>&nbsp; - de formations généralistes, investissements à terme ?</p><p>&nbsp; - d\'une incitation à la consommation de biens culturels mis à disposition par le Comité d\'Entreprise (médiathèque, bibliothèque, etc...) ?</p><p>&nbsp; - à une diffusion d\'informations qui peuvent être aussi du savoir pour autant que quelques clés sont données ?&nbsp;</p><p>Priez-vous tout un chacun de diffuser les connaissances et les savoirs qu\'il possède éventuellement en les organisant? </p>', 0),
(485, 8, '<p><br></p><p>L\'entreprise gagne-t-elle à se garder d\'être un lieu de travail autant qu\'un lieu d\'éducation ?&nbsp;</p><p>Ce choix ne diminue-t-il pas l\'estime que peut ou pourrait attribuer le personnel à son encadrement et inversement ?&nbsp;</p><p>Ce choix ne pâtit-il pas à l\'encadrement immanquablement jugé par le personnel ? </p>', 0),
(486, 8, '<p><br></p><p>L\'absence d\'une incitation à la curiosité culturelle et intellectuelle ne lèse-t-elle pas<span style=\"font-size: 10pt;\">&nbsp;</span></p><p><span style=\"font-size: 10pt;\">&nbsp; &nbsp; - l\'encadrement ayant en charge l\'exportation ou le personnel expatrié ?</span></p><p><span style=\"font-size: 10pt;\">&nbsp; &nbsp; - la capacité de l\'entreprise à entrevoir et à concevoir des transformations de production ?</span></p>', 0),
(487, 8, '<p><br></p><p>La culture développée dans l\'entreprise intègre-t-elle</p><p>&nbsp;- la notion de développement incertain,</p><p>&nbsp;- la notion de désordre \\organisé\\ dans l\'organisation du travail afin d\'entretenir une capacité d\'adaptation permanente et une vitalité interne ? </p>', 0),
(488, 8, '<p><br></p><p>La meilleure formation des personnels, les nouveaux rapports d\'autorité au regard du savoir apporté sont-ils intégrés comme éléments constitutifs de la culture d\'entreprise ?&nbsp;</p><p>La modification des relations entre les jeunes dans leurs relations et dans leurs rapports à l\'autorité, la transformation des relations entre les sexes, l\'émergence de nouvelles valeurs matérielles (salaires)sont-elles intégrées comme éléments constitutifs de la culture d\'entreprise ? </p>', 0),
(489, 8, '<p>A l\'intégration des évolutions sociales et culturelles de la société est-il associé</p><p>&nbsp;- des formes d\'autorité classique ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- un sens de la démonstration et de l\'explication ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- une meilleure écoute des techniciens et des spécialistes ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - une souplesse des relations mariées à une efficacité requise ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- des rémunérations individualisées ? </p>', 0),
(490, 8, '<p><br></p><p>L\'entreprise gagne-t-elle à nier ou à rester sourde aux évolutions sociales et culturelles de ses membres ?&nbsp;</p><p>Ignorer les évolutions sociales et culturelles de la société, ne nourrit-il pas le ressentiment, le désintérêt chez une part du personnel ?&nbsp;</p><p>Ignorer les évolutions sociales et culturelles de la société, ne crée-t-il pas un décalage entre la vie dans l\'entreprise et hors d\'elle ? </p>', 0),
(491, 8, '<p>Vous venez de terminer la consultation de \"RH -culture d\'entreprise\" inclus dans le thème intitulé \"Relations Humaines\". Le contenu qui vous été soumis ne peut être que valorisé par vous.</p><p>L\'aide que cet EAO vous apporte n\'est qu\'une aide à la réflexion pour permettre l\'action. Les réalisations que vous aurez à conduire seront mieux pesées après ce parcours. Elles auront plus de chances de réussir précédemment évaluées par vous qu\'invitées par un consultant.&nbsp;</p><p>GRH-dialogue vous souhaite une agréable consultation de son contenu.</p>', 0),
(493, 9, '<p>L\'exclusion de toute discrimination</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- sociale,</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- culturelle,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; - ethnique,</p><p>&nbsp; &nbsp; &nbsp; &nbsp;- personnelle</p><p>comptent-elles parmi les conditions retenues pour instaurer cette Gestion des Ressources Humaines ?&nbsp;</p><p>Cet écran n\'appelle pas de réponse fermée. Rechercher pour chaque écran une application est le plus sûr moyen de lui donner tout son emploi.</p>', 0),
(494, 9, '<p><br></p><p>Une politique de petits faits produits avec tact (aménagement d\'un poste de travail pour une personne momentanément fatiguée, autorisation de sortie exceptionnellement accordée ) compte-t-elle parmi les conditions retenues pour instaurer cette Gestion des Ressources Humaines ?&nbsp;</p><p><br></p><p>Pour s\'enrichir, cet EAO a besoin de vous, faites nous part de toutes vos suggestions et propositions à travers l\'onglet \"Contributions ou par mail\"</p>', 0),
(495, 9, '<p>Adhéreriez-vous à l\'opinion suivante:</p><p>\"quand une entreprise a une attitude intelligente envers ses employés, il est probable que ceux ci auront une attitude intelligente vis à vis d\'elle et sauront, si l\'intérêt général le commande, sacrifier momentanément leurs intérêts particuliers\".</p><p><br></p><p>Malgré sa formulation, cet écran n\'appelle pas de réponse fermée.</p>', 0),
(496, 9, '<p><br></p><p>Une logique de sentiments (félicitations formulées, etc...) est-elle introduite dans la Gestion des Ressources Humaines ?&nbsp;</p><p><br></p><p><br></p><p>Cet écran est laissé à votre appréciation.</p>', 0),
(497, 9, '<p><br></p><p>La Gestion des Ressources Humaines sait-elle se débarrasser de tout calcul économique pour être instinctive ?&nbsp;</p><p>La Gestion des Ressources Humaines sait-elle tabler sur les effets et les sensations produits par des gestes gratuits et jamais (ré-)évoqués ?&nbsp;</p><p><br></p><p>Cet écran est laissé à votre appréciation.</p>', 0),
(498, 9, '<p>La Gestion des Ressources Humaines entérine-t-elle des relations proches manifestées par</p><p><br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- un langage direct ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - une adresse respectueuse ?</p><p>&nbsp; - un titre \"Monsieur, Madame, Mademoiselle\" suivi d\'un patronyme (nom ou prénom) ? </p>', 0),
(499, 9, '<p>La Gestion des Ressources Humaines entérine-t-elle des relations proches manifestées par</p><p>- une adresse personnelle via un déplacement plutôt que par un médium ( téléphone, communication électronique) ?</p><p>- une communication personnalisée ?</p><p>- un formalisme réduit mais accueillant ?</p><p>Ne vous privez de revenir sur votre choix pour vous confronter aux deux itinéraires proposés. </p>', 0),
(500, 9, '<p>La communication avec le personnel est-elle réalisée</p><p>&nbsp; - sur la base d\'un face à face ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- sur la base d\'un dialogue ouvert et franc ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - sur le respect de l\'autre en toutes circonstances ?&nbsp;</p><p><br></p><p>Cette liste subjective est naturellement incomplète elle ne demande qu\'à être complétée.Faites nous en part via notre adresse électronique</p>', 0),
(501, 9, '<p><br></p><p>Il vous appartient de refuser d\'entériner le mode relationnel présenté. Cependant, la communication avec le personnel ne gagnerait-elle pas à être établie</p><p>&nbsp; &nbsp; - sur la base d\'un face à face ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- sur la base de la franchise ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- sur le respect de l\'autre en toute circonstance ? </p>', 0),
(502, 9, '<p>La gestion des RH se manifeste-t-elle par la production d\'actes positifs tels</p><p>&nbsp; - une attitude gratifiante ?</p><p>&nbsp; &nbsp; &nbsp;- une courtoisie et une politesse éprouvées ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- une égalité de traitement quelle que soit la situation de l\'employé ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- des attentions de sympathie ( lors d’événements familiaux ) ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- de l\'humour ? </p>', 0),
(503, 12, '<p><br></p><p><br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;La notion d\'équité trouve-t-elle une place dans votre gestion ? </p>', 0),
(504, 9, '<p>Ces attentions seraient-elles complétées</p><p>&nbsp; - par des signes de déférence tels le salut automatique à toute personne rencontrée, etc... ?</p><p>&nbsp; - par une aide apportée à toute personne en difficulté... ?&nbsp;</p><p>Ces attentions seraient-elles complétées</p><p>&nbsp; - par l\'emploi de mots tels que collaborateur plutôt qu\'adjoint, secrétaire, employé, etc... ?</p><p>&nbsp; - par l\'absence de possessifs tels \"mes salariés\", \"mon personnel\" ? </p>', 0),
(505, 9, '<p><br></p><p>Ces attentions seraient-elles complétées</p><p>&nbsp;- par des gestes quotidiens ou rares, banaux et symboliques (restitution au personnel de cadeaux offerts à l\'entreprise ou à l\'encadrement) ?</p><p>&nbsp;- autres ? </p>', 0),
(506, 9, '<p><br></p><p>Manifester de la considération n\'est-il pas à considérer comme un investissement \"affectif\" ?&nbsp;</p><p>Une communication \"affective\" se suffit-elle à elle-même pour être perçue ?&nbsp;</p><p>Une communication affectivement perçue est-elle forcément comprise ? </p>', 0),
(507, 9, '<p>La gestion des RH peut-elle demander en se passant de donner ?&nbsp;</p><p>L\'entreprise n\'a-t-elle pas à investir dans les gestes gratuits riches de considération en retour ? Exemples:</p><p>&nbsp; - demande de nouvelles de la santé d\'un proche ou du salarié,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - aménagement d\'un poste de travail,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - prêt d\'un matériel de l\'entreprise,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - caution morale de l\'employeur.</p>', 0),
(508, 9, '<p>Dans le cadre d\'un dialogue</p><p>- les collaborateurs sont-ils invités plutôt que convoqués (sauf nécessité) ?</p><p>- les collaborateurs se voient-ils reçus ponctuellement à défaut des excuses leur sont-elles faites pour ces oublis ?&nbsp;</p><p>Dans le cadre d\'un dialogue, est-il attribué aux collaborateurs</p><p>&nbsp; &nbsp;- une disponibilité personnelle ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - un temps imparti ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- une faculté d\'expression ? </p>', 0),
(509, 9, '<p>Lors d\'un dialogue, l\'écoute est-elle soutenue</p><p>&nbsp;- par une nature et une position du regard ?</p><p>&nbsp;- par un éclat du visage: * plutôt souriant et accueillant que crispé ? * plutôt encourageant que distant ?&nbsp;</p><p>Lors du dialogue, l\'écoute est-elle sous-tendue</p><p>&nbsp;- par une attention portée aux propos tenus et aux regards offerts ?</p><p>- &nbsp;par une attitude positive (dénuée d\'arrière-pensées) ? </p>', 0),
(510, 9, '<p>La position d\'écoute est-elle d\'autre part manifestée entre autres</p><p>&nbsp; - par des bras et des jambes non croisés ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- des épaules tirées en arrière ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par une souplesse du corps ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- une écoute manifeste ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par un regard mobile, franc, courtois et soutenu ?</p><p>&nbsp;La position d\'écoute est-elle confortée par un naturel plutôt que par une mise en scène ? </p>', 0),
(511, 9, '<p>Lors d\'un dialogue, la voix est-elle assortie</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- d\'un ton adapté ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - d\'inflexions non infantilisantes ?</p><p>&nbsp; - d\'absence d\'inflexions empruntées ?&nbsp;</p><p><br></p><p>Il vous appartient d\'apprécier les situations vécues au regard de ces éléments.</p>', 0),
(512, 9, '<p><br></p><p>Une physionomie ouverte, des ponctuations dans la conversation, des reformulations sont-elles de mise dans les échanges avec le personnel ?&nbsp;</p><p><br></p><p>Une adaptation du vocabulaire aux personnes, une présentation simple des questions sont-elles naturellement adoptées dans les échanges ? </p>', 0),
(513, 9, '<p>Est-il recouru</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à un sens critique à des fins constructives,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- à une synthèse des questions,</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - à l\'énoncé de conclusions en fin de conversation</p><p>pour s\'assurer de s\'être bien compris, et signifier l\'attention portée à la conversation ? </p>', 0),
(514, 9, '<p><br></p><p>La position d\'écoute est-elle confortée</p><p>&nbsp; - par une invitation permanente à parler ?</p><p>&nbsp; - par un attachement à laisser parler ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par une invitation aux demandes d\'explications ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par une reformulation des réponses ? </p>', 0),
(515, 9, '<p>La position d\'écoute est-elle établie</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par une prévenance quant aux choix des mots et de la syntaxe ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par une mise en confiance ?&nbsp;</p><p>Le dialogue est-il aménagé</p><p>- autour d\'un langage accessible ?</p><p>- autour d\'informations volontairement limitées pour en permettre aisément l\'assimilation ?</p><p>- autour d\'une responsabilisation et non d\'une infantilisation ? </p>', 0),
(516, 9, '<p>Le dialogue est-il ménagé</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par l\'absence d\'interruptions de parole des interlocuteurs ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par le refus de faire les questions et les réponses ?</p><p>&nbsp;- par l\'absence de &nbsp;distribuer &nbsp;des &nbsp;récriminations gratuites ?</p><p>&nbsp;- par le refus de monopoliser la parole du fait de son statut ? </p>', 0),
(517, 9, '<p>Veillez vous à rassurer le (ou les) interlocuteur(s)</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par un savoir-être ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - par l\'atténuation des statuts ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- par un escamotage des différences sociales ou culturelles ?</p><p>&nbsp; &nbsp; &nbsp;- par une empathie ?</p><p>&nbsp;- par une attitude constante toujours égale? </p>', 0),
(518, 9, '<p>Est-il accordé à tout entretien</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- une réelle volonté d\'écoute ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- une capacité d\'écoute ?</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- une sincérité faisant fi de démagogie ?&nbsp;</p><p>Est-il accordé à tout entretien</p><p>&nbsp; &nbsp; &nbsp; - une faculté d\'écoute assurant la recherche de messages non dits ?</p><p>&nbsp; &nbsp; &nbsp; - le sentiment d\'être écouté et &nbsp;non seulement &nbsp;celui d\'être entendu ? </p>', 0),
(519, 9, '<p>Le sentiment d\'être écouté est-il conforté</p><p>- par une mise en position d\'égalité de l\'interlocuteur ?</p><p>- par une disposition satisfaisante du mobilier (forme et hauteur de la table et des fauteuils, espaces entre les interlocuteurs...),</p><p>- par des lieux de rencontre relativement neutre ?&nbsp;</p><p>ne visant pas à inférioriser ?&nbsp;</p><p>Cette présentation non exhaustive ne trouve son intérêt que dans l\'analyse réalisée. Les écrans proposés n\'appellent aucune réponse fermée. </p>', 0),
(520, 9, '<p><br></p><p><span style=\"font-size: 10pt;\">L\'indifférence, la condescendance (attitude hautaine), le dédain, une trop grande assurance pointée de vanité, un orgueil malvenu, des comportements habituellement inappréciés etc... sont-ils exclus de toute façon d\'être ?</span></p>', 0),
(521, 9, '<p><br></p><p>Une trop grande sécheresse de ton, une autorité implacable, l\'absence d\'humour, une extériorisation exacerbée de signes de richesse, la vulgarité ( par l\'insulte) en guise de management sont-ils exclus de toute façon d\'être ? </p>', 0),
(522, 9, '<p><br></p><p><br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Il vous appartient de compléter la liste présentée.</p>', 0),
(523, 9, '<p><br></p><p><br></p><p><br></p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Votre réponse est-elle plausible ? </p>', 0),
(524, 9, '<p><br></p><p>Une politique dite des portes ouvertes pour autant qu\'elle soit exclusive de toute autre disposition, une politique slogan, une politique courtisane en matière de Relations Humaines, des discours sur la culture d\'entreprise seulement patronaux sont-il exclus ? </p>', 0),
(525, 9, '<p><br></p><p><br></p><p>Agir en parlant peu, permettre la spontanéité, faire preuve exceptionnellement de générosité, faire preuve de modestie,sont-elles des expressions couramment adoptées par l\'encadrement ? </p>', 0),
(526, 9, '<p><br></p><p><span style=\"font-size: 10pt;\">Faire preuve de simplicité (prêter, partager sa voiture de service au besoin..), montrer les preuves du travail de chacun, être prêt à partager le travail de quiconque sont-elles des expressions couramment adoptées par l\'encadrement ?</span></p>', 0),
(527, 9, '<p>Etre équitable,&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;s\'auto-critiquer au besoin,&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; accepter d\'être critiqué,&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;faire sienne l\'image d\'anti-héros&nbsp;</p><p>sont-ils des éléments présents sinon courants dans l\'entreprise ? </p>', 0),
(528, 9, '<p><br></p><p>Savoir transgresser parfois les tabous,&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;demander sans honte des explications, &nbsp; &nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;montrer des lacunes,&nbsp;</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; produire des stratégies de rupture&nbsp;</p><p>sont-elles des expressions rencontrées dans l\'entreprise ? </p>', 0),
(529, 9, '<p>La gestion des RH s\'emploie-t-elle à éviter</p><p>- de considérer les cadres comme omniscients, omnipotents ?</p><p>- de considérer les exploitants et les producteurs comme des gens passifs ou incapables de gérer quoi que soit ?&nbsp;</p><p><br></p><p>La gestion des RH s\'épargne-t-elle</p><p>- de traiter les personnes polyvalentes comme des entités interchangeables ? </p>', 0),
